INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5680, 'nvi', 'Deuteronômio', 'dt', 29, 1, 'São estes os termos da aliança que o Senhor ordenou que Moisés fizesse com os israelitas em Moabe, além da aliança que tinha feito com eles em Horebe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5681, 'nvi', 'Deuteronômio', 'dt', 29, 2, 'Moisés convocou todos os israelitas e lhes disse: Os seus olhos viram tudo o que o Senhor fez no Egito ao faraó, a todos os seus oficiais e a toda a sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5682, 'nvi', 'Deuteronômio', 'dt', 29, 3, 'Com os seus próprios olhos vocês viram aquelas grandes provas, aqueles sinais e grandes maravilhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5683, 'nvi', 'Deuteronômio', 'dt', 29, 4, 'Mas até hoje o Senhor não lhes deu mente que entenda, olhos que vejam, e ouvidos que ouçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5684, 'nvi', 'Deuteronômio', 'dt', 29, 5, '"Durante os quarenta anos que os conduzi pelo deserto", disse ele, "nem as suas roupas, nem as sandálias dos seus pés se gastaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5685, 'nvi', 'Deuteronômio', 'dt', 29, 6, 'Vocês não comeram pão, nem beberam vinho, nem qualquer outra bebida fermentada. Fiz isso para que vocês soubessem que eu sou o Senhor, o seu Deus. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5686, 'nvi', 'Deuteronômio', 'dt', 29, 7, 'Quando vocês chegaram a este lugar, Seom, rei de Hesbom, e Ogue, rei de Basã, atacou-os, mas nós os derrotamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5687, 'nvi', 'Deuteronômio', 'dt', 29, 8, 'Conquistamos a terra deles e a demos por herança as tribos de Rúben e de Gade e à metade da tribo de Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5688, 'nvi', 'Deuteronômio', 'dt', 29, 9, 'Sigam fielmente os termos desta aliança, para que vocês prosperem em tudo o que fizerem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5689, 'nvi', 'Deuteronômio', 'dt', 29, 10, 'Hoje todos vocês estão na presença do Senhor, do seu Deus: os seus chefes e homens destacados, os seus líderes e oficiais, e todos os demais homens de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5690, 'nvi', 'Deuteronômio', 'dt', 29, 11, 'juntamente com os seus filhos e as suas mulheres e os estrangeiros que vivem nos seus acampamentos cortando lenha e carregando água para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5691, 'nvi', 'Deuteronômio', 'dt', 29, 12, 'Vocês estão aqui presentes para entrar em aliança com o Senhor, o seu Deus, aliança que ele está fazendo com vocês hoje, selando-a sob juramento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5692, 'nvi', 'Deuteronômio', 'dt', 29, 13, 'para hoje confirmá-los como seu povo, para que ele seja o seu Deus conforme lhes prometeu e jurou aos seus antepassados, Abraão, Isaque e Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5693, 'nvi', 'Deuteronômio', 'dt', 29, 14, 'Não faço esta aliança, sob juramento, somente com vocês');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5694, 'nvi', 'Deuteronômio', 'dt', 29, 15, 'que estão aqui conosco na presença do Senhor nosso Deus, mas também com aqueles que não estão aqui hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5695, 'nvi', 'Deuteronômio', 'dt', 29, 16, 'Vocês mesmos sabem como vivemos no Egito e como passamos por várias nações até chegar aqui.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5696, 'nvi', 'Deuteronômio', 'dt', 29, 17, 'Vocês viram nelas as suas imagens e os seus ídolos detestáveis, feitos de madeira, de pedra, de prata e de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5697, 'nvi', 'Deuteronômio', 'dt', 29, 18, 'Cuidem que não haja entre vocês nenhum homem ou mulher, clã ou tribo cujo coração se afaste do Senhor, do nosso Deus, para adorar os deuses daquelas nações, e para que não haja no meio de vocês nenhuma raiz que produza esse veneno amargo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5698, 'nvi', 'Deuteronômio', 'dt', 29, 19, 'Se alguém, cujo coração se afastou do Senhor para adorar outros deuses, ouvir as palavras deste juramento, invocar uma bênção sobre si mesmo e pensar: "Estarei em segurança, muito embora persista em seguir o meu próprio caminho", trará desgraça tanto à terra irrigada quanto à terra seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5699, 'nvi', 'Deuteronômio', 'dt', 29, 20, 'O Senhor jamais se disporá a perdoá-lo; a sua ira e o seu zelo se acenderão contra tal pessoa. Todas as maldições escritas neste livro cairão sobre ela, e o Senhor apagará o seu nome de debaixo do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5700, 'nvi', 'Deuteronômio', 'dt', 29, 21, 'O Senhor a separará de todas as tribos de Israel para que sofra desgraça, de acordo com todas as maldições da aliança escrita neste Livro da Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5701, 'nvi', 'Deuteronômio', 'dt', 29, 22, 'Os seus filhos, os seus descendentes e os estrangeiros que vierem de terras distantes verão as desgraças que terão caído sobre a terra e as doenças com que o Senhor a terá afligido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5702, 'nvi', 'Deuteronômio', 'dt', 29, 23, 'A terra inteira será um deserto abrasador de sal e enxofre, no qual nada que for plantado brotará, onde nenhuma vegetação crescerá. Será como a destruição de Sodoma e Gomorra, de Admá e Zeboim, que o Senhor destruiu com ira e furor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5703, 'nvi', 'Deuteronômio', 'dt', 29, 24, 'Todas as nações perguntarão: "Por que o Senhor fez isto a esta terra? Por que tanta ira e tanto furor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5704, 'nvi', 'Deuteronômio', 'dt', 29, 25, 'E a resposta será: "Foi porque este povo abandonou a aliança do Senhor, o Deus dos seus antepassados, aliança feita com eles quando os tirou do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5705, 'nvi', 'Deuteronômio', 'dt', 29, 26, 'Eles se foram e adoraram outros deuses e se prostraram diante deles, deuses que eles não conheciam antes, deuses que o Senhor não lhes tinha dado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5706, 'nvi', 'Deuteronômio', 'dt', 29, 27, 'Por isso a ira do Senhor acendeu-se contra esta terra, e ele trouxe sobre ela todas as maldições escritas neste livro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5707, 'nvi', 'Deuteronômio', 'dt', 29, 28, 'Cheio de ira, indignação e grande furor, o Senhor os desarraigou da sua terra e os lançou numa outra terra, como hoje se vê".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5708, 'nvi', 'Deuteronômio', 'dt', 29, 29, 'As coisas encobertas pertencem ao Senhor, ao nosso Deus, mas as reveladas pertencem a nós e aos nossos filhos para sempre, para que sigamos todas as palavras desta lei.');
