INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4976, 'nvi', 'Deuteronômio', 'dt', 3, 1, 'Depois, voltamos e subimos rumo a Basã. Ogue, rei de Basã, atacou-nos com todo o seu exército, em Edrei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4977, 'nvi', 'Deuteronômio', 'dt', 3, 2, 'O Senhor me disse: "Não tenha medo dele, pois eu o entreguei em suas mãos, com todo o seu exército, e dei-lhe também a terra dele. Você fará com ele como fez com Seom, rei dos amorreus, que habitava em Hesbom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4978, 'nvi', 'Deuteronômio', 'dt', 3, 3, 'Então o Senhor, o nosso Deus, também nos entregou em nossas mãos Ogue, rei de Basã, e todo o seu exército. Nós os derrotamos, sem deixar nenhum sobrevivente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4979, 'nvi', 'Deuteronômio', 'dt', 3, 4, 'Naquela ocasião conquistamos todas as suas cidades. Não houve cidades que não tomássemos. Foram sessenta cidades, em toda a região de Argobe, o reino de Ogue, em Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4980, 'nvi', 'Deuteronômio', 'dt', 3, 5, 'Todas essas cidades eram fortificadas com muros altos e com portas e trancas. Além delas havia muitas cidades sem muros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4981, 'nvi', 'Deuteronômio', 'dt', 3, 6, 'Nós as destruímos completamente, tal como havíamos feito com Seom, rei de Hesbom, destruindo todas as cidades, matando também os homens, as mulheres e as crianças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4982, 'nvi', 'Deuteronômio', 'dt', 3, 7, 'Mas os animais todos e o despojo das cidades tomamos como espólio de guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4983, 'nvi', 'Deuteronômio', 'dt', 3, 8, 'Foi assim que, naquela ocasião, tomamos desses dois reis amorreus o território a leste do Jordão, que vai desde o ribeiro do Arnom até o monte Hermom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4984, 'nvi', 'Deuteronômio', 'dt', 3, 9, '( Os sidônios chamam o Hermom de Siriom; e os amorreus o chamam Senir. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4985, 'nvi', 'Deuteronômio', 'dt', 3, 10, 'Conquistamos todas as cidades do planalto, e toda a Gileade, e também toda a Basã, até Salcá e Edrei, cidades do reino de Ogue, em Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4986, 'nvi', 'Deuteronômio', 'dt', 3, 11, 'Ogue, rei de Basã, era o único sobrevivente dos refains. Sua cama era de ferro e tinha, pela medida comum, quatro metros de comprimento e um metro e oitenta centímetros de largura. Ela ainda está em Rabá dos amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4987, 'nvi', 'Deuteronômio', 'dt', 3, 12, 'Da terra da qual tomamos posse naquela época, o território que vai de Aroer, junto ao ribeiro do Arnom, até mais da metade dos montes de Gileade com as suas cidades, dei-o às tribos de Rúben e de Gade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4988, 'nvi', 'Deuteronômio', 'dt', 3, 13, 'O restante de Gileade e também toda a Basã, o reino de Ogue, dei-o à metade da tribo de Manassés. ( Toda a região de Argobe em Basã era conhecida no passado como a terra dos refains.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4989, 'nvi', 'Deuteronômio', 'dt', 3, 14, 'Jair, um descendente de Manassés, conquistou toda a região de Argobe até à fronteira dos gesuritas e dos maacatitas; essa região recebeu o seu nome, de modo que até hoje Basã é chamada Povoados de Jair. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4990, 'nvi', 'Deuteronômio', 'dt', 3, 15, 'E dei Gileade a Maquir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4991, 'nvi', 'Deuteronômio', 'dt', 3, 16, 'Às tribos de Rúben e de Gade dei a região que vai de Gileade, até o ribeiro do Arnom ( a fronteira passava bem no meio do vale ) e até o vale do Jaboque, na fronteira dos amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4992, 'nvi', 'Deuteronômio', 'dt', 3, 17, 'Dei-lhe também a Arabá, tendo como fronteira ocidental o Jordão, desde Quinerete até o mar da Arabá, que é o mar Salgado, abaixo das encostas do Pisga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4993, 'nvi', 'Deuteronômio', 'dt', 3, 18, 'Naquela ocasião eu lhes ordenei o seguinte: O Senhor, o Deus de vocês, deu-lhes esta terra para que dela tomem posse. Todos os guerreiros devem marchar à frente dos seus irmãos israelitas, armados para a guerra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4994, 'nvi', 'Deuteronômio', 'dt', 3, 19, 'Deixem nas cidades que lhes dei as mulheres, as crianças e os grandes rebanhos, que eu sei que vocês possuem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4995, 'nvi', 'Deuteronômio', 'dt', 3, 20, 'até que o Senhor conceda descanso aos seus outros irmãos israelitas como deu a vocês, e tomem eles posse da terra que o Senhor, o Deus de vocês, está dando a eles, do outro lado do Jordão. Depois vocês poderão retornar, cada um à propriedade que lhes dei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4996, 'nvi', 'Deuteronômio', 'dt', 3, 21, 'Naquela ocasião também ordenei a Josué: "Você viu com os seus próprios olhos tudo o que o Senhor, o Deus de vocês, fez com estes dois reis. Assim o Senhor fará com todos os reinos pelos quais vocês terão que passar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4997, 'nvi', 'Deuteronômio', 'dt', 3, 22, 'Não tenham medo deles. O Senhor, o Deus de vocês, é quem lutará por vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4998, 'nvi', 'Deuteronômio', 'dt', 3, 23, 'Naquela ocasião implorei ao Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4999, 'nvi', 'Deuteronômio', 'dt', 3, 24, '"Ó Soberano Senhor, tu começaste a mostrar a teu servo a tua grandeza e a tua mão poderosa! Que Deus existe no céu ou na terra que possa realizar as tuas obras e os teus feitos poderosos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5000, 'nvi', 'Deuteronômio', 'dt', 3, 25, 'Deixa-me atravessar, eu te suplico, e ver a boa terra do outro lado do Jordão, a bela região montanhosa e o Líbano! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5001, 'nvi', 'Deuteronômio', 'dt', 3, 26, 'Todavia, por causa de vocês, o Senhor irou-se contra mim e não quis me atender. "Basta", ele disse. "Não me fale mais sobre isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5002, 'nvi', 'Deuteronômio', 'dt', 3, 27, 'Suba ao ponto mais alto do Pisga e olhe para o Oeste, para o Norte, para o Sul, e para o Leste. Veja a terra com os seus próprios olhos, pois você não atravessará o Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5003, 'nvi', 'Deuteronômio', 'dt', 3, 28, 'Portanto, dê ordens a Josué, fortaleça-o e encoraje-o; porque será ele que atravessará à frente deste povo, e lhes repartirá por herança a terra que você apenas verá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5004, 'nvi', 'Deuteronômio', 'dt', 3, 29, 'Então ficamos acampados no vale, diante de Bete-Peor.');
