INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5759, 'nvi', 'Deuteronômio', 'dt', 32, 1, 'Escutem, ó céus, e eu falarei; ouça, ó terra, as palavras da minha boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5760, 'nvi', 'Deuteronômio', 'dt', 32, 2, 'Que o meu ensino caia como chuva e as minhas palavras desçam como orvalho, como chuva branda sobre o pasto novo, como garoa sobre tenras plantas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5761, 'nvi', 'Deuteronômio', 'dt', 32, 3, 'Proclamarei o nome do Senhor. Louvem a grandeza do nosso Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5762, 'nvi', 'Deuteronômio', 'dt', 32, 4, 'Ele é a Rocha, as suas obras são perfeitas, e todos os seus caminhos são justos. É Deus fiel, que não comete erros; justo e reto ele é.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5763, 'nvi', 'Deuteronômio', 'dt', 32, 5, 'Seus filhos têm agido corruptamente para com ele, e não como filhos; que vergonha! São geração pervertida e transviada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5764, 'nvi', 'Deuteronômio', 'dt', 32, 6, 'É assim que retribuem ao Senhor, povo insensato e ignorante? Não é ele o Pai de vocês, o seu Criador, que os fez e os formou?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5765, 'nvi', 'Deuteronômio', 'dt', 32, 7, 'Lembrem-se dos dias do passado; considerem as gerações há muito passadas. Perguntem aos seus pais, e estes lhes contarão, aos seus líderes, e eles lhes explicarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5766, 'nvi', 'Deuteronômio', 'dt', 32, 8, 'Quando o Altíssimo deu às nações a sua herança, quando dividiu toda a humanidade, estabeleceu fronteiras para os povos de acordo com o número dos filhos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5767, 'nvi', 'Deuteronômio', 'dt', 32, 9, 'Pois o povo preferido do Senhor é este povo, Jacó é a herança que lhe coube.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5768, 'nvi', 'Deuteronômio', 'dt', 32, 10, 'Numa terra deserta ele o encontrou, numa região árida e de ventos uivantes. Ele o protegeu e dele cuidou; guardou-o como a menina dos seus olhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5769, 'nvi', 'Deuteronômio', 'dt', 32, 11, 'como a águia que desperta a sua ninhada, paira sobre os seus filhotes, e depois estende as asas para apanhá-los, levando-os sobre elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5770, 'nvi', 'Deuteronômio', 'dt', 32, 12, 'O Senhor sozinho o levou; nenhum deus estrangeiro o ajudou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5771, 'nvi', 'Deuteronômio', 'dt', 32, 13, 'Ele o fez cavalgar nos lugares altos da terra e o alimentou com o fruto dos campos. Ele o nutriu com mel tirado da rocha, e com óleo extraído do penhasco pedregoso,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5772, 'nvi', 'Deuteronômio', 'dt', 32, 14, 'com coalhada e leite do gado e do rebanho, e com cordeiros e bodes cevados; com os melhores carneiros de Basã e com as mais excelentes sementes de trigo. Você bebeu o espumoso sangue das uvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5773, 'nvi', 'Deuteronômio', 'dt', 32, 15, 'Jesurum engordou e deu pontapés; você engordou, tornou-se pesado e farto de alimentos. Abandonou o Deus que o fez e rejeitou a Rocha, que é o seu Salvador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5774, 'nvi', 'Deuteronômio', 'dt', 32, 16, 'Eles o deixaram com ciúmes por causa dos deuses estrangeiros, e o provocaram com os seus ídolos abomináveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5775, 'nvi', 'Deuteronômio', 'dt', 32, 17, 'Sacrificaram a demônios que não são Deus, a deuses que não conheceram, a deuses que surgiram recentemente, a deuses que os seus antepassados não adoraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5776, 'nvi', 'Deuteronômio', 'dt', 32, 18, 'Vocês abandonaram a Rocha, que os gerou; vocês se esqueceram do Deus que os fez nascer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5777, 'nvi', 'Deuteronômio', 'dt', 32, 19, 'O Senhor viu isso e os rejeitou, porque foi provocado pelos seus filhos e suas filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5778, 'nvi', 'Deuteronômio', 'dt', 32, 20, '"Esconderei o meu rosto deles", disse, "e verei qual o fim que terão; pois são geração perversa, filhos infiéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5779, 'nvi', 'Deuteronômio', 'dt', 32, 21, 'Provocaram-me os ciúmes com aquilo que nem deus é e irritaram-me com seus ídolos inúteis. Farei que tenham ciúmes de quem não é meu povo; eu os provocarei à ira por meio de uma nação insensata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5780, 'nvi', 'Deuteronômio', 'dt', 32, 22, 'Pois um fogo foi aceso pela minha ira, fogo que queimará até às profundezas do Sheol. Ele devorará a terra e as suas colheitas e consumirá os alicerces dos montes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5781, 'nvi', 'Deuteronômio', 'dt', 32, 23, '"Amontoarei desgraças sobre eles e contra eles gastarei as minhas flechas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5782, 'nvi', 'Deuteronômio', 'dt', 32, 24, 'Enviarei dentes de feras, uma fome devastadora, uma peste avassaladora e uma praga mortal; enviarei contra eles dentes de animais selvagens, e veneno de víboras que se arrastam no pó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5783, 'nvi', 'Deuteronômio', 'dt', 32, 25, 'Nas ruas a espada os deixará sem filhos; em seus lares reinará o terror. Morrerão moços e moças, crianças e homens já grisalhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5784, 'nvi', 'Deuteronômio', 'dt', 32, 26, 'Eu disse que os dispersaria e que apagaria da humanidade a lembrança deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5785, 'nvi', 'Deuteronômio', 'dt', 32, 27, 'Mas temi a provocação do inimigo, que o adversário entendesse mal e dissesse: ‘A nossa mão triunfou; o Senhor nada fez’. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5786, 'nvi', 'Deuteronômio', 'dt', 32, 28, 'É uma nação sem juízo; não têm discernimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5787, 'nvi', 'Deuteronômio', 'dt', 32, 29, 'Quem dera fossem sábios e entendessem; e compreendessem qual será o seu fim!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5788, 'nvi', 'Deuteronômio', 'dt', 32, 30, 'Como poderia um só homem perseguir mil, ou dois porem em fuga dez mil, a não ser que a sua Rocha os tivesse vendido, a não ser que o Senhor os tivesse abandonado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5789, 'nvi', 'Deuteronômio', 'dt', 32, 31, 'Pois a rocha deles não é como a nossa Rocha, com o que até mesmo os nossos inimigos concordam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5790, 'nvi', 'Deuteronômio', 'dt', 32, 32, 'A vinha deles é de Sodoma e das lavouras de Gomorra. Suas uvas estão cheias de veneno, e seus cachos, de amargura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5791, 'nvi', 'Deuteronômio', 'dt', 32, 33, 'O vinho deles é a peçonha das serpentes, o veneno mortal das cobras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5792, 'nvi', 'Deuteronômio', 'dt', 32, 34, '"Acaso não guardei isto em segredo? Não o selei em meus tesouros?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5793, 'nvi', 'Deuteronômio', 'dt', 32, 35, 'A mim pertence a vingança e a retribuição. No devido tempo os pés deles escorregarão; o dia da sua desgraça está chegando e o seu próprio destino se apressa sobre eles. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5794, 'nvi', 'Deuteronômio', 'dt', 32, 36, 'O Senhor julgará o seu povo e terá compaixão dos seus servos, quando vir que a força deles se esvaiu e que ninguém sobrou, nem escravo nem livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5795, 'nvi', 'Deuteronômio', 'dt', 32, 37, 'Ele dirá: "Agora, onde estão os seus deuses, a rocha em que se refugiaram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5796, 'nvi', 'Deuteronômio', 'dt', 32, 38, 'os deuses que comeram a gordura dos seus sacrifícios e beberam o vinho das suas ofertas derramadas? Que eles se levantem para ajudá-los! Que eles lhes ofereçam abrigo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5797, 'nvi', 'Deuteronômio', 'dt', 32, 39, '"Vejam agora que eu sou o único, eu mesmo. Não há deus além de mim. Faço morrer e faço viver, feri e curarei, e ninguém é capaz de livrar-se da minha mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5798, 'nvi', 'Deuteronômio', 'dt', 32, 40, 'Ergo a minha mão para os céus e declaro: Juro pelo meu nome que,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5799, 'nvi', 'Deuteronômio', 'dt', 32, 41, 'quando eu afiar a minha espada refulgente e a minha mão empunhá-la para julgar, eu me vingarei dos meus adversários e retribuirei àqueles que me odeiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5800, 'nvi', 'Deuteronômio', 'dt', 32, 42, 'Embeberei as minhas flechas em sangue, enquanto a minha espada devorar carne: o sangue dos mortos e dos cativos, as cabeças dos líderes inimigos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5801, 'nvi', 'Deuteronômio', 'dt', 32, 43, 'Cantem de alegria, ó nações, com o povo dele, pois ele vingará o sangue dos seus servos; retribuirá com vingança aos seus adversários e fará propiciação por sua terra e por seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5802, 'nvi', 'Deuteronômio', 'dt', 32, 44, 'Moisés veio com Josué, filho de Num, e recitou todas as palavras desta canção na presença do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5803, 'nvi', 'Deuteronômio', 'dt', 32, 45, 'Quando Moisés terminou de recitar todas essas palavras a todo o Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5804, 'nvi', 'Deuteronômio', 'dt', 32, 46, 'disse-lhes: "Guardem no coração todas as palavras que hoje lhes declarei solenemente, para que ordenem aos seus filhos que obedeçam fielmente a todas as palavras desta lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5805, 'nvi', 'Deuteronômio', 'dt', 32, 47, 'Elas não são palavras inúteis. São a sua vida. Por meio delas vocês viverão muito tempo na terra da qual tomarão posse do outro lado do Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5806, 'nvi', 'Deuteronômio', 'dt', 32, 48, 'Naquele mesmo dia o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5807, 'nvi', 'Deuteronômio', 'dt', 32, 49, '"Suba as montanhas de Abarim, até o monte Nebo, em Moabe, em frente de Jericó, e contemple Canaã, a terra que dou aos israelitas como propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5808, 'nvi', 'Deuteronômio', 'dt', 32, 50, 'Ali, na montanha que você tiver subido, você morrerá e será reunido aos seus antepassados, assim como o seu irmão Arão morreu no monte Hor e foi reunido aos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5809, 'nvi', 'Deuteronômio', 'dt', 32, 51, 'Assim será porque vocês dois foram infiéis para comigo na presença dos israelitas, junto às águas de Meribá, em Cades, no deserto de Zim, e porque vocês não sustentaram a minha santidade no meio dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5810, 'nvi', 'Deuteronômio', 'dt', 32, 52, 'Portanto, você verá a terra somente à distância, mas não entrará na terra que estou dando ao povo de Israel".');
