INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5811, 'nvi', 'Deuteronômio', 'dt', 33, 1, 'Esta é a bênção com a qual Moisés, homem de Deus, abençoou os israelitas antes da sua morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5812, 'nvi', 'Deuteronômio', 'dt', 33, 2, 'Ele disse: "O Senhor veio do Sinai e alvoreceu sobre eles desde o Seir, resplandeceu desde o monte Parã. Veio com miríades de santos desde o sul, desde as encostas de suas montanhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5813, 'nvi', 'Deuteronômio', 'dt', 33, 3, 'Certamente és tu que amas o povo; todos os santos estão em tuas mãos. A teus pés todos eles se prostram e de ti recebem instrução,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5814, 'nvi', 'Deuteronômio', 'dt', 33, 4, 'a lei que Moisés nos deu, a herança da assembléia de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5815, 'nvi', 'Deuteronômio', 'dt', 33, 5, 'Ele era rei sobre Jesurum, quando os chefes do povo se reuniam, juntamente com as tribos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5816, 'nvi', 'Deuteronômio', 'dt', 33, 6, '"Que Rúben viva e não morra, mesmo sendo poucos os seus homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5817, 'nvi', 'Deuteronômio', 'dt', 33, 7, 'E disse a respeito de Judá: "Ouve, ó Senhor, o grito de Judá; traze-o para o seu povo. Que as suas próprias mãos sejam suficientes, e que haja auxílio contra os seus adversários! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5818, 'nvi', 'Deuteronômio', 'dt', 33, 8, 'A respeito de Levi disse: "O teu Urim e o teu Tumim pertencem ao homem a quem favoreceste. Tu o provaste em Massá; disputaste com ele junto às águas de Meribá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5819, 'nvi', 'Deuteronômio', 'dt', 33, 9, 'Levi disse do seu pai e da sua mãe: ‘Não tenho consideração por eles’. Não reconheceu os seus irmãos, Nem conheceu os próprios filhos, apesar de que guardaram a tua palavra e observaram a tua aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5820, 'nvi', 'Deuteronômio', 'dt', 33, 10, 'Ele ensina as tuas ordenanças a Jacó e a tua lei a Israel. Ele te oferece incenso e holocaustos completos no teu altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5821, 'nvi', 'Deuteronômio', 'dt', 33, 11, 'Abençoa todos os seus esforços, ó Senhor, e aprova a obra das suas mãos. Despedaça os lombos dos seus adversários, dos que o odeiam, sejam quem forem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5822, 'nvi', 'Deuteronômio', 'dt', 33, 12, 'A respeito de Benjamim disse: "Que o amado do Senhor descanse nele em segurança, pois ele o protege o tempo inteiro, e aquele a quem o Senhor ama descansa nos seus braços".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5823, 'nvi', 'Deuteronômio', 'dt', 33, 13, 'A respeito de José disse: "Que o Senhor abençoe a sua terra com o precioso orvalho que vem de cima, do céu, e com as águas das profundezas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5824, 'nvi', 'Deuteronômio', 'dt', 33, 14, 'com o melhor que o sol amadurece e com o melhor que a lua possa dar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5825, 'nvi', 'Deuteronômio', 'dt', 33, 15, 'com as dádivas mais bem escolhidas dos montes antigos e com a fertilidade das colinas eternas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5826, 'nvi', 'Deuteronômio', 'dt', 33, 16, 'com os melhores frutos da terra e a sua plenitude, e o favor daquele que apareceu na sarça ardente. Que tudo isso repouse sobre a cabeça de José, sobre a fronte do escolhido entre os seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5827, 'nvi', 'Deuteronômio', 'dt', 33, 17, 'É majestoso como a primeira cria de um touro; seus chifres são os chifres de um boi selvagem, com os quais ferirá as nações, até os confins da terra. Assim são as dezenas de milhares de Efraim; assim são os milhares de Manassés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5828, 'nvi', 'Deuteronômio', 'dt', 33, 18, 'A respeito de Zebulom disse: "Alegre-se, Zebulom, em suas viagens, e você, Issacar, em suas tendas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5829, 'nvi', 'Deuteronômio', 'dt', 33, 19, 'Eles convocarão povos para o monte e ali oferecerão sacrifícios de justiça; farão um banquete com a riqueza dos mares, com os tesouros ocultos das praias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5830, 'nvi', 'Deuteronômio', 'dt', 33, 20, 'A respeito de Gade disse: "Bendito é aquele que amplia os domínios de Gade! Gade fica à espreita como um leão; despedaça um braço e também a cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5831, 'nvi', 'Deuteronômio', 'dt', 33, 21, 'Escolheu para si o melhor; a porção do líder lhe foi reservada. Tornou-se o chefe do povo e executou a justa vontade do Senhor e os seus juízos sobre Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5832, 'nvi', 'Deuteronômio', 'dt', 33, 22, 'A respeito de Dã disse: "Dã é um filhote de leão, que vem saltando desde Basã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5833, 'nvi', 'Deuteronômio', 'dt', 33, 23, 'A respeito de Naftali disse: "Naftali tem fartura do favor do Senhor e está repleto de suas bênçãos; suas posses estendem-se para o sul, em direção ao mar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5834, 'nvi', 'Deuteronômio', 'dt', 33, 24, 'A respeito de Aser disse: "Bendito é Aser entre os filhos; seja ele favorecido por seus irmãos, e banhe os seus pés no azeite!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5835, 'nvi', 'Deuteronômio', 'dt', 33, 25, 'Sejam de ferro e bronze as trancas das suas portas, e dure a sua força como os seus dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5836, 'nvi', 'Deuteronômio', 'dt', 33, 26, '"Não há ninguém como o Deus de Jesurum, que cavalga os céus para ajudá-lo, e cavalga as nuvens em sua majestade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5837, 'nvi', 'Deuteronômio', 'dt', 33, 27, 'O Deus eterno é o seu refúgio, e para segurá-lo estão os braços eternos. Ele expulsará os inimigos da sua presença, dizendo: ‘Destrua-os! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5838, 'nvi', 'Deuteronômio', 'dt', 33, 28, 'Somente Israel viverá em segurança; a fonte de Jacó está segura numa terra de trigo e de vinho novo, onde os céus gotejam orvalho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5839, 'nvi', 'Deuteronômio', 'dt', 33, 29, 'Como você é feliz, Israel! Quem é como você, povo salvo pelo Senhor? Ele é o seu abrigo, o seu ajudador e a sua espada gloriosa. Os seus inimigos se encolherão diante de você, mas você pisará os seus altos".');
