INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5005, 'nvi', 'Deuteronômio', 'dt', 4, 1, 'E agora, ó Israel, ouça os decretos e as leis que lhes estou ensinando a cumprir, para que vivam e tomem posse da terra, que o Senhor, o Deus dos seus antepassados, dá a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5006, 'nvi', 'Deuteronômio', 'dt', 4, 2, 'Nada acrescentem às palavras que eu lhes ordeno e delas nada retirem, mas obedeçam aos mandamentos do Senhor, o Deus de vocês, que eu lhes ordeno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5007, 'nvi', 'Deuteronômio', 'dt', 4, 3, 'Vocês viram com os próprios olhos o que o Senhor fez em Baal-Peor. O Senhor, o seu Deus, destruiu do meio de vocês todos os que seguiram a Baal-Peor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5008, 'nvi', 'Deuteronômio', 'dt', 4, 4, 'mas vocês, que permaneceram fiéis ao Senhor, o Deus de vocês, estão hoje todos vivos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5009, 'nvi', 'Deuteronômio', 'dt', 4, 5, 'Eu lhes ensinei decretos e leis, como me ordenou o Senhor, o meu Deus, para que sejam cumpridos na terra na qual vocês estão entrando para dela tomar posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5010, 'nvi', 'Deuteronômio', 'dt', 4, 6, 'Vocês devem obedecer-lhes e cumpri-los, pois assim os outros povos verão a sabedoria e o discernimento de vocês. Quando eles ouvirem todos estes decretos dirão: "De fato esta grande nação é um povo sábio e inteligente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5011, 'nvi', 'Deuteronômio', 'dt', 4, 7, 'Pois, que grande nação tem um Deus tão próximo como o Senhor, o nosso Deus, sempre que o invocamos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5012, 'nvi', 'Deuteronômio', 'dt', 4, 8, 'Ou, que grande nação tem decretos e preceitos tão justos como esta lei que estou apresentando a vocês hoje?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5013, 'nvi', 'Deuteronômio', 'dt', 4, 9, 'Apenas cuidado! Muito cuidado, para que vocês nunca se esqueçam das coisas que os seus olhos viram; conservem-nas na memória por toda a sua vida. Contem-nas a seus filhos e a seus netos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5014, 'nvi', 'Deuteronômio', 'dt', 4, 10, 'Houve um dia em que vocês estiveram diante do Senhor, o seu Deus, em Horebe, quando o Senhor me disse: "Reúna o povo diante de mim para ouvir as minhas palavras, a fim de que aprendam a me temer enquanto viverem sobre a terra, e as ensinem a seus filhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5015, 'nvi', 'Deuteronômio', 'dt', 4, 11, 'Vocês se aproximaram e ficaram ao pé do monte. O monte ardia em chamas que subiam até o céu, e estava envolvido por uma nuvem escura e densa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5016, 'nvi', 'Deuteronômio', 'dt', 4, 12, 'Então o Senhor falou a vocês do meio do fogo. Vocês ouviram as palavras, mas não viram forma alguma; apenas se ouvia a voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5017, 'nvi', 'Deuteronômio', 'dt', 4, 13, 'Ele lhes anunciou a sua aliança, os Dez Mandamentos. E escreveu-os sobre duas tábuas de pedra e ordenou que os cumprissem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5018, 'nvi', 'Deuteronômio', 'dt', 4, 14, 'Naquela ocasião, o Senhor mandou-me ensinar-lhes decretos e leis para que vocês os cumprissem na terra da qual vão tomar posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5019, 'nvi', 'Deuteronômio', 'dt', 4, 15, 'No dia em que o Senhor lhes falou do meio do fogo em Horebe, vocês não viram forma alguma. Portanto, tenham muito cuidado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5020, 'nvi', 'Deuteronômio', 'dt', 4, 16, 'para que não se corrompam e não façam para si um ídolo, uma imagem de qualquer forma semelhante a homem ou mulher,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5021, 'nvi', 'Deuteronômio', 'dt', 4, 17, 'ou a qualquer animal da terra ou a qualquer ave que voa no céu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5022, 'nvi', 'Deuteronômio', 'dt', 4, 18, 'ou a qualquer criatura que se move rente ao chão ou a qualquer peixe que vive nas águas debaixo da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5023, 'nvi', 'Deuteronômio', 'dt', 4, 19, 'E para que, ao erguerem os olhos ao céu e virem o sol, a lua e as estrelas, todos os corpos celestes, vocês não se desviem e se prostrem diante deles, e prestem culto àquilo que o Senhor, o seu Deus, distribuiu a todos os povos debaixo do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5024, 'nvi', 'Deuteronômio', 'dt', 4, 20, 'A vocês, o Senhor tomou e os tirou da fornalha de fundir ferro, do Egito, para serem o povo de sua herança, como hoje se pode ver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5025, 'nvi', 'Deuteronômio', 'dt', 4, 21, 'O Senhor irou-se contra mim por causa de vocês e jurou que eu não atravessaria o Jordão e não entraria na boa terra que o Senhor, o seu Deus, está lhes dando por herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5026, 'nvi', 'Deuteronômio', 'dt', 4, 22, 'Eu morrerei nesta terra; não atravessarei o Jordão. Mas vocês atravessarão e tomarão posse daquela boa terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5027, 'nvi', 'Deuteronômio', 'dt', 4, 23, 'Tenham o cuidado de não esquecer da aliança que o Senhor, o seu Deus, fez com vocês; não façam para si ídolo algum com a forma de qualquer coisa que o Senhor, o seu Deus, proibiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5028, 'nvi', 'Deuteronômio', 'dt', 4, 24, 'Pois o Senhor, o seu Deus, é Deus zeloso; é fogo consumidor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5029, 'nvi', 'Deuteronômio', 'dt', 4, 25, 'Quando vocês tiverem filhos e netos, e já estiverem a muito tempo na terra, e se corromperem e fizerem ídolos de qualquer tipo, fazendo o que o Senhor, o seu Deus, reprova, provocando a sua ira,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5030, 'nvi', 'Deuteronômio', 'dt', 4, 26, 'invoco hoje o céu e a terra como testemunhas contra vocês de que vocês serão rapidamente eliminados da terra, da qual estão tomando posse ao atravessar o Jordão. Vocês não viverão muito ali; serão totalmente destruídos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5031, 'nvi', 'Deuteronômio', 'dt', 4, 27, 'O Senhor os espalhará entre os povos, e restarão apenas alguns de vocês entre as nações às quais o Senhor os levará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5032, 'nvi', 'Deuteronômio', 'dt', 4, 28, 'Lá vocês prestarão culto a deuses de madeira e de pedra, deuses feitos por mãos humanas, deuses que não podem ver, nem ouvir, nem comer, nem cheirar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5033, 'nvi', 'Deuteronômio', 'dt', 4, 29, 'E lá procurarão o Senhor, o seu Deus, e o acharão, se o procurarem de todo o seu coração e de toda a sua alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5034, 'nvi', 'Deuteronômio', 'dt', 4, 30, 'Quando vocês estiverem sofrendo e todas essas coisas tiverem acontecido com vocês, então, em dias futuros, vocês voltarão para o Senhor, o seu Deus, e lhe obedecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5035, 'nvi', 'Deuteronômio', 'dt', 4, 31, 'Pois o Senhor, o seu Deus, é Deus misericordioso; ele não os abandonará, nem os destruirá nem se esquecerá da aliança que com juramento fez com os seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5036, 'nvi', 'Deuteronômio', 'dt', 4, 32, 'Perguntem, agora, aos tempos antigos, antes de vocês existirem, desde o dia em que Deus criou o homem sobre a terra. Perguntem de um lado ao outro do céu: Já aconteceu algo tão grandioso ou já se ouviu algo parecido?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5037, 'nvi', 'Deuteronômio', 'dt', 4, 33, 'Que povo ouviu a voz de Deus falando do meio do fogo, como vocês ouviram, e continua vivo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5038, 'nvi', 'Deuteronômio', 'dt', 4, 34, 'Ou que deus decidiu tirar uma nação do meio de outra para lhe pertencer, com provas, sinais, maravilhas e lutas, com mão poderosa e braço forte, e com feitos temíveis e grandiosos, conforme tudo o que o Senhor fez por vocês no Egito, como vocês viram com os seus próprios olhos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5039, 'nvi', 'Deuteronômio', 'dt', 4, 35, 'Tudo isso foi mostrado a vocês para que soubessem que o Senhor é Deus; e que não há outro além dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5040, 'nvi', 'Deuteronômio', 'dt', 4, 36, 'Do céu ele fez com que vocês ouvissem a sua voz, para discipliná-los. Na terra, mostrou-lhes o seu grande fogo, e vocês ouviram as suas palavras vindas do meio do fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5041, 'nvi', 'Deuteronômio', 'dt', 4, 37, 'E porque amou os seus antepassados e escolheu a descendência deles, ele foi em pessoa tirá-los do Egito com o seu grande poder,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5042, 'nvi', 'Deuteronômio', 'dt', 4, 38, 'para expulsar diante de vocês nações maiores e mais fortes, a fim de fazê-los entrar e possuir como herança a terra deles, como hoje se vê.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5043, 'nvi', 'Deuteronômio', 'dt', 4, 39, 'Reconheçam isso hoje, e ponham no coração que o Senhor é Deus em cima nos céus e embaixo na terra. Não há nenhum outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5044, 'nvi', 'Deuteronômio', 'dt', 4, 40, 'Obedeçam aos seus decretos e mandamentos que hoje eu lhes ordeno, para que tudo vá bem com vocês e com seus descendentes, e para que vivam muito tempo na terra que o Senhor, o seu Deus, lhes dá para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5045, 'nvi', 'Deuteronômio', 'dt', 4, 41, 'Então Moisés separou três cidades a leste do Jordão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5046, 'nvi', 'Deuteronômio', 'dt', 4, 42, 'para onde poderia fugir quem tivesse matado alguém sem intenção e sem premeditação. O perseguido poderia fugir para uma dessas cidades a fim de salvar a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5047, 'nvi', 'Deuteronômio', 'dt', 4, 43, 'As cidades eram as seguintes: Bezer, no planalto do deserto, para a tribo de Rúben; Ramote, em Gileade, para a tribo de Gade; e Golã, em Basã, para a tribo de Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5048, 'nvi', 'Deuteronômio', 'dt', 4, 44, 'Esta é a lei que Moisés apresentou aos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5049, 'nvi', 'Deuteronômio', 'dt', 4, 45, 'Estes são os mandamentos, os decretos e as ordenanças que Moisés promulgou como leis para os israelitas quando saíram do Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5050, 'nvi', 'Deuteronômio', 'dt', 4, 46, 'do outro lado do Jordão, no vale fronteiro a Bete-Peor, na terra de Seom, rei dos amorreus, que habitava em Hesbom, a quem Moisés e os israelitas derrotaram quando saíram do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5051, 'nvi', 'Deuteronômio', 'dt', 4, 47, 'Eles tomaram posse da terra dele e da terra de Ogue, rei de Basã, os dois reis amorreus que viviam a leste do Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5052, 'nvi', 'Deuteronômio', 'dt', 4, 48, 'Essa terra estendia-se desde Aroer, na margem do ribeiro do Arnom, até o monte Siom, isto é, o Hermom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5053, 'nvi', 'Deuteronômio', 'dt', 4, 49, 'e incluía toda a região da Arabá, a leste do Jordão, até o mar da Arabá, abaixo das encostas do Pisga.');
