INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5087, 'nvi', 'Deuteronômio', 'dt', 6, 1, 'Esta é a lei, isto é, os decretos e as ordenanças, que o Senhor, o seu Deus ordenou que eu lhes ensinasse, para que vocês os cumpram na terra para a qual estão indo para dela tomar posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5088, 'nvi', 'Deuteronômio', 'dt', 6, 2, 'Desse modo vocês, seus filhos e seus netos temerão ao Senhor, o seu Deus, e obedecerão a todos os seus decretos e mandamentos, que eu lhes ordeno, todos os dias da sua vida, para que tenham vida longa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5089, 'nvi', 'Deuteronômio', 'dt', 6, 3, 'Ouça e obedeça, ó Israel! Assim tudo lhe irá bem e você será muito numeroso numa terra onde manam leite e mel, como lhe prometeu o Senhor, o Deus dos seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5090, 'nvi', 'Deuteronômio', 'dt', 6, 4, 'Ouça, ó Israel: O Senhor, o nosso Deus, é o único Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5091, 'nvi', 'Deuteronômio', 'dt', 6, 5, 'Ame o Senhor, o seu Deus, de todo o seu coração, de toda a sua alma e de todas as suas forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5092, 'nvi', 'Deuteronômio', 'dt', 6, 6, 'Que todas estas palavras que hoje lhe ordeno estejam em seu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5093, 'nvi', 'Deuteronômio', 'dt', 6, 7, 'Ensine-as com persistência a seus filhos. Converse sobre elas quando estiver sentado em casa, quando estiver andando pelo caminho, quando se deitar e quando se levantar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5094, 'nvi', 'Deuteronômio', 'dt', 6, 8, 'Amarre-as como um sinal nos braços e prenda-as na testa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5095, 'nvi', 'Deuteronômio', 'dt', 6, 9, 'Escreva-as nos batentes das portas de sua casa e em seus portões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5096, 'nvi', 'Deuteronômio', 'dt', 6, 10, 'O Senhor, o seu Deus, os conduzirá à terra que jurou aos seus antepassados, Abraão, Isaque e Jacó, que daria a vocês, terra com grandes e boas cidades que vocês não construíram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5097, 'nvi', 'Deuteronômio', 'dt', 6, 11, 'com casas cheias de tudo que há de melhor, de coisas que vocês não produziram, com cisternas que vocês não cavaram, com vinhas e oliveiras que não plantaram. Quando isso acontecer, e vocês comerem e ficarem satisfeitos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5098, 'nvi', 'Deuteronômio', 'dt', 6, 12, 'tenham cuidado! Não esqueçam o Senhor que os tirou do Egito, da terra da escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5099, 'nvi', 'Deuteronômio', 'dt', 6, 13, 'Temam o Senhor, o seu Deus, e só a ele prestem culto, e jurem somente pelo seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5100, 'nvi', 'Deuteronômio', 'dt', 6, 14, 'Não sigam outros deuses, os deuses dos povos ao redor;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5101, 'nvi', 'Deuteronômio', 'dt', 6, 15, 'pois o Senhor, o seu Deus, que está no meio de vocês, é Deus zeloso; a ira do Senhor, o seu Deus, se acenderá contra vocês, e ele os banirá da face da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5102, 'nvi', 'Deuteronômio', 'dt', 6, 16, 'Não ponham à prova o Senhor, o seu Deus, como fizeram em Massá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5103, 'nvi', 'Deuteronômio', 'dt', 6, 17, 'Obedeçam cuidadosamente aos mandamentos do Senhor, o seu Deus, e aos preceitos e decretos que ele lhes ordenou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5104, 'nvi', 'Deuteronômio', 'dt', 6, 18, 'Façam o que é justo e bom perante o Senhor, para que tudo lhes vá bem e vocês entrem e tomem posse da boa terra que o Senhor prometeu, sob juramento, a seus antepassados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5105, 'nvi', 'Deuteronômio', 'dt', 6, 19, 'expulsando todos os seus inimigos de diante de vocês, conforme o Senhor prometeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5106, 'nvi', 'Deuteronômio', 'dt', 6, 20, 'No futuro, quando o seu filho lhes perguntar: "O que significam estes preceitos, decretos e ordenanças que o Senhor, o nosso Deus, ordenou a vocês? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5107, 'nvi', 'Deuteronômio', 'dt', 6, 21, 'Vocês lhe responderão: "Fomos escravos do faraó no Egito, mas o Senhor nos tirou do Egito com mão poderosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5108, 'nvi', 'Deuteronômio', 'dt', 6, 22, 'O Senhor realizou, diante dos nossos olhos, sinais e maravilhas grandiosas e terríveis contra o Egito e contra o faraó e toda a sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5109, 'nvi', 'Deuteronômio', 'dt', 6, 23, 'Mas ele nos tirou de lá para nos trazer para cá e nos dar a terra que, sob juramento, prometeu a nossos antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5110, 'nvi', 'Deuteronômio', 'dt', 6, 24, 'O Senhor nos ordenou que obedecêssemos a todos estes decretos e que temêssemos o Senhor, o nosso Deus, para que sempre fôssemos bem sucedidos e preservados em vida, como hoje se pode ver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5111, 'nvi', 'Deuteronômio', 'dt', 6, 25, 'E, se nós nos aplicarmos a obedecer a toda essa lei perante o Senhor, o nosso Deus, conforme ele nos ordenou, esta será a nossa justiça".');
