INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17495, 'nvi', 'Eclesiastes', 'ec', 10, 1, 'Assim como a mosca morta produz mau cheiro e estraga o perfume, também um pouco de insensatez pesa mais que a sabedoria e a honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17496, 'nvi', 'Eclesiastes', 'ec', 10, 2, 'O coração do sábio se inclina para o bem, mas o coração do tolo, para o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17497, 'nvi', 'Eclesiastes', 'ec', 10, 3, 'Mesmo quando anda pelo caminho, o tolo age sem o mínimo bom senso e mostra a todos que não passa de um tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17498, 'nvi', 'Eclesiastes', 'ec', 10, 4, 'Se a ira de uma autoridade se levantar contra você, não abandone o seu posto; a tranqüilidade evita grandes erros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17499, 'nvi', 'Eclesiastes', 'ec', 10, 5, 'Há outro mal que vi debaixo do sol, um erro cometido pelos que governam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17500, 'nvi', 'Eclesiastes', 'ec', 10, 6, 'Tolos são postos em cargos elevados, enquanto ricos ocupam cargos inferiores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17501, 'nvi', 'Eclesiastes', 'ec', 10, 7, 'Tenho visto servos andando a cavalo, e príncipes andando a pé, como servos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17502, 'nvi', 'Eclesiastes', 'ec', 10, 8, 'Quem cava um poço cairá nele; quem derruba um muro será picado por uma cobra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17503, 'nvi', 'Eclesiastes', 'ec', 10, 9, 'Quem arranca pedras, com elas se ferirá; quem racha lenha se arrisca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17504, 'nvi', 'Eclesiastes', 'ec', 10, 10, 'Se o machado está cego e sua lâmina não foi afiada, é preciso golpear com mais força; agir com sabedoria assegura o sucesso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17505, 'nvi', 'Eclesiastes', 'ec', 10, 11, 'Se a cobra morder antes de ser encantada, para que servirá o encantador?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17506, 'nvi', 'Eclesiastes', 'ec', 10, 12, 'As palavras do sábio lhe trazem benefícios, mas os lábios do insensato o destroem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17507, 'nvi', 'Eclesiastes', 'ec', 10, 13, 'No início as suas palavras são mera tolice, mas no final são loucura perversa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17508, 'nvi', 'Eclesiastes', 'ec', 10, 14, 'Embora o tolo fale sem parar, ninguém sabe o que está para vir; quem poderá dizer a outrem o que lhe acontecerá depois?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17509, 'nvi', 'Eclesiastes', 'ec', 10, 15, 'O trabalho do tolo o deixa tão exausto que ele nem consegue achar o caminho de casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17510, 'nvi', 'Eclesiastes', 'ec', 10, 16, 'Pobre da terra cujo rei é jovem demais e cujos líderes fazem banquetes logo de manhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17511, 'nvi', 'Eclesiastes', 'ec', 10, 17, 'Feliz é a terra cujo rei é de origem nobre, e cujos líderes comem no devido tempo, para recuperar as forças, e não para embriagar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17512, 'nvi', 'Eclesiastes', 'ec', 10, 18, 'Por causa da preguiça, o telhado se enverga; por causa das mãos indolentes, a casa tem goteiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17513, 'nvi', 'Eclesiastes', 'ec', 10, 19, 'O banquete é feito para divertir, e o vinho torna a vida alegre, mas isso tudo se paga com dinheiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17514, 'nvi', 'Eclesiastes', 'ec', 10, 20, 'Nem em pensamento insulte o rei! Nem mesmo em seu quarto amaldiçoe o rico! Porque uma ave do céu poderá levar as suas palavras, e seres alados poderão divulgar o que você disser.');
