INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17431, 'nvi', 'Eclesiastes', 'ec', 7, 1, 'Um bom nome é melhor do que um perfume finíssimo, e o dia da morte é melhor do que o dia do nascimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17432, 'nvi', 'Eclesiastes', 'ec', 7, 2, 'É melhor ir a uma casa onde há luto do que a uma casa em festa, pois a morte é o destino de todos; os vivos devem levar isso a sério!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17433, 'nvi', 'Eclesiastes', 'ec', 7, 3, 'A tristeza é melhor do que o riso, porque o rosto triste melhora o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17434, 'nvi', 'Eclesiastes', 'ec', 7, 4, 'O coração do sábio está na casa onde há luto, mas o dos tolos, na casa da alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17435, 'nvi', 'Eclesiastes', 'ec', 7, 5, 'É melhor ouvir a repreensão de um sábio do que a canção dos tolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17436, 'nvi', 'Eclesiastes', 'ec', 7, 6, 'Tal como o estalo de espinhos debaixo da panela, assim é o riso dos tolos. Isso também não faz sentido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17437, 'nvi', 'Eclesiastes', 'ec', 7, 7, 'A opressão transforma o sábio em tolo, e o suborno corrompe o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17438, 'nvi', 'Eclesiastes', 'ec', 7, 8, 'O fim das coisas é melhor do que o seu início, e o paciente é melhor que o orgulhoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17439, 'nvi', 'Eclesiastes', 'ec', 7, 9, 'Não permita que a ira domine depressa o seu espírito, pois a ira se aloja no íntimo dos tolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17440, 'nvi', 'Eclesiastes', 'ec', 7, 10, 'Não diga: "Por que os dias do passado foram melhores que os de hoje? " Pois não é sábio fazer tais perguntas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17441, 'nvi', 'Eclesiastes', 'ec', 7, 11, 'A sabedoria, como uma herança, é coisa boa e beneficia aqueles que vêem o sol.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17442, 'nvi', 'Eclesiastes', 'ec', 7, 12, 'A sabedoria oferece proteção, como o faz o dinheiro, mas a vantagem do conhecimento é esta: a sabedoria preserva a vida de quem a possui.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17443, 'nvi', 'Eclesiastes', 'ec', 7, 13, 'Considere o que Deus fez: Quem pode endireitar o que ele fez torto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17444, 'nvi', 'Eclesiastes', 'ec', 7, 14, 'Quando os dias forem bons, aproveite-os bem; mas, quando forem ruins, considere: Deus fez tanto um quanto o outro, para evitar que o homem descubra qualquer coisa sobre o seu futuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17445, 'nvi', 'Eclesiastes', 'ec', 7, 15, 'Nesta vida sem sentido eu já vi de tudo: um justo que morreu apesar da sua justiça, e um ímpio que teve vida longa apesar da sua impiedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17446, 'nvi', 'Eclesiastes', 'ec', 7, 16, 'Não seja excessivamente justo nem demasiadamente sábio; por que destruir-se a si mesmo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17447, 'nvi', 'Eclesiastes', 'ec', 7, 17, 'Não seja demasiadamente ímpio e não seja tolo; por que morrer antes do tempo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17448, 'nvi', 'Eclesiastes', 'ec', 7, 18, 'É bom reter uma coisa e não abrir mão da outra, pois quem teme a Deus evitará ambos os extremos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17449, 'nvi', 'Eclesiastes', 'ec', 7, 19, 'A sabedoria torna o sábio mais poderoso que uma cidade guardada por dez valentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17450, 'nvi', 'Eclesiastes', 'ec', 7, 20, 'Todavia, não há um só justo na terra, ninguém que pratique o bem e nunca peque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17451, 'nvi', 'Eclesiastes', 'ec', 7, 21, 'Não dê atenção a todas as palavras que o povo diz, caso contrário, poderá ouvir o seu próprio servo falando mal de você;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17452, 'nvi', 'Eclesiastes', 'ec', 7, 22, 'pois em seu coração você sabe que muitas vezes você mesmo também falou mal de outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17453, 'nvi', 'Eclesiastes', 'ec', 7, 23, 'Tudo isso eu examinei mediante a sabedoria e disse: Estou decidido a ser sábio; mas isso estava fora do meu alcance.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17454, 'nvi', 'Eclesiastes', 'ec', 7, 24, 'A realidade está bem distante e é muito profunda; quem pode descobri-la?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17455, 'nvi', 'Eclesiastes', 'ec', 7, 25, 'Por isso dediquei-me a aprender, a investigar, a buscar a sabedoria e a razão de ser das coisas, para compreender a insensatez da impiedade e a loucura da insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17456, 'nvi', 'Eclesiastes', 'ec', 7, 26, 'Descobri que muito mais amarga do que a morte é a mulher que serve de laço, cujo coração é uma armadilha e cujas mãos são correntes. O homem que agrada a Deus escapará dela, mas ao pecador ela apanhará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17457, 'nvi', 'Eclesiastes', 'ec', 7, 27, '"Veja", diz o Mestre, "foi isto que descobri: Ao comparar uma coisa com outra para descobrir a sua razão de ser,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17458, 'nvi', 'Eclesiastes', 'ec', 7, 28, 'sim, durante essa minha busca que ainda não terminou, entre mil homens, descobri apenas um que julgo digno, mas entre as mulheres não achei uma sequer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17459, 'nvi', 'Eclesiastes', 'ec', 7, 29, 'Assim, cheguei a esta conclusão: Deus fez os homens justos, mas eles foram em busca de muitas intrigas. "');
