INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12254, 'nvi', 'Esdras', 'ed', 10, 1, 'Enquanto Esdras estava orando e confessando, chorando prostrado diante do templo de Deus, uma grande multidão de israelitas, homens, mulheres e crianças, reuniram-se em volta dele. Eles também choravam amargamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12255, 'nvi', 'Esdras', 'ed', 10, 2, 'Então Secanias, filho de Jeiel, um dos descendentes de Elão, disse a Esdras: "Fomos infiéis ao nosso Deus quando nos casamos com mulheres estrangeiras procedentes dos povos vizinhos. Mas, apesar disso, ainda há esperança para Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12256, 'nvi', 'Esdras', 'ed', 10, 3, 'Façamos agora um acordo diante do nosso Deus, e mandemos de volta todas essas mulheres e seus filhos, segundo o conselho do meu senhor e daqueles que tremem diante dos mandamentos de nosso Deus. Que isso seja feito em conformidade com a Lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12257, 'nvi', 'Esdras', 'ed', 10, 4, 'Levante-se! Esta questão está em suas mãos, mas nós o apoiaremos. Tenha coragem e mãos à obra! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12258, 'nvi', 'Esdras', 'ed', 10, 5, 'Esdras levantou-se e fez os sacerdotes principais e os levitas e todo o Israel jurarem que fariam o que fora sugerido. E eles juraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12259, 'nvi', 'Esdras', 'ed', 10, 6, 'Então Esdras retirou-se de diante do templo de Deus e foi para o quarto de Joanã, filho de Eliasibe. Enquanto esteve ali, não comeu nem bebeu nada, lamentando a infidelidade dos exilados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12260, 'nvi', 'Esdras', 'ed', 10, 7, 'Fez-se então uma proclamação em todo o Judá e em Jerusalém convocando todos os exilados a se reunirem em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12261, 'nvi', 'Esdras', 'ed', 10, 8, 'Os líderes e as demais autoridades tinham decidido que aquele que não viesse no prazo de três dias perderia todos os seus bens e seria excluído da comunidade dos exilados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12262, 'nvi', 'Esdras', 'ed', 10, 9, 'No prazo de três dias, todos os homens de Judá e de Benjamim tinham se reunido em Jerusalém, e no vigésimo dia do nono mês todo o povo estava sentado na praça que ficava diante do templo de Deus. Todos estavam profundamente abatidos por causa do motivo da reunião e também porque chovia muito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12263, 'nvi', 'Esdras', 'ed', 10, 10, 'Então o sacerdote Esdras levantou-se e disse-lhes: "Vocês têm sido infiéis! Vocês se casaram com mulheres estrangeiras, aumentando a culpa de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12264, 'nvi', 'Esdras', 'ed', 10, 11, 'Agora confessem ao Senhor, o Deus dos seus antepassados, e façam a vontade dele. Separem-se dos povos vizinhos e das suas mulheres estrangeiras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12265, 'nvi', 'Esdras', 'ed', 10, 12, 'A comunidade toda respondeu em voz alta: "Você está certo! Devemos fazer o que você diz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12266, 'nvi', 'Esdras', 'ed', 10, 13, 'Mas há muita gente aqui, e esta é a estação das chuvas; por isso não podemos ficar do lado de fora. Além disso, essa questão não pode ser resolvida em um dia ou dois, porquanto foram muitos os que assim pecaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12267, 'nvi', 'Esdras', 'ed', 10, 14, 'Que os nossos líderes decidam por toda a assembléia. Então que cada um de nossas cidades que se casou com mulher estrangeira venha numa data marcada, acompanhado dos líderes e juízes de cada cidade, para que se afaste de nós o furor da ira de nosso Deus por causa deste pecado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12268, 'nvi', 'Esdras', 'ed', 10, 15, 'Somente Jônatas, filho de Asael, e Jaseías, filho de Ticvá, apoiados por Mesulão e o levita Sabetai, discordaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12269, 'nvi', 'Esdras', 'ed', 10, 16, 'E assim os exilados fizeram conforme proposto. O sacerdote Esdras escolheu chefes de famílias, um de cada grupo de famílias, todos eles chamados por nome. E no dia primeiro do décimo mês eles se assentaram para investigar cada caso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12270, 'nvi', 'Esdras', 'ed', 10, 17, 'No dia primeiro do primeiro mês terminaram de investigar todos os casos de casamento com mulheres estrangeiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12271, 'nvi', 'Esdras', 'ed', 10, 18, 'Entre os descendentes dos sacerdotes, estes foram os que se casaram com mulheres estrangeiras: Dentre os descendentes de Jesua, filho de Jozadaque, e seus irmãos Maaséias, Eliézer, Jaribe e Gedalias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12272, 'nvi', 'Esdras', 'ed', 10, 19, 'Todos eles apertaram as mãos em sinal de garantia que iam despedir suas mulheres, e cada um apresentou um carneiro do rebanho como oferta por sua culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12273, 'nvi', 'Esdras', 'ed', 10, 20, 'Dentre os descendentes de Imer: Hanani e Zebadias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12274, 'nvi', 'Esdras', 'ed', 10, 21, 'Dentre os descendentes de Harim: Maaséias, Elias, Semaías, Jeiel e Uzias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12275, 'nvi', 'Esdras', 'ed', 10, 22, 'Dentre os descendentes de Pasur: Elioenai, Maaséias, Ismael, Natanael, Jozabade e Eleasa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12276, 'nvi', 'Esdras', 'ed', 10, 23, 'Dentre os levitas: Jozabade, Simei, Quelaías, também chamado Quelita, Petaías, Judá e Eliézer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12277, 'nvi', 'Esdras', 'ed', 10, 24, 'Dentre os cantores: Eliasibe. Dentre os porteiros: Salum, Telém e Uri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12278, 'nvi', 'Esdras', 'ed', 10, 25, 'E dentre os outros israelitas: Dentre os descendentes de Parós: Ramias, Jezias, Malquias, Miamim, Eleazar, Malquias e Benaia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12279, 'nvi', 'Esdras', 'ed', 10, 26, 'Dentre os descendentes de Elão: Matanias, Zacarias, Jeiel, Abdi, Jeremote e Elias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12280, 'nvi', 'Esdras', 'ed', 10, 27, 'Dentre os descendentes de Zatu: Elioenai, Eliasibe, Matanias, Jeremote, Zabade e Aziza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12281, 'nvi', 'Esdras', 'ed', 10, 28, 'Dentre os descendentes de Bebai: Joanã, Hananias, Zabai e Atlai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12282, 'nvi', 'Esdras', 'ed', 10, 29, 'Dentre os descendentes de Bani: Mesulão, Maluque, Adaías, Jasube, Seal e Jeremote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12283, 'nvi', 'Esdras', 'ed', 10, 30, 'Dentre os descendentes de Paate-Moabe: Adna, Quelal, Benaia, Maaséias, Matanias, Bezalel, Binui e Manassés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12284, 'nvi', 'Esdras', 'ed', 10, 31, 'Dentre os descendentes de Harim: Eliézer, Issias, Malquias, Semaías, Simeão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12285, 'nvi', 'Esdras', 'ed', 10, 32, 'Benjamim, Maluque e Semarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12286, 'nvi', 'Esdras', 'ed', 10, 33, 'Dentre os descendentes de Hasum: Matenai, Matatá, Zabade, Elifelete, Jeremai, Manassés e Simei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12287, 'nvi', 'Esdras', 'ed', 10, 34, 'Dentre os descendentes de Bani: Maadai, Anrão, Uel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12288, 'nvi', 'Esdras', 'ed', 10, 35, 'Benaia, Bedias, Queluí,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12289, 'nvi', 'Esdras', 'ed', 10, 36, 'Vanias, Meremote, Eliasibe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12290, 'nvi', 'Esdras', 'ed', 10, 37, 'Matanias, Matenai e Jaasai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12291, 'nvi', 'Esdras', 'ed', 10, 38, 'Dentre os descendentes de Binui: Simei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12292, 'nvi', 'Esdras', 'ed', 10, 39, 'Selemias, Natã, Adaías,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12293, 'nvi', 'Esdras', 'ed', 10, 40, 'Macnadbai, Sasai, Sarai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12294, 'nvi', 'Esdras', 'ed', 10, 41, 'Azareel, Selemias, Semarias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12295, 'nvi', 'Esdras', 'ed', 10, 42, 'Salum, Amarias e José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12296, 'nvi', 'Esdras', 'ed', 10, 43, 'Dentre os descendentes de Nebo: Jeiel, Matitias, Zabade, Zebina, Jadai, Joel e Benaia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12297, 'nvi', 'Esdras', 'ed', 10, 44, 'Todos esses tinham se casado com mulheres estrangeiras, e alguns deles tiveram filhos dessas mulheres.');
