INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12087, 'nvi', 'Esdras', 'ed', 2, 59, 'Os que chegaram das cidades de Tel-Melá, Tel-Harsa, Querube, Adã e Imer, mas não puderam comprovar que suas famílias descendiam de Israel, foram os seguintes:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12088, 'nvi', 'Esdras', 'ed', 2, 60, 'Os descendentes de Delaías, Tobias e Necoda 652');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12089, 'nvi', 'Esdras', 'ed', 2, 61, 'E dentre os sacerdotes: Os descendentes de Habaías, Coz e Barzilai, homem que se casou com uma filha de Barzilai, de Gileade, e que era chamado pelo nome do sogro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12090, 'nvi', 'Esdras', 'ed', 2, 62, 'Eles procuraram pelos seus registros de família, mas não conseguiram achá-los e foram considerados impuros para o sacerdócio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12091, 'nvi', 'Esdras', 'ed', 2, 63, 'Por isso o governador os proibiu de comer alimentos sagrados enquanto não houvesse um sacerdote capaz de consultar a Deus por meio do Urim e do Tumim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12092, 'nvi', 'Esdras', 'ed', 2, 64, 'A totalidade dos que voltaram do exílio atingiu o número de 42. 360 homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12093, 'nvi', 'Esdras', 'ed', 2, 65, 'além dos seus 7. 337 servos e servas; havia entre eles 200 cantores e cantoras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12094, 'nvi', 'Esdras', 'ed', 2, 66, 'Possuíam 736 cavalos, 245 mulas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12095, 'nvi', 'Esdras', 'ed', 2, 67, '435 camelos e 6. 720 jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12096, 'nvi', 'Esdras', 'ed', 2, 68, 'Quando chegaram ao templo do Senhor em Jerusalém, alguns dos chefes das famílias deram ofertas voluntárias para a reconstrução do templo de Deus no seu antigo local.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12097, 'nvi', 'Esdras', 'ed', 2, 69, 'De acordo com as suas possibilidades, deram à tesouraria para essa obra quinhentos quilos de ouro, três toneladas de prata e cem vestes sacerdotais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12098, 'nvi', 'Esdras', 'ed', 2, 70, 'Os sacerdotes, os levitas, os cantores, os porteiros e os servidores do templo, bem como os demais israelitas, estabeleceram-se em suas cidades de origem.');
