INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12175, 'nvi', 'Esdras', 'ed', 7, 1, 'Depois dessas coisas, no reinado de Artaxerxes, rei da Pérsia, vivia Esdras, filho de Seraías, filho de Azarias, filho de Hilquias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12176, 'nvi', 'Esdras', 'ed', 7, 2, 'filho de Salum, filho de Zadoque, filho de Aitube,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12177, 'nvi', 'Esdras', 'ed', 7, 3, 'filho de Amarias, filho de Azarias, filho de Meraiote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12178, 'nvi', 'Esdras', 'ed', 7, 4, 'filho de Zeraías, filho de Uzi, filho de Buqui,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12179, 'nvi', 'Esdras', 'ed', 7, 5, 'filho de Abisua, filho de Finéias, filho de Eleazar, filho do sumo sacerdote Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12180, 'nvi', 'Esdras', 'ed', 7, 6, 'Este Esdras veio da Babilônia. Ele era um escriba que conhecia muito a Lei de Moisés dada pelo Senhor, o Deus de Israel. O rei lhe concedera tudo o que ele tinha pedido, pois a mão do Senhor, o seu Deus, estava sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12181, 'nvi', 'Esdras', 'ed', 7, 7, 'Alguns dos israelitas, inclusive sacerdotes, levitas, cantores, porteiros e servidores do templo, também foram para Jerusalém no sétimo ano do reinado de Artaxerxes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12182, 'nvi', 'Esdras', 'ed', 7, 8, 'Esdras chegou a Jerusalém no quinto mês do sétimo ano desse reinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12183, 'nvi', 'Esdras', 'ed', 7, 9, 'No dia primeiro do primeiro mês ele saiu da Babilônia, e chegou a Jerusalém no primeiro dia do quinto mês, porquanto a boa mão de seu Deus estava sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12184, 'nvi', 'Esdras', 'ed', 7, 10, 'Pois Esdras tinha decidido dedicar-se a estudar a Lei do Senhor e a praticá-la, e a ensinar os seus decretos e mandamentos aos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12185, 'nvi', 'Esdras', 'ed', 7, 11, 'Temos aqui uma cópia da carta que o rei Artaxerxes entregou ao sacerdote e escriba Esdras, conhecedor dos mandamentos e decretos do Senhor para Israel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12186, 'nvi', 'Esdras', 'ed', 7, 12, '"Artaxerxes, rei dos reis, "Ao sacerdote Esdras, escriba da Lei do Deus dos céus: "Paz e prosperidade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12187, 'nvi', 'Esdras', 'ed', 7, 13, '"Estou decretando que qualquer israelita em meu reino, inclusive sacerdotes e levitas, que desejar ir a Jerusalém com você, poderá fazê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12188, 'nvi', 'Esdras', 'ed', 7, 14, 'Você está sendo enviado pelo rei e por seus sete conselheiros para fazer uma investigação em Judá e em Jerusalém com respeito à Lei do seu Deus, que está nas suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12189, 'nvi', 'Esdras', 'ed', 7, 15, 'Além disso, você levará a prata e o ouro que o rei e seus conselheiros voluntariamente ofereceram ao Deus de Israel, cuja habitação está em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12190, 'nvi', 'Esdras', 'ed', 7, 16, 'juntamente com toda a prata e ouro que você receber da província da Babilônia, bem como as ofertas voluntárias do povo e dos sacerdotes para o templo do Deus deles em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12191, 'nvi', 'Esdras', 'ed', 7, 17, 'Com esse dinheiro compre novilhos, carneiros e cordeiros, como também o que for necessário para as suas ofertas de cereal e de bebida, e sacrifique-os no altar do templo do seu Deus em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12192, 'nvi', 'Esdras', 'ed', 7, 18, '"Você e seus irmãos poderão fazer o que acharem melhor com o restante da prata e do ouro, de acordo com a vontade do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12193, 'nvi', 'Esdras', 'ed', 7, 19, 'Entregue ao Deus de Jerusalém todos os utensílios que foram confiados a você para o culto no templo de seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12194, 'nvi', 'Esdras', 'ed', 7, 20, 'E todas as demais despesas necessárias com relação ao templo de seu Deus serão pagas pelo tesouro real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12195, 'nvi', 'Esdras', 'ed', 7, 21, '"Agora eu, o rei Artaxerxes, ordeno a todos os tesoureiros do território a oeste do Eufrates que forneçam tudo o que o sacerdote Esdras, escriba da Lei do Deus dos céus, solicitar a vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12196, 'nvi', 'Esdras', 'ed', 7, 22, 'até três toneladas e meia de prata, cem tonéis de trigo, dez barris de vinho, dez barris de azeite de oliva, e sal à vontade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12197, 'nvi', 'Esdras', 'ed', 7, 23, 'Tudo o que o Deus dos céus tenha prescrito, que se faça com presteza para o templo do Deus dos céus, para que a sua ira não venha contra o império do rei e dos seus descendentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12198, 'nvi', 'Esdras', 'ed', 7, 24, 'Saibam também que vocês não têm autoridade para exigir impostos, tributos ou taxas de nenhum sacerdote, levita, cantor, porteiro, servidor do templo e de todos quantos trabalham neste templo de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12199, 'nvi', 'Esdras', 'ed', 7, 25, '"E você, Esdras, com a sabedoria que o seu Deus lhe deu, nomeie magistrados e juízes para ministrarem justiça a todo o povo do território a oeste do Eufrates, a todos os que conhecem as leis do seu Deus. E aos que não as conhecem, você deverá ensiná-las.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12200, 'nvi', 'Esdras', 'ed', 7, 26, 'Aquele que não obedecer à lei do Deus de vocês e à lei do rei seja punido com a morte, ou com o exílio, ou com o confisco de bens ou com a prisão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12201, 'nvi', 'Esdras', 'ed', 7, 27, 'Bendito seja o Senhor, o Deus de nossos antepassados, que pôs no coração do rei o propósito de honrar desta maneira o templo do Senhor em Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12202, 'nvi', 'Esdras', 'ed', 7, 28, 'e que, por sua bondade, levou o rei, os seus conselheiros e todos os seus altos oficiais. Como a mão do Senhor meu Deus esteve sobre mim, tomei coragem e reuni alguns líderes de Israel para me acompanharem.');
