INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29208, 'nvi', 'Efésios', 'ef', 1, 1, 'Paulo, apóstolo de Cristo Jesus pela vontade de Deus, aos santos e fiéis em Cristo Jesus que estão em Éfeso:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29209, 'nvi', 'Efésios', 'ef', 1, 2, 'A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29210, 'nvi', 'Efésios', 'ef', 1, 3, 'Bendito seja o Deus e Pai de nosso Senhor Jesus Cristo, que nos abençoou com todas as bênçãos espirituais nas regiões celestiais em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29211, 'nvi', 'Efésios', 'ef', 1, 4, 'Porque Deus nos escolheu nele antes da criação do mundo, para sermos santos e irrepreensíveis em sua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29212, 'nvi', 'Efésios', 'ef', 1, 5, 'Em amor nos predestinou para sermos adotados como filhos por meio de Jesus Cristo, conforme o bom propósito da sua vontade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29213, 'nvi', 'Efésios', 'ef', 1, 6, 'para o louvor da sua gloriosa graça, a qual nos deu gratuitamente no Amado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29214, 'nvi', 'Efésios', 'ef', 1, 7, 'Nele temos a redenção por meio de seu sangue, o perdão dos pecados, de acordo com as riquezas da graça de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29215, 'nvi', 'Efésios', 'ef', 1, 8, 'a qual ele derramou sobre nós com toda a sabedoria e entendimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29216, 'nvi', 'Efésios', 'ef', 1, 9, 'E nos revelou o mistério da sua vontade, de acordo com o seu bom propósito que ele estabeleceu em Cristo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29217, 'nvi', 'Efésios', 'ef', 1, 10, 'isto é, de fazer convergir em Cristo todas as coisas, celestiais ou terrenas, na dispensação da plenitude dos tempos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29218, 'nvi', 'Efésios', 'ef', 1, 11, 'Nele fomos também escolhidos, tendo sido predestinados conforme o plano daquele que faz todas as coisas segundo o propósito da sua vontade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29219, 'nvi', 'Efésios', 'ef', 1, 12, 'a fim de que nós, os que primeiro esperamos em Cristo, sejamos para o louvor da sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29220, 'nvi', 'Efésios', 'ef', 1, 13, 'Nele, quando vocês ouviram e creram na palavra da verdade, o evangelho que os salvou, vocês foram selados com o Espírito Santo da promessa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29221, 'nvi', 'Efésios', 'ef', 1, 14, 'que é a garantia da nossa herança até a redenção daqueles que pertencem a Deus, para o louvor da sua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29222, 'nvi', 'Efésios', 'ef', 1, 15, 'Por essa razão, desde que ouvi falar da fé que vocês têm no Senhor Jesus e do amor que demonstram para com todos os santos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29223, 'nvi', 'Efésios', 'ef', 1, 16, 'não deixo de dar graças por vocês, mencionando-os em minhas orações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29224, 'nvi', 'Efésios', 'ef', 1, 17, 'Peço que o Deus de nosso Senhor Jesus Cristo, o glorioso Pai, lhes dê espírito de sabedoria e de revelação, no pleno conhecimento dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29225, 'nvi', 'Efésios', 'ef', 1, 18, 'Oro também para que os olhos do coração de vocês sejam iluminados, a fim de que vocês conheçam a esperança para a qual ele os chamou, as riquezas da gloriosa herança dele nos santos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29226, 'nvi', 'Efésios', 'ef', 1, 19, 'e a incomparável grandeza do seu poder para conosco, os que cremos, conforme a atuação da sua poderosa força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29227, 'nvi', 'Efésios', 'ef', 1, 20, 'Esse poder ele exerceu em Cristo, ressuscitando-o dos mortos e fazendo-o assentar-se à sua direita, nas regiões celestiais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29228, 'nvi', 'Efésios', 'ef', 1, 21, 'muito acima de todo governo e autoridade, poder e domínio, e de todo nome que se possa mencionar, não apenas nesta era, mas também na que há de vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29229, 'nvi', 'Efésios', 'ef', 1, 22, 'Deus colocou todas as coisas debaixo de seus pés e o designou como cabeça de todas as coisas para a igreja,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29230, 'nvi', 'Efésios', 'ef', 1, 23, 'que é o seu corpo, a plenitude daquele que enche todas as coisas, em toda e qualquer circunstância.');
