INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29231, 'nvi', 'Efésios', 'ef', 2, 1, 'Vocês estavam mortos em suas transgressões e pecados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29232, 'nvi', 'Efésios', 'ef', 2, 2, 'nos quais costumavam viver, quando seguiam a presente ordem deste mundo e o príncipe do poder do ar, o espírito que agora está atuando nos que vivem na desobediência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29233, 'nvi', 'Efésios', 'ef', 2, 3, 'Anteriormente, todos nós também vivíamos entre eles, satisfazendo as vontades da nossa carne, seguindo os seus desejos e pensamentos. Como os outros, éramos por natureza merecedores da ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29234, 'nvi', 'Efésios', 'ef', 2, 4, 'Todavia, Deus, que é rico em misericórdia, pelo grande amor com que nos amou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29235, 'nvi', 'Efésios', 'ef', 2, 5, 'deu-nos vida juntamente com Cristo, quando ainda estávamos mortos em transgressões — pela graça vocês são salvos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29236, 'nvi', 'Efésios', 'ef', 2, 6, 'Deus nos ressuscitou com Cristo e com ele nos fez assentar nos lugares celestiais em Cristo Jesus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29237, 'nvi', 'Efésios', 'ef', 2, 7, 'para mostrar, nas eras que hão de vir, a incomparável riqueza de sua graça, demonstrada em sua bondade para conosco em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29238, 'nvi', 'Efésios', 'ef', 2, 8, 'Pois vocês são salvos pela graça, por meio da fé, e isto não vem de vocês, é dom de Deus;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29239, 'nvi', 'Efésios', 'ef', 2, 9, 'não por obras, para que ninguém se glorie.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29240, 'nvi', 'Efésios', 'ef', 2, 10, 'Porque somos criação de Deus realizada em Cristo Jesus para fazermos boas obras, as quais Deus preparou de antemão para que nós as praticássemos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29241, 'nvi', 'Efésios', 'ef', 2, 11, 'Portanto, lembrem-se de que anteriormente vocês eram gentios por nascimento e chamados incircuncisão pelos que se chamam circuncisão, feita no corpo por mãos humanas, e que');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29242, 'nvi', 'Efésios', 'ef', 2, 12, 'naquela época vocês estavam sem Cristo, separados da comunidade de Israel, sendo estrangeiros quanto às alianças da promessa, sem esperança e sem Deus no mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29243, 'nvi', 'Efésios', 'ef', 2, 13, 'Mas agora, em Cristo Jesus, vocês, que antes estavam longe, foram aproximados mediante o sangue de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29244, 'nvi', 'Efésios', 'ef', 2, 14, 'Pois ele é a nossa paz, o qual de ambos fez um e destruiu a barreira, o muro de inimizade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29245, 'nvi', 'Efésios', 'ef', 2, 15, 'anulando em seu corpo a lei dos mandamentos expressa em ordenanças. O objetivo dele era criar em si mesmo, dos dois, um novo homem, fazendo a paz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29246, 'nvi', 'Efésios', 'ef', 2, 16, 'e reconciliar com Deus os dois em um corpo, por meio da cruz, pela qual ele destruiu a inimizade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29247, 'nvi', 'Efésios', 'ef', 2, 17, 'Ele veio e anunciou paz a vocês que estavam longe e paz aos que estavam perto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29248, 'nvi', 'Efésios', 'ef', 2, 18, 'pois por meio dele tanto nós como vocês temos acesso ao Pai, por um só Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29249, 'nvi', 'Efésios', 'ef', 2, 19, 'Portanto, vocês já não são estrangeiros nem forasteiros, mas concidadãos dos santos e membros da família de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29250, 'nvi', 'Efésios', 'ef', 2, 20, 'edificados sobre o fundamento dos apóstolos e dos profetas, tendo Jesus Cristo como pedra angular,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29251, 'nvi', 'Efésios', 'ef', 2, 21, 'no qual todo o edifício é ajustado e cresce para tornar-se um santuário santo no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29252, 'nvi', 'Efésios', 'ef', 2, 22, 'Nele vocês também estão sendo juntamente edificados, para se tornarem morada de Deus por seu Espírito.');
