INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29253, 'nvi', 'Efésios', 'ef', 3, 1, 'Por essa razão, eu, Paulo, prisioneiro de Cristo Jesus por amor de vocês, gentios —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29254, 'nvi', 'Efésios', 'ef', 3, 2, 'Certamente vocês ouviram falar da responsabilidade imposta a mim em favor de vocês pela graça de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29255, 'nvi', 'Efésios', 'ef', 3, 3, 'isto é, o mistério que me foi dado a conhecer por revelação, como já lhes escrevi brevemente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29256, 'nvi', 'Efésios', 'ef', 3, 4, 'Ao lerem isso vocês poderão entender a minha compreensão do mistério de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29257, 'nvi', 'Efésios', 'ef', 3, 5, 'Esse mistério não foi dado a conhecer aos homens doutras gerações, mas agora foi revelado pelo Espírito aos santos apóstolos e profetas de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29258, 'nvi', 'Efésios', 'ef', 3, 6, 'a saber, que mediante o evangelho os gentios são co-herdeiros com Israel, membros do mesmo corpo, e co-participantes da promessa em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29259, 'nvi', 'Efésios', 'ef', 3, 7, 'Deste me tornei ministro pelo dom da graça de Deus, a mim concedida pela operação de seu poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29260, 'nvi', 'Efésios', 'ef', 3, 8, 'Embora eu seja o menor dos menores dentre todos os santos, foi-me concedida esta graça de anunciar aos gentios as insondáveis riquezas de Cristo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29261, 'nvi', 'Efésios', 'ef', 3, 9, 'e esclarecer a todos a administração deste mistério que, durante as épocas passadas, foi mantido oculto em Deus, que criou todas as coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29262, 'nvi', 'Efésios', 'ef', 3, 10, 'A intenção dessa graça era que agora, mediante a igreja, a multiforme sabedoria de Deus se tornasse conhecida dos poderes e autoridades nas regiões celestiais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29263, 'nvi', 'Efésios', 'ef', 3, 11, 'de acordo com o seu eterno plano que ele realizou em Cristo Jesus, nosso Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29264, 'nvi', 'Efésios', 'ef', 3, 12, 'por intermédio de quem temos livre acesso a Deus em confiança, pela fé nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29265, 'nvi', 'Efésios', 'ef', 3, 13, 'Portanto, peço-lhes que não se desanimem por causa das minhas tribulações em seu favor, pois elas são uma glória para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29266, 'nvi', 'Efésios', 'ef', 3, 14, 'Por essa razão, ajoelho-me diante do Pai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29267, 'nvi', 'Efésios', 'ef', 3, 15, 'do qual recebe o nome toda a família nos céus e na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29268, 'nvi', 'Efésios', 'ef', 3, 16, 'Oro para que, com as suas gloriosas riquezas, ele os fortaleça no íntimo do seu ser com poder, por meio do seu Espírito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29269, 'nvi', 'Efésios', 'ef', 3, 17, 'para que Cristo habite em seus corações mediante a fé; e oro para que vocês, arraigados e alicerçados em amor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29270, 'nvi', 'Efésios', 'ef', 3, 18, 'possam, juntamente com todos os santos, compreender a largura, o comprimento, a altura e a profundidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29271, 'nvi', 'Efésios', 'ef', 3, 19, 'e conhecer o amor de Cristo que excede todo conhecimento, para que vocês sejam cheios de toda a plenitude de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29272, 'nvi', 'Efésios', 'ef', 3, 20, 'Àquele que é capaz de fazer infinitamente mais do que tudo o que pedimos ou pensamos, de acordo com o seu poder que atua em nós,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29273, 'nvi', 'Efésios', 'ef', 3, 21, 'a ele seja a glória na igreja e em Cristo Jesus, por todas as gerações, para todo o sempre! Amém!');
