INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29274, 'nvi', 'Efésios', 'ef', 4, 1, 'Como prisioneiro no Senhor, rogo-lhes que vivam de maneira digna da vocação que receberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29275, 'nvi', 'Efésios', 'ef', 4, 2, 'Sejam completamente humildes e dóceis, e sejam pacientes, suportando uns aos outros com amor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29276, 'nvi', 'Efésios', 'ef', 4, 3, 'Façam todo o esforço para conservar a unidade do Espírito pelo vínculo da paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29277, 'nvi', 'Efésios', 'ef', 4, 4, 'Há um só corpo e um só Espírito, assim como a esperança para a qual vocês foram chamados é uma só;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29278, 'nvi', 'Efésios', 'ef', 4, 5, 'há um só Senhor, uma só fé, um só batismo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29279, 'nvi', 'Efésios', 'ef', 4, 6, 'um só Deus e Pai de todos, que é sobre todos, por meio de todos e em todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29280, 'nvi', 'Efésios', 'ef', 4, 7, 'E a cada um de nós foi concedida a graça, conforme a medida repartida por Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29281, 'nvi', 'Efésios', 'ef', 4, 8, 'Por isso é que foi dito: "Quando ele subiu em triunfo às alturas, levou cativo muitos prisioneiros, e deu dons aos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29282, 'nvi', 'Efésios', 'ef', 4, 9, '( Que significa "ele subiu", senão que também descera às profundezas da terra?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29283, 'nvi', 'Efésios', 'ef', 4, 10, 'Aquele que desceu é o mesmo que subiu acima de todos os céus, a fim de encher todas as coisas. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29284, 'nvi', 'Efésios', 'ef', 4, 11, 'E ele designou alguns para apóstolos, outros para profetas, outros para evangelistas, e outros para pastores e mestres,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29285, 'nvi', 'Efésios', 'ef', 4, 12, 'com o fim de preparar os santos para a obra do ministério, para que o corpo de Cristo seja edificado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29286, 'nvi', 'Efésios', 'ef', 4, 13, 'até que todos alcancemos a unidade da fé e do conhecimento do Filho de Deus, e cheguemos à maturidade, atingindo a medida da plenitude de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29287, 'nvi', 'Efésios', 'ef', 4, 14, 'O propósito é que não sejamos mais como crianças, levados de um lado para outro pelas ondas, nem jogados para cá e para lá por todo vento de doutrina e pela astúcia e esperteza de homens que induzem ao erro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29288, 'nvi', 'Efésios', 'ef', 4, 15, 'Antes, seguindo a verdade em amor, cresçamos em tudo naquele que é a cabeça, Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29289, 'nvi', 'Efésios', 'ef', 4, 16, 'Dele todo o corpo, ajustado e unido pelo auxílio de todas as juntas, cresce e edifica-se a si mesmo em amor, na medida em que cada parte realiza a sua função.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29290, 'nvi', 'Efésios', 'ef', 4, 17, 'Assim, eu lhes digo, e no Senhor insisto, que não vivam mais como os gentios, que vivem na futilidade dos seus pensamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29291, 'nvi', 'Efésios', 'ef', 4, 18, 'Eles estão obscurecidos no entendimento e separados da vida de Deus por causa da ignorância em que estão, devido ao endurecimento dos seus corações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29292, 'nvi', 'Efésios', 'ef', 4, 19, 'Tendo perdido toda a sensibilidade, ele se entregaram à depravação, cometendo com avidez toda espécie de impureza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29293, 'nvi', 'Efésios', 'ef', 4, 20, 'Todavia, não foi assim que vocês aprenderam de Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29294, 'nvi', 'Efésios', 'ef', 4, 21, 'De fato, vocês ouviram falar dele, e nele foram ensinados de acordo com a verdade que está em Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29295, 'nvi', 'Efésios', 'ef', 4, 22, 'Quanto à antiga maneira de viver, vocês foram ensinados a despir-se do velho homem, que se corrompe por desejos enganosos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29296, 'nvi', 'Efésios', 'ef', 4, 23, 'a serem renovados no modo de pensar e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29297, 'nvi', 'Efésios', 'ef', 4, 24, 'a revestir-se do novo homem, criado para ser semelhante a Deus em justiça e em santidade provenientes da verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29298, 'nvi', 'Efésios', 'ef', 4, 25, 'Portanto, cada um de vocês deve abandonar a mentira e falar a verdade ao seu próximo, pois todos somos membros de um mesmo corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29299, 'nvi', 'Efésios', 'ef', 4, 26, '"Quando vocês ficarem irados, não pequem". Apazigüem a sua ira antes que o sol se ponha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29300, 'nvi', 'Efésios', 'ef', 4, 27, 'e não dêem lugar ao diabo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29301, 'nvi', 'Efésios', 'ef', 4, 28, 'O que furtava não furte mais; antes trabalhe, fazendo algo de útil com as mãos, para que tenha o que repartir com quem estiver em necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29302, 'nvi', 'Efésios', 'ef', 4, 29, 'Nenhuma palavra torpe saia da boca de vocês, mas apenas a que for útil para edificar os outros, conforme a necessidade, para que conceda graça aos que a ouvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29303, 'nvi', 'Efésios', 'ef', 4, 30, 'Não entristeçam o Espírito Santo de Deus, com o qual vocês foram selados para o dia da redenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29304, 'nvi', 'Efésios', 'ef', 4, 31, 'Livrem-se de toda amargura, indignação e ira, gritaria e calúnia, bem como de toda maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29305, 'nvi', 'Efésios', 'ef', 4, 32, 'Sejam bondosos e compassivos uns para com os outros, perdoando-se mutuamente, assim como Deus perdoou vocês em Cristo.');
