INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29306, 'nvi', 'Efésios', 'ef', 5, 1, 'Portanto, sejam imitadores de Deus, como filhos amados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29307, 'nvi', 'Efésios', 'ef', 5, 2, 'e vivam em amor, como também Cristo nos amou e se entregou por nós como oferta e sacrifício de aroma agradável a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29308, 'nvi', 'Efésios', 'ef', 5, 3, 'Entre vocês não deve haver nem sequer menção de imoralidade sexual nem de qualquer espécie de impureza nem de cobiça; pois estas coisas não são próprias para os santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29309, 'nvi', 'Efésios', 'ef', 5, 4, 'Não haja obscenidade nem conversas tolas nem gracejos imorais, que são inconvenientes, mas, ao invés disso, ação de graças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29310, 'nvi', 'Efésios', 'ef', 5, 5, 'Porque vocês podem estar certos disto: nenhum imoral nem impuro nem ganancioso, que é idólatra, tem herança no Reino de Cristo e de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29311, 'nvi', 'Efésios', 'ef', 5, 6, 'Ninguém os engane com palavras tolas, pois é por causa dessas coisas que a ira de Deus vem sobre os que vivem na desobediência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29312, 'nvi', 'Efésios', 'ef', 5, 7, 'Portanto, não participem com eles dessas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29313, 'nvi', 'Efésios', 'ef', 5, 8, 'Porque outrora vocês eram trevas, mas agora são luz no Senhor. Vivam como filhos da luz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29314, 'nvi', 'Efésios', 'ef', 5, 9, 'pois o fruto da luz consiste em toda bondade, justiça e verdade;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29315, 'nvi', 'Efésios', 'ef', 5, 10, 'e aprendam a discernir o que é agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29316, 'nvi', 'Efésios', 'ef', 5, 11, 'Não participem das obras infrutíferas das trevas; antes, exponham-nas à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29317, 'nvi', 'Efésios', 'ef', 5, 12, 'Porque aquilo que eles fazem em oculto, até mencionar é vergonhoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29318, 'nvi', 'Efésios', 'ef', 5, 13, 'Mas, tudo o que é exposto pela luz torna-se visível, pois a luz torna visíveis todas as coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29319, 'nvi', 'Efésios', 'ef', 5, 14, 'Por isso é que foi dito: "Desperta, ó tu que dormes, levanta-te dentre os mortos e Cristo resplandecerá sobre ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29320, 'nvi', 'Efésios', 'ef', 5, 15, 'Tenham cuidado com a maneira como vocês vivem; que não seja como insensatos, mas como sábios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29321, 'nvi', 'Efésios', 'ef', 5, 16, 'aproveitando ao máximo cada oportunidade, porque os dias são maus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29322, 'nvi', 'Efésios', 'ef', 5, 17, 'Portanto, não sejam insensatos, mas procurem compreender qual é a vontade do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29323, 'nvi', 'Efésios', 'ef', 5, 18, 'Não se embriaguem com vinho, que leva à libertinagem, mas deixem-se encher pelo Espírito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29324, 'nvi', 'Efésios', 'ef', 5, 19, 'falando entre si com salmos, hinos e cânticos espirituais, cantando e louvando de coração ao Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29325, 'nvi', 'Efésios', 'ef', 5, 20, 'dando graças constantemente a Deus Pai por todas as coisas, em nome de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29326, 'nvi', 'Efésios', 'ef', 5, 21, 'Sujeitem-se uns aos outros, por temor a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29327, 'nvi', 'Efésios', 'ef', 5, 22, 'Mulheres, sujeitem-se a seus maridos, como ao Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29328, 'nvi', 'Efésios', 'ef', 5, 23, 'pois o marido é o cabeça da mulher, como também Cristo é o cabeça da igreja, que é o seu corpo, do qual ele é o Salvador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29329, 'nvi', 'Efésios', 'ef', 5, 24, 'Assim como a igreja está sujeita a Cristo, também as mulheres estejam em tudo sujeitas a seus maridos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29330, 'nvi', 'Efésios', 'ef', 5, 25, 'Maridos, amem suas mulheres, assim como Cristo amou a igreja e entregou-se a si mesmo por ela');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29331, 'nvi', 'Efésios', 'ef', 5, 26, 'para santificá-la, tendo-a purificado pelo lavar da água mediante a palavra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29332, 'nvi', 'Efésios', 'ef', 5, 27, 'e apresentá-la a si mesmo como igreja gloriosa, sem mancha nem ruga ou coisa semelhante, mas santa e inculpável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29333, 'nvi', 'Efésios', 'ef', 5, 28, 'Da mesma forma, os maridos devem amar as suas mulheres como a seus próprios corpos. Quem ama sua mulher, ama a si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29334, 'nvi', 'Efésios', 'ef', 5, 29, 'Além do mais, ninguém jamais odiou o seu próprio corpo, antes o alimenta e dele cuida, como também Cristo faz com a igreja,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29335, 'nvi', 'Efésios', 'ef', 5, 30, 'pois somos membros do seu corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29336, 'nvi', 'Efésios', 'ef', 5, 31, '"Por essa razão, o homem deixará pai e mãe e se unirá à sua mulher, e os dois se tornarão uma só carne".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29337, 'nvi', 'Efésios', 'ef', 5, 32, 'Este é um mistério profundo; refiro-me, porém, a Cristo e à igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29338, 'nvi', 'Efésios', 'ef', 5, 33, 'Portanto, cada um de vocês também ame a sua mulher como a si mesmo, e a mulher trate o marido com todo o respeito.');
