INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29339, 'nvi', 'Efésios', 'ef', 6, 1, 'Filhos, obedeçam a seus pais no Senhor, pois isso é justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29340, 'nvi', 'Efésios', 'ef', 6, 2, '"Honra teu pai e tua mãe", este é o primeiro mandamento com promessa:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29341, 'nvi', 'Efésios', 'ef', 6, 3, '"para que tudo te corra bem e tenhas longa vida sobre a terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29342, 'nvi', 'Efésios', 'ef', 6, 4, 'Pais, não irritem seus filhos; antes criem-nos segundo a instrução e o conselho do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29343, 'nvi', 'Efésios', 'ef', 6, 5, 'Escravos, obedeçam a seus senhores terrenos com respeito e temor, com sinceridade de coração, como a Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29344, 'nvi', 'Efésios', 'ef', 6, 6, 'Obedeçam-lhes não apenas para agradá-los quando eles os observam, mas como escravos de Cristo, fazendo de coração a vontade de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29345, 'nvi', 'Efésios', 'ef', 6, 7, 'Sirvam aos seus senhores de boa vontade, como ao Senhor, e não aos homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29346, 'nvi', 'Efésios', 'ef', 6, 8, 'porque vocês sabem que o Senhor recompensará a cada um pelo bem que praticar, seja escravo, seja livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29347, 'nvi', 'Efésios', 'ef', 6, 9, 'Vocês, senhores, tratem seus escravos da mesma forma. Não os ameacem, uma vez que vocês sabem que o Senhor deles e de vocês está nos céus, e ele não faz diferença entre as pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29348, 'nvi', 'Efésios', 'ef', 6, 10, 'Finalmente, fortaleçam-se no Senhor e no seu forte poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29349, 'nvi', 'Efésios', 'ef', 6, 11, 'Vistam toda a armadura de Deus, para poderem ficar firmes contra as ciladas do diabo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29350, 'nvi', 'Efésios', 'ef', 6, 12, 'pois a nossa luta não é contra pessoas, mas contra os poderes e autoridades, contra os dominadores deste mundo de trevas, contra as forças espirituais do mal nas regiões celestiais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29351, 'nvi', 'Efésios', 'ef', 6, 13, 'Por isso, vistam toda a armadura de Deus, para que possam resistir no dia mau e permanecer inabaláveis, depois de terem feito tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29352, 'nvi', 'Efésios', 'ef', 6, 14, 'Assim, mantenham-se firmes, cingindo-se com o cinto da verdade, vestindo a couraça da justiça');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29353, 'nvi', 'Efésios', 'ef', 6, 15, 'e tendo os pés calçados com a prontidão do evangelho da paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29354, 'nvi', 'Efésios', 'ef', 6, 16, 'Além disso, usem o escudo da fé, com o qual vocês poderão apagar todas as setas inflamadas do Maligno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29355, 'nvi', 'Efésios', 'ef', 6, 17, 'Usem o capacete da salvação e a espada do Espírito, que é a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29356, 'nvi', 'Efésios', 'ef', 6, 18, 'Orem no Espírito em todas as ocasiões, com toda oração e súplica; tendo isso em mente, estejam atentos e perseverem na oração por todos os santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29357, 'nvi', 'Efésios', 'ef', 6, 19, 'Orem também por mim, para que, quando eu falar, seja-me dada a mensagem a fim de que, destemidamente, torne conhecido o mistério do evangelho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29358, 'nvi', 'Efésios', 'ef', 6, 20, 'pelo qual sou embaixador preso em correntes. Orem para que, permanecendo nele, eu fale com coragem, como me cumpre fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29359, 'nvi', 'Efésios', 'ef', 6, 21, 'Tíquico, o irmão amado e fiel servo do Senhor, lhes informará tudo, para que vocês também saibam qual é a minha situação e o que estou fazendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29360, 'nvi', 'Efésios', 'ef', 6, 22, 'Enviei-o a vocês por essa mesma razão, para que saibam como estamos e para que ele os encoraje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29361, 'nvi', 'Efésios', 'ef', 6, 23, 'Paz seja com os irmãos, e amor com fé da parte de Deus Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29362, 'nvi', 'Efésios', 'ef', 6, 24, 'A graça seja com todos os que amam a nosso Senhor Jesus Cristo com amor incorruptível.');
