INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12726, 'nvi', 'Ester', 'et', 2, 1, 'Depois disso, quando cessou a indignação do rei Xerxes, ele se lembrou de Vasti, do que ela havia feito e do que ele tinha decretado contra ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12727, 'nvi', 'Ester', 'et', 2, 2, 'Então os conselheiros do rei sugeriram que se procurassem virgens lindas para o rei');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12728, 'nvi', 'Ester', 'et', 2, 3, 'e que se nomeassem comissários em cada província do império para trazer todas essas lindas moças ao harém da cidadela de Susã. Elas estariam sob os cuidados de Hegai, oficial responsável pelo harém; e deveriam receber tratamento de beleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12729, 'nvi', 'Ester', 'et', 2, 4, 'A moça que mais agradasse o rei seria rainha no lugar de Vasti. Esse conselho agradou o rei, e ele o acatou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12730, 'nvi', 'Ester', 'et', 2, 5, 'Ora, na cidadela de Susã havia um judeu chamado Mardoqueu, da tribo de Benjamim, filho de Jair, neto de Simei e bisneto de Quis,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12731, 'nvi', 'Ester', 'et', 2, 6, 'que fora levado de Jerusalém para o exílio por Nabucodonosor, rei da Babilônia, entre os que foram levados prisioneiros com Joaquim, rei de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12732, 'nvi', 'Ester', 'et', 2, 7, 'Mardoqueu tinha uma prima chamada Hadassa, que havia sido criada por ele, por não ter pai nem mãe. Essa moça, também conhecida como Ester, era atraente e muito bonita, e Mardoqueu a havia tomado como filha quando o pai e a mãe dela morreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12733, 'nvi', 'Ester', 'et', 2, 8, 'Quando a ordem e o decreto do rei foram proclamados, muitas moças foram trazidas à cidadela de Susã e colocadas sob os cuidados de Hegai. Ester também foi trazida ao palácio do rei e confiada a Hegai, encarregado do harém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12734, 'nvi', 'Ester', 'et', 2, 9, 'A moça o agradou e ele a favoreceu. Ele logo lhe providenciou tratamento de beleza e comida especial. Designou-lhe sete moças escolhidas do palácio do rei e transferiu-a, junto com suas jovens, para o melhor lugar do harém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12735, 'nvi', 'Ester', 'et', 2, 10, 'Ester não tinha revelado a que povo pertencia nem a origem da sua família, pois Mardoqueu a havia proibido de fazê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12736, 'nvi', 'Ester', 'et', 2, 11, 'Diariamente ele caminhava de um lado para outro perto do pátio do harém, para saber como Ester estava e o que lhe estava acontecendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12737, 'nvi', 'Ester', 'et', 2, 12, 'Antes de qualquer daquelas moças apresentar-se ao rei Xerxes, devia completar doze meses de tratamento de beleza prescritos para as mulheres, seis meses com óleo de mirra e seis meses com perfumes e cosméticos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12738, 'nvi', 'Ester', 'et', 2, 13, 'Quando ia apresentar-se ao rei, a moça recebia tudo o que quisesse levar consigo do harém para o palácio do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12739, 'nvi', 'Ester', 'et', 2, 14, 'À tarde ela ia para lá e de manhã voltava para outra parte do harém, que ficava sob os cuidados de Saasgaz, oficial responsável pelas concubinas. Ela não voltava ao rei, a menos que dela ele se agradasse e a mandasse chamar pelo nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12740, 'nvi', 'Ester', 'et', 2, 15, 'Quando chegou a vez de Ester, a moça adotada por Mardoqueu, filha de seu tio Abiail, ela não pediu nada além daquilo que Hegai, oficial responsável pelo harém, sugeriu. Ester agradava a todos os que a viam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12741, 'nvi', 'Ester', 'et', 2, 16, 'Ela foi levada ao rei Xerxes, à residência real, no décimo mês, o mês de tebete, no sétimo ano do seu reinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12742, 'nvi', 'Ester', 'et', 2, 17, 'Ora, o rei gostou mais de Ester do que de qualquer outra mulher, e ela foi favorecida por ele e ganhou sua aprovação mais do que qualquer das outras virgens. Então ele lhe colocou uma coroa real e tornou-a rainha no lugar de Vasti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12743, 'nvi', 'Ester', 'et', 2, 18, 'E o rei deu um grande banquete, o banquete de Ester, para todos os seus nobres e oficiais. Proclamou feriado em todas as províncias e distribuiu presentes por sua generosidade real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12744, 'nvi', 'Ester', 'et', 2, 19, 'Quando as virgens foram reunidas pela segunda vez, Mardoqueu estava sentado junto à porta do palácio real.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12745, 'nvi', 'Ester', 'et', 2, 20, 'Ester havia mantido segredo sobre seu povo e sobre a origem de sua família, conforme a ordem de Mardoqueu, pois continuava a seguir as instruções de Mardoqueu, como fazia quando ainda estava sob sua tutela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12746, 'nvi', 'Ester', 'et', 2, 21, 'Um dia, quando Mardoqueu estava sentado junto à porta do palácio real, Bigtã e Teres, dois dos oficiais do rei que guardavam a entrada, estavam indignados e conspiravam para assassinar o rei Xerxes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12747, 'nvi', 'Ester', 'et', 2, 22, 'Mardoqueu, porém, descobriu o plano e contou-o à rainha Ester, que, por sua vez, passou a informação ao rei, em nome de Mardoqueu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12748, 'nvi', 'Ester', 'et', 2, 23, 'Depois de investigada a informação e descobrindo-se que era verdadeira, os dois funcionários foram enforcados. Tudo isso foi escrito nos registros históricos, na presença do rei.');
