INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1533, 'nvi', 'Êxodo', 'ex', 1, 1, 'São estes, pois, os nomes dos filhos de Israel que entraram com Jacó no Egito, cada um com a sua respectiva família:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1534, 'nvi', 'Êxodo', 'ex', 1, 2, 'Rúben, Simeão, Levi e Judá;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1535, 'nvi', 'Êxodo', 'ex', 1, 3, 'Issacar, Zebulom e Benjamim;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1536, 'nvi', 'Êxodo', 'ex', 1, 4, 'Dã, Naftali, Gade e Aser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1537, 'nvi', 'Êxodo', 'ex', 1, 5, 'Ao todo, os descendentes de Jacó eram setenta; José, porém, já se encontrava no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1538, 'nvi', 'Êxodo', 'ex', 1, 6, 'Ora, morreram José, todos os seus irmãos e toda aquela geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1539, 'nvi', 'Êxodo', 'ex', 1, 7, 'Os israelitas, porém, eram férteis, proliferaram, tornaram-se numerosos e fortaleceram-se muito, tanto que encheram o país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1540, 'nvi', 'Êxodo', 'ex', 1, 8, 'Então subiu ao trono do Egito um novo rei, que nada sabia sobre José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1541, 'nvi', 'Êxodo', 'ex', 1, 9, 'Disse ele ao seu povo: "Vejam! O povo israelita é agora numeroso e mais forte que nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1542, 'nvi', 'Êxodo', 'ex', 1, 10, 'Temos de agir com astúcia, para que não se tornem ainda mais numerosos e, no caso de guerra, aliem-se aos nossos inimigos, lutem contra nós e fujam do país".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1543, 'nvi', 'Êxodo', 'ex', 1, 11, 'Estabeleceram, pois, sobre eles chefes de trabalhos forçados, para os oprimir com tarefas pesadas. E assim os israelitas construíram para o faraó as cidades-celeiros de Pitom e Ramessés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1544, 'nvi', 'Êxodo', 'ex', 1, 12, 'Todavia, quanto mais eram oprimidos, mais numerosos se tornavam e mais se espalhavam. Por isso os egípcios passaram a temer os israelitas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1545, 'nvi', 'Êxodo', 'ex', 1, 13, 'e os sujeitaram a cruel escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1546, 'nvi', 'Êxodo', 'ex', 1, 14, 'Tornaram-lhes a vida amarga, impondo-lhes a árdua tarefa de preparar o barro e fazer tijolos, e executar todo tipo de trabalho agrícola; em tudo os egípcios os sujeitavam a cruel escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1547, 'nvi', 'Êxodo', 'ex', 1, 15, 'O rei do Egito ordenou às parteiras dos hebreus, que se chamavam Sifrá e Puá:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1548, 'nvi', 'Êxodo', 'ex', 1, 16, '"Quando vocês ajudarem as hebréias a dar à luz, verifiquem se é menino. Se for, matem-no; se for menina, deixem-na viver".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1549, 'nvi', 'Êxodo', 'ex', 1, 17, 'Todavia, as parteiras temeram a Deus e não obedeceram às ordens do rei do Egito; deixaram viver os meninos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1550, 'nvi', 'Êxodo', 'ex', 1, 18, 'Então o rei do Egito convocou as parteiras e lhes perguntou: "Por que vocês fizeram isso? Por que deixaram viver os meninos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1551, 'nvi', 'Êxodo', 'ex', 1, 19, 'Responderam as parteiras do faraó: "As mulheres hebréias não são como as egípcias. São cheias de vigor e dão à luz antes de chegarem as parteiras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1552, 'nvi', 'Êxodo', 'ex', 1, 20, 'Deus foi bondoso com as parteiras; e o povo ia se tornando ainda mais numeroso, cada vez mais forte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1553, 'nvi', 'Êxodo', 'ex', 1, 21, 'Visto que as parteiras temeram a Deus, ele concedeu-lhes que tivessem suas próprias famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1554, 'nvi', 'Êxodo', 'ex', 1, 22, 'Por isso o faraó ordenou a todo o seu povo: "Lancem ao Nilo todo menino recém-nascido, mas deixem viver as meninas".');
