INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1778, 'nvi', 'Êxodo', 'ex', 10, 1, 'O Senhor disse a Moisés: "Vá ao faraó, pois tornei obstinado o coração dele e o de seus conselheiros, a fim de realizar estes meus prodígios entre eles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1779, 'nvi', 'Êxodo', 'ex', 10, 2, 'para que você possa contar a seus filhos e netos como zombei dos egípcios e como realizei meus milagres entre eles. Assim vocês saberão que eu sou o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1780, 'nvi', 'Êxodo', 'ex', 10, 3, 'Dirigiram-se, pois, Moisés e Arão ao faraó e lhe disseram: "Assim diz o Senhor, o Deus dos hebreus: ‘Até quando você se recusará a humilhar-se perante mim? Deixe ir o meu povo, para que me preste culto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1781, 'nvi', 'Êxodo', 'ex', 10, 4, 'Se você não quiser deixá-lo ir, farei vir gafanhotos sobre o seu território amanhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1782, 'nvi', 'Êxodo', 'ex', 10, 5, 'Eles cobrirão a face da terra até não se poder enxergar o solo. Devorarão tanto o pouco que ainda lhes restou da tempestade de granizo como todas as árvores que estiverem brotando nos campos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1783, 'nvi', 'Êxodo', 'ex', 10, 6, 'Encherão os seus palácios e as casas de todos os seus conselheiros e de todos os egípcios: algo que nem os seus pais nem os seus antepassados jamais viram, desde o dia em que se fixaram nesta terra até o dia de hoje’ ". A seguir Moisés virou as costas e saiu da presença do faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1784, 'nvi', 'Êxodo', 'ex', 10, 7, 'Os conselheiros do faraó lhe disseram: "Até quando este homem será uma ameaça para nós? Deixa os homens irem prestar culto ao Senhor Deus deles. Não percebes que o Egito está arruinado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1785, 'nvi', 'Êxodo', 'ex', 10, 8, 'Então Moisés e Arão foram trazidos de volta à presença do faraó, que lhes disse: "Vão e prestem culto ao Senhor, ao seu Deus. Mas, digam-me, quem irá? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1786, 'nvi', 'Êxodo', 'ex', 10, 9, 'Moisés respondeu: "Temos que levar todos: os jovens e os velhos, os nossos filhos e as nossas filhas, as nossas ovelhas e os nossos bois, pois celebraremos uma festa ao Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1787, 'nvi', 'Êxodo', 'ex', 10, 10, 'Disse-lhes o faraó: "Vocês vão mesmo precisar do Senhor quando eu deixá-los ir com as mulheres e crianças! É claro que vocês estão com más intenções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1788, 'nvi', 'Êxodo', 'ex', 10, 11, 'De forma alguma! Só os homens podem ir prestar culto ao Senhor, como vocês têm pedido". E Moisés e Arão foram expulsos da presença do faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1789, 'nvi', 'Êxodo', 'ex', 10, 12, 'Mas o Senhor disse a Moisés: "Estenda a mão sobre o Egito para que os gafanhotos venham sobre a terra e devorem toda a vegetação, tudo o que foi deixado pelo granizo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1790, 'nvi', 'Êxodo', 'ex', 10, 13, 'Moisés estendeu a vara sobre o Egito, e o Senhor fez soprar sobre a terra um vento oriental durante todo aquele dia e toda aquela noite. Pela manhã, o vento havia trazido os gafanhotos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1791, 'nvi', 'Êxodo', 'ex', 10, 14, 'os quais invadiram todo o Egito e desceram em grande número sobre toda a sua extensão. Nunca antes houve tantos gafanhotos, nem jamais haverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1792, 'nvi', 'Êxodo', 'ex', 10, 15, 'Eles cobriram toda a face da terra de tal forma que essa escureceu. Devoraram tudo o que o granizo tinha deixado: toda a vegetação e todos os frutos das árvores. Não restou nada verde nas árvores nem nas plantas do campo, em toda a terra do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1793, 'nvi', 'Êxodo', 'ex', 10, 16, 'O faraó mandou chamar Moisés e Arão imediatamente e disse-lhes: "Pequei contra o Senhor seu Deus e contra vocês!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1794, 'nvi', 'Êxodo', 'ex', 10, 17, 'Agora perdoem ainda esta vez o meu pecado e orem ao Senhor seu Deus para que leve esta praga mortal para longe de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1795, 'nvi', 'Êxodo', 'ex', 10, 18, 'Moisés saiu da presença do faraó e orou ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1796, 'nvi', 'Êxodo', 'ex', 10, 19, 'E o Senhor fez soprar com muito mais força o vento ocidental, e este envolveu os gafanhotos e os lançou no mar Vermelho. Não restou um gafanhoto sequer em toda a extensão do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1797, 'nvi', 'Êxodo', 'ex', 10, 20, 'Mas o Senhor endureceu o coração do faraó, e ele não deixou que os israelitas saíssem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1798, 'nvi', 'Êxodo', 'ex', 10, 21, 'O Senhor disse a Moisés: "Estenda a mão para o céu, e trevas cobrirão o Egito, trevas tais que poderão ser apalpadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1799, 'nvi', 'Êxodo', 'ex', 10, 22, 'Moisés estendeu a mão para o céu, e por três dias houve densas trevas em todo o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1800, 'nvi', 'Êxodo', 'ex', 10, 23, 'Ninguém pôde ver ninguém, nem sair do seu lugar durante três dias. Todavia, todos os israelitas tinham luz nos locais em que habitavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1801, 'nvi', 'Êxodo', 'ex', 10, 24, 'Então o faraó mandou chamar Moisés e disse: "Vão e prestem culto ao Senhor. Deixem somente as ovelhas e os bois; as mulheres e as crianças podem ir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1802, 'nvi', 'Êxodo', 'ex', 10, 25, 'Mas Moisés contestou: "Tu mesmo nos darás os animais para os nossos sacrifícios e holocaustos que ofereceremos ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1803, 'nvi', 'Êxodo', 'ex', 10, 26, 'Além disso, os nossos rebanhos também irão conosco; nem um casco de animal será deixado. Temos que escolher alguns deles para prestar culto ao Senhor nosso Deus e, enquanto não chegarmos ao local, não saberemos quais animais sacrificaremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1804, 'nvi', 'Êxodo', 'ex', 10, 27, 'Mas o Senhor endureceu o coração do faraó, e ele se recusou a deixá-los ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1805, 'nvi', 'Êxodo', 'ex', 10, 28, 'Disse o faraó a Moisés: "Saia da minha presença! Trate de não aparecer nunca mais diante de mim! No dia em que vir a minha face, você morrerá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1806, 'nvi', 'Êxodo', 'ex', 10, 29, 'Respondeu Moisés: "Será como disseste; nunca mais verei a tua face".');
