INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1807, 'nvi', 'Êxodo', 'ex', 11, 1, 'Disse então o Senhor a Moisés: "Enviarei ainda mais uma praga sobre o faraó e sobre o Egito. Somente depois desta ele os deixará sair daqui e até os expulsará totalmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1808, 'nvi', 'Êxodo', 'ex', 11, 2, 'Diga ao povo, tanto aos homens como às mulheres, que peça aos seus vizinhos objetos de prata e de ouro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1809, 'nvi', 'Êxodo', 'ex', 11, 3, 'O Senhor tornou os egípcios favoráveis ao povo, e o próprio Moisés era tido em alta estima no Egito pelos conselheiros do faraó e pelo povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1810, 'nvi', 'Êxodo', 'ex', 11, 4, 'Disse, pois, Moisés ao faraó: "Assim diz o Senhor: ‘Por volta da meia-noite, passarei por todo o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1811, 'nvi', 'Êxodo', 'ex', 11, 5, 'Todos os primogênitos do Egito morrerão, desde o filho mais velho do faraó, herdeiro do trono, até o filho mais velho da escrava que trabalha no moinho, e também todas as primeiras crias do gado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1812, 'nvi', 'Êxodo', 'ex', 11, 6, 'Haverá grande pranto em todo o Egito, como nunca houve antes nem jamais haverá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1813, 'nvi', 'Êxodo', 'ex', 11, 7, 'Entre os israelitas, porém, nem sequer um cão latirá contra homem ou animal’. Então vocês saberão que o Senhor faz distinção entre o Egito e Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1814, 'nvi', 'Êxodo', 'ex', 11, 8, 'Todos esses seus conselheiros virão a mim e se ajoelharão diante de mim, suplicando: ‘Saiam você e todo o povo que o segue! ’ Só então eu sairei". E, com grande ira, Moisés saiu da presença do faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1815, 'nvi', 'Êxodo', 'ex', 11, 9, 'O Senhor tinha dito a Moisés: "O faraó não lhes dará ouvidos, a fim de que os meus prodígios se multipliquem no Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1816, 'nvi', 'Êxodo', 'ex', 11, 10, 'Moisés e Arão realizaram todos esses prodígios diante do faraó, mas o Senhor lhe endureceu o coração, e ele não quis deixar os israelitas saírem do país.');
