INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1868, 'nvi', 'Êxodo', 'ex', 13, 1, 'E disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1869, 'nvi', 'Êxodo', 'ex', 13, 2, '"Consagre a mim todos os primogênitos. O primeiro filho israelita me pertence, não somente entre os homens, mas também entre os animais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1870, 'nvi', 'Êxodo', 'ex', 13, 3, 'Então disse Moisés ao povo: "Comemorem esse dia em que vocês saíram do Egito, da terra da escravidão, porque o Senhor os tirou dali com mão poderosa. Não comam nada fermentado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1871, 'nvi', 'Êxodo', 'ex', 13, 4, 'Neste dia do mês de abibe vocês estão saindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1872, 'nvi', 'Êxodo', 'ex', 13, 5, 'Quando o Senhor os fizer entrar na terra dos cananeus, dos hititas, dos amorreus, dos heveus e dos jebuseus — terra que ele jurou aos seus antepassados que daria a vocês, terra onde manam leite e mel — vocês deverão celebrar esta cerimônia neste mesmo mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1873, 'nvi', 'Êxodo', 'ex', 13, 6, 'Durante sete dias comam pão sem fermento e, no sétimo dia façam uma festa ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1874, 'nvi', 'Êxodo', 'ex', 13, 7, 'Comam pão sem fermento durante os sete dias; não haja nada fermentado entre vocês, nem fermento algum dentro do seu território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1875, 'nvi', 'Êxodo', 'ex', 13, 8, '"Nesse dia cada um dirá a seu filho: Assim faço pelo que o Senhor fez por mim quando saí do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1876, 'nvi', 'Êxodo', 'ex', 13, 9, 'Isto lhe será como sinal em sua mão e memorial em sua testa, para que a lei do Senhor esteja em seus lábios, porque o Senhor o tirou do Egito com mão poderosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1877, 'nvi', 'Êxodo', 'ex', 13, 10, 'Cumpra esta determinação na época certa, de ano em ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1878, 'nvi', 'Êxodo', 'ex', 13, 11, '"Depois que o Senhor os fizer entrar na terra dos cananeus e entregá-la a vocês, como jurou a vocês e aos seus antepassados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1879, 'nvi', 'Êxodo', 'ex', 13, 12, 'separem para o Senhor o primeiro nascido de todo ventre. Todos os primeiros machos dos seus rebanhos pertencem ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1880, 'nvi', 'Êxodo', 'ex', 13, 13, 'Resgate com um cordeiro toda primeira cria dos jumentos, mas se não quiser resgatá-la, quebre-lhe o pescoço. Resgate também todo primogênito entre os seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1881, 'nvi', 'Êxodo', 'ex', 13, 14, '"No futuro, quando os seus filhos lhes perguntarem: ‘Que significa isto? ’, digam-lhes: Com mão poderosa o Senhor nos tirou do Egito, da terra da escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1882, 'nvi', 'Êxodo', 'ex', 13, 15, 'Quando o faraó resistiu e recusou deixar-nos sair, o Senhor matou todos os primogênitos do Egito, tanto de homens como de animais. Por isso sacrificamos ao Senhor os primeiros machos de todo ventre e resgatamos os nossos primogênitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1883, 'nvi', 'Êxodo', 'ex', 13, 16, '"Isto será como sinal em sua mão e marca em sua testa de que o Senhor nos tirou do Egito com mão poderosa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1884, 'nvi', 'Êxodo', 'ex', 13, 17, 'Quando o faraó deixou sair o povo, Deus não o guiou pela rota da terra dos filisteus, embora este fosse o caminho mais curto, pois disse: "Se eles se defrontarem com a guerra, talvez se arrependam e voltem para o Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1885, 'nvi', 'Êxodo', 'ex', 13, 18, 'Assim, o Senhor fez o povo dar a volta pelo deserto, seguindo o caminho que leva ao mar Vermelho. Os israelitas saíram do Egito preparados para lutar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1886, 'nvi', 'Êxodo', 'ex', 13, 19, 'Moisés levou os ossos de José, porque José havia feito os filhos de Israel prestarem um juramento, quando disse: "Deus certamente virá em auxílio de vocês; levem então os meus ossos daqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1887, 'nvi', 'Êxodo', 'ex', 13, 20, 'Os israelitas partiram de Sucote e acamparam em Etã, junto ao deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1888, 'nvi', 'Êxodo', 'ex', 13, 21, 'Durante o dia o Senhor ia adiante deles, numa coluna de nuvem, para guiá-los no caminho, e de noite, numa coluna de fogo, para iluminá-los, e assim podiam caminhar de dia e de noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1889, 'nvi', 'Êxodo', 'ex', 13, 22, 'A coluna de nuvem não se afastava do povo de dia, nem a coluna de fogo, de noite.');
