INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1921, 'nvi', 'Êxodo', 'ex', 15, 1, 'Então Moisés e os israelitas entoaram este cântico ao Senhor: "Cantarei ao Senhor, pois triunfou gloriosamente. Lançou ao mar o cavalo e o seu cavaleiro!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1922, 'nvi', 'Êxodo', 'ex', 15, 2, 'O Senhor é a minha força e a minha canção; ele é a minha salvação! Ele é o meu Deus e eu o louvarei, é o Deus de meu pai, e eu o exaltarei!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1923, 'nvi', 'Êxodo', 'ex', 15, 3, 'O Senhor é guerreiro, o seu nome é Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1924, 'nvi', 'Êxodo', 'ex', 15, 4, 'Ele lançou ao mar os carros de guerra e o exército do faraó. Os seus melhores oficiais afogaram-se no mar Vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1925, 'nvi', 'Êxodo', 'ex', 15, 5, 'Águas profundas os encobriram; como pedra desceram ao fundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1926, 'nvi', 'Êxodo', 'ex', 15, 6, 'Senhor, a tua mão direita foi majestosa em poder. Senhor, a tua mão direita despedaçou o inimigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1927, 'nvi', 'Êxodo', 'ex', 15, 7, 'Em teu triunfo grandioso, derrubaste os teus adversários. Enviaste o teu furor flamejante, que os consumiu como palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1928, 'nvi', 'Êxodo', 'ex', 15, 8, 'Pelo forte sopro das tuas narinas as águas se amontoaram. As águas turbulentas firmaram-se como muralha; as águas profundas congelaram-se no coração do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1929, 'nvi', 'Êxodo', 'ex', 15, 9, 'O inimigo se gloriava: ‘Eu os perseguirei e os alcançarei, dividirei o despojo e os devorarei. Com a espada na mão, eu os destruirei. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1930, 'nvi', 'Êxodo', 'ex', 15, 10, 'Mas enviaste o teu sopro, e o mar os encobriu. Afundaram como chumbo nas águas volumosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1931, 'nvi', 'Êxodo', 'ex', 15, 11, 'Quem entre os deuses é semelhante a ti, Senhor? Quem é semelhante a ti? Majestoso em santidade, terrível em feitos gloriosos, autor de maravilhas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1932, 'nvi', 'Êxodo', 'ex', 15, 12, 'Estendes a tua mão direita e a terra os engole.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1933, 'nvi', 'Êxodo', 'ex', 15, 13, 'Com o teu amor conduzes o povo que resgataste; com a tua força tu o levas à tua santa habitação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1934, 'nvi', 'Êxodo', 'ex', 15, 14, 'As nações ouvem e estremecem; angústia se apodera do povo da Filístia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1935, 'nvi', 'Êxodo', 'ex', 15, 15, 'Os chefes de Edom ficam aterrorizados, os poderosos de Moabe são tomados de tremor, o povo de Canaã esmorece;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1936, 'nvi', 'Êxodo', 'ex', 15, 16, 'terror e medo caem sobre eles; pelo poder do teu braço ficam paralisados como pedra, até que passe o teu povo, ó Senhor, até que passe o povo que tu compraste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1937, 'nvi', 'Êxodo', 'ex', 15, 17, 'Tu o farás entrar e o plantarás no monte da tua herança, no lugar, ó Senhor, que fizeste para a tua habitação, no santuário, ó Senhor, que as tuas mãos estabeleceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1938, 'nvi', 'Êxodo', 'ex', 15, 18, 'O Senhor reinará eternamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1939, 'nvi', 'Êxodo', 'ex', 15, 19, 'Quando os cavalos, os carros de guerra e os cavaleiros do faraó entraram no mar, o Senhor fez que as águas do mar se voltassem sobre eles, mas os israelitas atravessaram o mar pisando em terra seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1940, 'nvi', 'Êxodo', 'ex', 15, 20, 'Então Miriã, a profetisa, irmã de Arão, pegou um tamborim e todas as mulheres a seguiram, tocando tamborins e dançando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1941, 'nvi', 'Êxodo', 'ex', 15, 21, 'E Miriã lhes respondia, cantando: "Cantem ao Senhor, pois triunfou gloriosamente. Lançou ao mar o cavalo e o seu cavaleiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1942, 'nvi', 'Êxodo', 'ex', 15, 22, 'Depois Moisés conduziu Israel desde o mar Vermelho até o deserto de Sur. Durante três dias caminharam no deserto sem encontrar água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1943, 'nvi', 'Êxodo', 'ex', 15, 23, 'Então chegaram a Mara, mas não puderam beber das águas de lá porque eram amargas. Esta é a razão porque o lugar chama-se Mara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1944, 'nvi', 'Êxodo', 'ex', 15, 24, 'E o povo começou a reclamar a Moisés, dizendo: "Que beberemos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1945, 'nvi', 'Êxodo', 'ex', 15, 25, 'Moisés clamou ao Senhor, e este lhe indicou um arbusto. Ele o lançou na água, e esta se tornou boa. Em Mara o Senhor lhes deu leis e ordenanças, e os colocou à prova,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1946, 'nvi', 'Êxodo', 'ex', 15, 26, 'dizendo-lhes: "Se vocês derem atenção ao Senhor, ao seu Deus e fizerem o que ele aprova, se derem ouvidos aos seus mandamentos e obedecerem a todos os seus decretos, não trarei sobre vocês nenhuma das doenças que eu trouxe sobre os egípcios, pois eu sou o Senhor que os cura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1947, 'nvi', 'Êxodo', 'ex', 15, 27, 'Depois chegaram a Elim, onde havia doze fontes de água e setenta palmeiras; e acamparam junto àquelas águas.');
