INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1948, 'nvi', 'Êxodo', 'ex', 16, 1, 'Toda a comunidade de Israel partiu de Elim e chegou ao deserto de Sim, que fica entre Elim e o Sinai. Foi no décimo quinto dia do segundo mês, depois que saíram do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1949, 'nvi', 'Êxodo', 'ex', 16, 2, 'No deserto, toda a comunidade de Israel reclamou a Moisés e Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1950, 'nvi', 'Êxodo', 'ex', 16, 3, 'Disseram-lhes os israelitas: "Quem dera a mão do Senhor nos tivesse matado no Egito! Lá nos sentávamos ao redor das panelas de carne e comíamos pão à vontade, mas vocês nos trouxeram a este deserto para fazer morrer de fome toda esta multidão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1951, 'nvi', 'Êxodo', 'ex', 16, 4, 'Disse, porém, o Senhor a Moisés: "Eu lhes farei chover pão do céu. O povo sairá e recolherá diariamente a porção necessária para aquele dia. Com isso os porei à prova para ver se seguem ou não as minhas instruções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1952, 'nvi', 'Êxodo', 'ex', 16, 5, 'No sexto dia trarão para ser preparado o dobro do que recolhem nos outros dias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1953, 'nvi', 'Êxodo', 'ex', 16, 6, 'Assim Moisés e Arão disseram a todos os israelitas: "Ao entardecer, vocês saberão que foi o Senhor quem os tirou do Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1954, 'nvi', 'Êxodo', 'ex', 16, 7, 'e amanhã cedo verão a glória do Senhor, porque o Senhor ouviu a queixa de vocês contra ele. Quem somos nós para que vocês reclamem a nós? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1955, 'nvi', 'Êxodo', 'ex', 16, 8, 'Disse ainda Moisés: "O Senhor lhes dará carne para comer ao entardecer e pão à vontade pela manhã, porque ele ouviu as suas queixas contra ele. Quem somos nós? Vocês não estão reclamando de nós, mas do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1956, 'nvi', 'Êxodo', 'ex', 16, 9, 'Disse Moisés a Arão: "Diga a toda a comunidade de Israel que se apresente ao Senhor, pois ele ouviu as suas queixas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1957, 'nvi', 'Êxodo', 'ex', 16, 10, 'Enquanto Arão falava a toda a comunidade, todos olharam em direção ao deserto, e a glória do Senhor apareceu na nuvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1958, 'nvi', 'Êxodo', 'ex', 16, 11, 'E o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1959, 'nvi', 'Êxodo', 'ex', 16, 12, '"Ouvi as queixas dos israelitas. Responda-lhes que ao pôr-do-sol vocês comerão carne, e ao amanhecer se fartarão de pão. Assim saberão que eu sou o Senhor seu Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1960, 'nvi', 'Êxodo', 'ex', 16, 13, 'No final da tarde, apareceram codornizes que cobriram o lugar onde estavam acampados; ao amanhecer havia uma camada de orvalho ao redor do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1961, 'nvi', 'Êxodo', 'ex', 16, 14, 'Depois que o orvalho secou, flocos finos semelhantes a geada estavam sobre a superfície do deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1962, 'nvi', 'Êxodo', 'ex', 16, 15, 'Quando os israelitas viram aquilo, começaram a perguntar uns aos outros: "Que é isso? ", pois não sabiam do que se tratava. Disse-lhes Moisés: "Este é o pão que o Senhor lhes deu para comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1963, 'nvi', 'Êxodo', 'ex', 16, 16, 'Assim ordenou o Senhor: ‘Cada chefe de família recolha o quanto precisar: um jarro para cada pessoa da sua tenda’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1964, 'nvi', 'Êxodo', 'ex', 16, 17, 'Os israelitas fizeram como lhes fora dito; alguns recolheram mais, outros menos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1965, 'nvi', 'Êxodo', 'ex', 16, 18, 'Quando mediram com o jarro, quem tinha recolhido muito não teve demais, e não faltou a quem tinha recolhido pouco. Cada um recolheu tanto quanto precisava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1966, 'nvi', 'Êxodo', 'ex', 16, 19, '"Ninguém deve guardar nada para a manhã seguinte", ordenou-lhes Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1967, 'nvi', 'Êxodo', 'ex', 16, 20, 'Todavia, alguns deles não deram atenção a Moisés e guardaram um pouco até a manhã seguinte, mas aquilo criou bicho e começou a cheirar mal. Por isso Moisés irou-se contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1968, 'nvi', 'Êxodo', 'ex', 16, 21, 'Cada manhã todos recolhiam o quanto precisavam, pois quando o sol esquentava, aquilo se derretia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1969, 'nvi', 'Êxodo', 'ex', 16, 22, 'No sexto dia recolheram o dobro: dois jarros para cada pessoa; e os líderes da comunidade foram contar isso a Moisés,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1970, 'nvi', 'Êxodo', 'ex', 16, 23, 'que lhes explicou: "Foi isto que o Senhor ordenou: ‘Amanhã será dia de descanso, sábado consagrado ao Senhor. Assem e cozinhem o que quiserem. Guardem o que sobrar até a manhã seguinte’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1971, 'nvi', 'Êxodo', 'ex', 16, 24, 'E eles o guardaram até a manhã seguinte, como Moisés tinha ordenado, e não cheirou mal nem criou bicho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1972, 'nvi', 'Êxodo', 'ex', 16, 25, '"Comam-no hoje", disse Moisés, "pois hoje é o sábado do Senhor. Hoje, vocês não o encontrarão no terreno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1973, 'nvi', 'Êxodo', 'ex', 16, 26, 'Durante seis dias vocês podem recolhê-lo, mas, no sétimo dia, o sábado, nada acharão. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1974, 'nvi', 'Êxodo', 'ex', 16, 27, 'Apesar disso, alguns deles saíram no sétimo dia para recolhê-lo, mas não encontraram nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1975, 'nvi', 'Êxodo', 'ex', 16, 28, 'Então o Senhor disse a Moisés: "Até quando vocês se recusarão a obedecer aos meus mandamentos e às minhas instruções?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1976, 'nvi', 'Êxodo', 'ex', 16, 29, 'Vejam que o Senhor lhes deu o sábado; e por isso, no sexto dia, ele lhes dá pão para dois dias. No sétimo dia, fiquem todos onde estiverem; ninguém deve sair".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1977, 'nvi', 'Êxodo', 'ex', 16, 30, 'Então o povo descansou no sétimo dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1978, 'nvi', 'Êxodo', 'ex', 16, 31, 'O povo de Israel chamou maná àquele pão. Era branco como semente de coentro e tinha gosto de bolo de mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1979, 'nvi', 'Êxodo', 'ex', 16, 32, 'Disse Moisés: "O Senhor ordenou-lhes que recolham um jarro de maná e guardem-no para as futuras gerações, para que vejam o pão que lhes dei no deserto, quando os tirei do Egito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1980, 'nvi', 'Êxodo', 'ex', 16, 33, 'Então Moisés disse a Arão: "Ponha numa vasilha a medida de um jarro de maná, e coloque-a diante do Senhor, para que seja conservado para as futuras gerações".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1981, 'nvi', 'Êxodo', 'ex', 16, 34, 'Em obediência ao que o Senhor tinha ordenado a Moisés, Arão colocou o maná junto às tábuas da aliança, para ali ser guardado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1982, 'nvi', 'Êxodo', 'ex', 16, 35, 'Os israelitas comeram maná durante quarenta anos, até chegarem a uma terra habitável; comeram maná até chegarem às fronteiras de Canaã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1983, 'nvi', 'Êxodo', 'ex', 16, 36, '( O jarro é a décima parte de uma arroba. )');
