INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2000, 'nvi', 'Êxodo', 'ex', 18, 1, 'Jetro, sacerdote de Midiã e sogro de Moisés, soube de tudo o que Deus tinha feito por Moisés e pelo povo de Israel, como o Senhor havia tirado Israel do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2001, 'nvi', 'Êxodo', 'ex', 18, 2, 'Moisés tinha mandado Zípora, sua mulher, para a casa de seu sogro Jetro, que a recebeu');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2002, 'nvi', 'Êxodo', 'ex', 18, 3, 'juntamente com os seus dois filhos. Um deles chamava-se Gérson, pois Moisés dissera: "Tornei-me imigrante em terra estrangeira";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2003, 'nvi', 'Êxodo', 'ex', 18, 4, 'e o outro chamava-se Eliézer, pois dissera: "O Deus de meu pai foi o meu ajudador; livrou-me da espada do faraó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2004, 'nvi', 'Êxodo', 'ex', 18, 5, 'Jetro, sogro de Moisés, veio com os filhos e a mulher de Moisés encontrá-lo no deserto, onde estava acampado, perto do monte de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2005, 'nvi', 'Êxodo', 'ex', 18, 6, 'E Jetro mandou dizer-lhe: "Eu, seu sogro Jetro, estou indo encontrá-lo, e comigo vão sua mulher e seus dois filhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2006, 'nvi', 'Êxodo', 'ex', 18, 7, 'Então Moisés saiu ao encontro do sogro, curvou-se e beijou-o; trocaram saudações e depois entraram na tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2007, 'nvi', 'Êxodo', 'ex', 18, 8, 'Então Moisés contou ao sogro tudo quanto o Senhor tinha feito ao faraó e aos egípcios por amor a Israel e também todas as dificuldades que tinham enfrentado pelo caminho e como o Senhor os livrara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2008, 'nvi', 'Êxodo', 'ex', 18, 9, 'Jetro alegrou-se ao ouvir todas as coisas boas que o Senhor tinha feito a Israel, libertando-o das mãos dos egípcios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2009, 'nvi', 'Êxodo', 'ex', 18, 10, 'Disse ele: "Bendito seja o Senhor que os libertou das mãos dos egípcios e do faraó; que livrou o povo das mãos dos egípcios!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2010, 'nvi', 'Êxodo', 'ex', 18, 11, 'Agora sei que o Senhor é maior do que todos os outros deuses, pois ele os superou exatamente naquilo de que se vangloriavam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2011, 'nvi', 'Êxodo', 'ex', 18, 12, 'Então Jetro, sogro de Moisés, ofereceu um holocausto e sacrifícios a Deus, e Arão veio com todas as autoridades de Israel para comerem com o sogro de Moisés na presença de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2012, 'nvi', 'Êxodo', 'ex', 18, 13, 'No dia seguinte Moisés assentou-se para julgar as questões do povo, e este permaneceu de pé diante dele, desde a manhã até o cair da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2013, 'nvi', 'Êxodo', 'ex', 18, 14, 'Quando o seu sogro viu tudo o que ele estava fazendo pelo povo, disse: "Que é que você está fazendo? Por que só você se assenta para julgar, e todo este povo o espera de pé, desde a manhã até o cair da tarde? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2014, 'nvi', 'Êxodo', 'ex', 18, 15, 'Moisés lhe respondeu: "O povo me procura para que eu consulte a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2015, 'nvi', 'Êxodo', 'ex', 18, 16, 'Toda vez que alguém tem uma questão, esta me é trazida, e eu decido entre as partes, e ensino-lhes os decretos e leis de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2016, 'nvi', 'Êxodo', 'ex', 18, 17, 'Respondeu o sogro de Moisés: "O que você está fazendo não é bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2017, 'nvi', 'Êxodo', 'ex', 18, 18, 'Você e o seu povo ficarão esgotados, pois esta tarefa lhe é pesada demais. Você não pode executá-la sozinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2018, 'nvi', 'Êxodo', 'ex', 18, 19, 'Agora, ouça-me! Eu lhe darei um conselho, e que Deus esteja com você! Seja você o representante do povo diante de Deus e leve a Deus as suas questões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2019, 'nvi', 'Êxodo', 'ex', 18, 20, 'Oriente-os quanto aos decretos e leis, mostrando-lhes como devem viver e o que devem fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2020, 'nvi', 'Êxodo', 'ex', 18, 21, 'Mas escolha dentre todo o povo homens capazes, tementes a Deus, dignos de confiança e inimigos de ganho desonesto. Estabeleça-os como chefes de mil, de cem, de cinqüenta e de dez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2021, 'nvi', 'Êxodo', 'ex', 18, 22, 'Eles estarão sempre à disposição do povo para julgar as questões. Trarão a você apenas as questões difíceis; as mais simples decidirão sozinhos. Isso tornará mais leve o seu fardo, porque eles o dividirão com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2022, 'nvi', 'Êxodo', 'ex', 18, 23, 'Se você assim fizer, e se assim Deus ordenar, você será capaz de suportar as dificuldades, e todo este povo voltará para casa satisfeito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2023, 'nvi', 'Êxodo', 'ex', 18, 24, 'Moisés aceitou o conselho do sogro e fez tudo como ele tinha sugerido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2024, 'nvi', 'Êxodo', 'ex', 18, 25, 'Escolheu homens capazes de todo o Israel e colocou-os como líderes do povo: chefes de mil, de cem, de cinqüenta e de dez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2025, 'nvi', 'Êxodo', 'ex', 18, 26, 'Estes ficaram como juízes permanentes do povo. As questões difíceis levavam a Moisés; as mais simples, porém, eles mesmos resolviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2026, 'nvi', 'Êxodo', 'ex', 18, 27, 'Então Moisés e seu sogro se despediram, e este voltou para a sua terra.');
