INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2027, 'nvi', 'Êxodo', 'ex', 19, 1, 'No dia em que se completaram três meses que os israelitas haviam saído do Egito, chegaram ao deserto do Sinai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2028, 'nvi', 'Êxodo', 'ex', 19, 2, 'Depois de saírem de Refidim, entraram no deserto do Sinai, e Israel acampou ali, diante do monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2029, 'nvi', 'Êxodo', 'ex', 19, 3, 'Logo Moisés subiu o monte para encontrar-se com Deus. E o Senhor o chamou do monte, dizendo: "Diga o seguinte aos descendentes de Jacó e declare aos israelitas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2030, 'nvi', 'Êxodo', 'ex', 19, 4, '‘Vocês viram o que fiz ao Egito e como os transportei sobre asas de águias e os trouxe para junto de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2031, 'nvi', 'Êxodo', 'ex', 19, 5, 'Agora, se me obedecerem fielmente e guardarem a minha aliança, vocês serão o meu tesouro pessoal dentre todas as nações. Embora toda a terra seja minha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2032, 'nvi', 'Êxodo', 'ex', 19, 6, 'vocês serão para mim um reino de sacerdotes e uma nação santa’. Essas são as palavras que você dirá aos israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2033, 'nvi', 'Êxodo', 'ex', 19, 7, 'Moisés voltou, convocou as autoridades do povo e lhes expôs tudo o que o Senhor havia-lhe mandado falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2034, 'nvi', 'Êxodo', 'ex', 19, 8, 'O povo todo respondeu unânime: "Faremos tudo o que o Senhor ordenou". E Moisés levou ao Senhor a resposta do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2035, 'nvi', 'Êxodo', 'ex', 19, 9, 'Disse o Senhor a Moisés: "Virei a você numa densa nuvem, a fim de que o povo, ouvindo-me falar-lhe, passe a confiar sempre em você". Então Moisés relatou ao Senhor o que o povo lhe dissera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2036, 'nvi', 'Êxodo', 'ex', 19, 10, 'E o Senhor disse a Moisés: "Vá ao povo e consagre-o hoje e amanhã. Eles deverão lavar as suas vestes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2037, 'nvi', 'Êxodo', 'ex', 19, 11, 'e estar prontos no terceiro dia, porque nesse dia o Senhor descerá sobre o monte Sinai, à vista de todo o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2038, 'nvi', 'Êxodo', 'ex', 19, 12, 'Estabeleça limites em torno do monte e diga ao povo: Tenham o cuidado de não subir ao monte e de não tocar na sua base. Quem tocar no monte certamente será morto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2039, 'nvi', 'Êxodo', 'ex', 19, 13, 'será apedrejado ou morto a flechadas. Ninguém deverá tocá-lo com a mão. Seja homem, seja animal, não viverá. Somente quando a corneta soar um toque longo eles poderão subir ao monte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2040, 'nvi', 'Êxodo', 'ex', 19, 14, 'Tendo Moisés descido do monte, consagrou o povo; e eles lavaram as suas vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2041, 'nvi', 'Êxodo', 'ex', 19, 15, 'Disse ele então ao povo: "Preparem-se para o terceiro dia, e até lá não se acheguem a mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2042, 'nvi', 'Êxodo', 'ex', 19, 16, 'Ao amanhecer do terceiro dia houve trovões e raios, uma densa nuvem cobriu o monte, e uma trombeta ressoou fortemente. Todos no acampamento tremeram de medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2043, 'nvi', 'Êxodo', 'ex', 19, 17, 'Moisés levou o povo para fora do acampamento, para encontrar-se com Deus, e eles ficaram ao pé do monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2044, 'nvi', 'Êxodo', 'ex', 19, 18, 'O monte Sinai estava coberto de fumaça, pois o Senhor tinha descido sobre ele em chamas de fogo. Dele subia fumaça como que de uma fornalha; todo o monte tremia violentamente,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2045, 'nvi', 'Êxodo', 'ex', 19, 19, 'e o som da trombeta era cada vez mais forte. Então Moisés falou, e a voz de Deus lhe respondeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2046, 'nvi', 'Êxodo', 'ex', 19, 20, 'O Senhor desceu ao topo do monte Sinai e chamou Moisés para o alto do monte. Moisés subiu');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2047, 'nvi', 'Êxodo', 'ex', 19, 21, 'e o Senhor lhe disse: "Desça e alerte o povo que não ultrapasse os limites, para ver o Senhor, e muitos deles pereçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2048, 'nvi', 'Êxodo', 'ex', 19, 22, 'Mesmo os sacerdotes que se aproximarem do Senhor devem consagrar-se; senão o Senhor os fulminará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2049, 'nvi', 'Êxodo', 'ex', 19, 23, 'Moisés disse ao Senhor: "O povo não pode subir ao monte Sinai, pois tu mesmo nos avisaste: ‘Estabeleça um limite em torno do monte e declare-o santo’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2050, 'nvi', 'Êxodo', 'ex', 19, 24, 'O Senhor respondeu: "Desça e depois torne a subir, acompanhado de Arão. Quanto aos sacerdotes e ao povo, não devem ultrapassar o limite para subir ao Senhor; senão, o Senhor os fulminará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2051, 'nvi', 'Êxodo', 'ex', 19, 25, 'Então Moisés desceu e avisou o povo.');
