INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1555, 'nvi', 'Êxodo', 'ex', 2, 1, 'Um homem da tribo de Levi casou-se com uma mulher da mesma tribo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1556, 'nvi', 'Êxodo', 'ex', 2, 2, 'e ela engravidou e deu à luz um filho. Vendo que era bonito, ela o escondeu por três meses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1557, 'nvi', 'Êxodo', 'ex', 2, 3, 'Quando já não podia mais escondê-lo, pegou um cesto feito de junco e o vedou com piche e betume. Colocou nele o menino e deixou o cesto entre os juncos, à margem do Nilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1558, 'nvi', 'Êxodo', 'ex', 2, 4, 'A irmã do menino ficou observando de longe para ver o que lhe aconteceria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1559, 'nvi', 'Êxodo', 'ex', 2, 5, 'A filha do faraó descera ao Nilo para tomar banho. Enquanto isso as suas servas andavam pela margem do rio. Nisso viu o cesto entre os juncos e mandou sua criada apanhá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1560, 'nvi', 'Êxodo', 'ex', 2, 6, 'Ao abri-lo viu um bebê chorando. Ficou com pena dele e disse: "Este menino é dos hebreus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1561, 'nvi', 'Êxodo', 'ex', 2, 7, 'Então a irmã do menino aproximou-se e perguntou à filha do faraó: "A senhora quer que eu vá chamar uma mulher dos hebreus para amamentar e criar o menino? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1562, 'nvi', 'Êxodo', 'ex', 2, 8, '"Quero", respondeu ela. E a moça foi chamar a mãe do menino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1563, 'nvi', 'Êxodo', 'ex', 2, 9, 'Então a filha do faraó disse à mulher: "Leve este menino e amamente-o para mim, e eu lhe pagarei por isso". A mulher levou o menino e o amamentou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1564, 'nvi', 'Êxodo', 'ex', 2, 10, 'Tendo o menino crescido, ela o levou à filha do faraó, que o adotou e lhe deu o nome de Moisés, dizendo: "Porque eu o tirei das águas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1565, 'nvi', 'Êxodo', 'ex', 2, 11, 'Certo dia, sendo Moisés já adulto, foi ao lugar onde estavam os seus irmãos hebreus e descobriu como era pesado o trabalho que realizavam. Viu também um egípcio espancar um dos hebreus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1566, 'nvi', 'Êxodo', 'ex', 2, 12, 'Correu o olhar por todos os lados e, não vendo ninguém, matou o egípcio e o escondeu na areia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1567, 'nvi', 'Êxodo', 'ex', 2, 13, 'No dia seguinte saiu e viu dois hebreus brigando. Então perguntou ao agressor: "Por que você está espancando o seu companheiro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1568, 'nvi', 'Êxodo', 'ex', 2, 14, 'O homem respondeu: "Quem o nomeou líder e juiz sobre nós? Quer matar-me como matou o egípcio? " Moisés teve medo e pensou: "Com certeza tudo já foi descoberto! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1569, 'nvi', 'Êxodo', 'ex', 2, 15, 'Quando o faraó soube disso, procurou matar Moisés, mas este fugiu e foi morar na terra de Midiã. Ali assentou-se à beira de um poço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1570, 'nvi', 'Êxodo', 'ex', 2, 16, 'Ora, o sacerdote de Midiã tinha sete filhas. Elas foram buscar água para encher os bebedouros e dar de beber ao rebanho de seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1571, 'nvi', 'Êxodo', 'ex', 2, 17, 'Alguns pastores se aproximaram e começaram a expulsá-las dali; Moisés, porém, veio em auxílio delas e deu água ao rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1572, 'nvi', 'Êxodo', 'ex', 2, 18, 'Quando as moças voltaram a seu pai Reuel, este lhes perguntou: "Por que voltaram tão cedo hoje? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1573, 'nvi', 'Êxodo', 'ex', 2, 19, 'Elas responderam: "Um egípcio defendeu-nos dos pastores e ainda tirou água do poço para nós e deu de beber ao rebanho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1574, 'nvi', 'Êxodo', 'ex', 2, 20, '"Onde está ele? ", perguntou o pai a elas. "Por que o deixaram lá? Convidem-no para comer conosco. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1575, 'nvi', 'Êxodo', 'ex', 2, 21, 'Moisés aceitou e concordou também em morar na casa daquele homem; este lhe deu por mulher sua filha Zípora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1576, 'nvi', 'Êxodo', 'ex', 2, 22, 'Ela deu à luz um menino, a quem Moisés deu o nome de Gérson, dizendo: "Sou imigrante em terra estrangeira".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1577, 'nvi', 'Êxodo', 'ex', 2, 23, 'Muito tempo depois, morreu o rei do Egito. Os israelitas gemiam e clamavam debaixo da escravidão; e o seu clamor subiu até Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1578, 'nvi', 'Êxodo', 'ex', 2, 24, 'Ouviu Deus o lamento deles e lembrou-se da aliança que fizera com Abraão, Isaque e Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1579, 'nvi', 'Êxodo', 'ex', 2, 25, 'Deus olhou para os israelitas e viu qual era a situação deles.');
