INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2052, 'nvi', 'Êxodo', 'ex', 20, 1, 'E Deus falou todas estas palavras:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2053, 'nvi', 'Êxodo', 'ex', 20, 2, '"Eu sou o Senhor, o teu Deus, que te tirou do Egito, da terra da escravidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2054, 'nvi', 'Êxodo', 'ex', 20, 3, '"Não terás outros deuses além de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2055, 'nvi', 'Êxodo', 'ex', 20, 4, '"Não farás para ti nenhum ídolo, nenhuma imagem de qualquer coisa no céu, na terra, ou nas águas debaixo da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2056, 'nvi', 'Êxodo', 'ex', 20, 5, 'Não te prostrarás diante deles nem lhes prestarás culto, porque eu, o Senhor teu Deus, sou Deus zeloso, que castigo os filhos pelos pecados de seus pais até a terceira e quarta geração daqueles que me desprezam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2057, 'nvi', 'Êxodo', 'ex', 20, 6, 'mas trato com bondade até mil gerações aos que me amam e guardam os meus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2058, 'nvi', 'Êxodo', 'ex', 20, 7, '"Não tomarás em vão o nome do Senhor teu Deus, pois o Senhor não deixará impune quem tomar o seu nome em vão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2059, 'nvi', 'Êxodo', 'ex', 20, 8, '"Lembra-te do dia de sábado, para santificá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2060, 'nvi', 'Êxodo', 'ex', 20, 9, 'Trabalharás seis dias e neles farás todos os teus trabalhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2061, 'nvi', 'Êxodo', 'ex', 20, 10, 'mas o sétimo dia é o sábado dedicado ao Senhor teu Deus. Nesse dia não farás trabalho algum, nem tu, nem teus filhos ou filhas, nem teus servos ou servas, nem teus animais, nem os estrangeiros que morarem em tuas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2062, 'nvi', 'Êxodo', 'ex', 20, 11, 'Pois em seis dias o Senhor fez os céus e a terra, o mar e tudo o que neles existe, mas no sétimo dia descansou. Portanto, o Senhor abençoou o sétimo dia e o santificou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2063, 'nvi', 'Êxodo', 'ex', 20, 12, '"Honra teu pai e tua mãe, a fim de que tenhas vida longa na terra que o Senhor teu Deus te dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2064, 'nvi', 'Êxodo', 'ex', 20, 13, '"Não matarás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2065, 'nvi', 'Êxodo', 'ex', 20, 14, '"Não adulterarás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2066, 'nvi', 'Êxodo', 'ex', 20, 15, '"Não furtarás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2067, 'nvi', 'Êxodo', 'ex', 20, 16, '"Não darás falso testemunho contra o teu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2068, 'nvi', 'Êxodo', 'ex', 20, 17, '"Não cobiçarás a casa do teu próximo. Não cobiçarás a mulher do teu próximo, nem seus servos ou servas, nem seu boi ou jumento, nem coisa alguma que lhe pertença".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2069, 'nvi', 'Êxodo', 'ex', 20, 18, 'Vendo-se o povo diante dos trovões e dos relâmpagos, e do som da trombeta e do monte fumegando, todos tremeram assustados. Ficaram à distância');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2070, 'nvi', 'Êxodo', 'ex', 20, 19, 'e disseram a Moisés: "Fala tu mesmo conosco, e ouviremos. Mas que Deus não fale conosco, para que não morramos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2071, 'nvi', 'Êxodo', 'ex', 20, 20, 'Moisés disse ao povo: "Não tenham medo! Deus veio prová-los, para que o temor de Deus esteja em vocês e os livre de pecar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2072, 'nvi', 'Êxodo', 'ex', 20, 21, 'Mas o povo permaneceu à distância, ao passo que Moisés aproximou-se da nuvem escura em que Deus se encontrava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2073, 'nvi', 'Êxodo', 'ex', 20, 22, 'O Senhor disse a Moisés: "Diga o seguinte aos israelitas: Vocês viram por si mesmos que do céu lhes falei:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2074, 'nvi', 'Êxodo', 'ex', 20, 23, 'não façam ídolos de prata nem de ouro para me representarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2075, 'nvi', 'Êxodo', 'ex', 20, 24, '"Façam-me um altar de terra e nele sacrifiquem-me os seus holocaustos e as suas ofertas de comunhão, as suas ovelhas e os seus bois. Onde quer que eu faça celebrar o meu nome, virei a vocês e os abençoarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2076, 'nvi', 'Êxodo', 'ex', 20, 25, 'Se me fizerem um altar de pedras, não o façam com pedras lavradas, porque o uso de ferramentas o profanaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2077, 'nvi', 'Êxodo', 'ex', 20, 26, 'Não subam por degraus ao meu altar, para que nele não seja exposta a sua nudez".');
