INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2078, 'nvi', 'Êxodo', 'ex', 21, 1, '"São estas as leis que você proclamará ao povo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2079, 'nvi', 'Êxodo', 'ex', 21, 2, '"Se você comprar um escravo hebreu, ele o servirá por seis anos. Mas no sétimo ano será liberto, sem precisar pagar nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2080, 'nvi', 'Êxodo', 'ex', 21, 3, 'Se chegou solteiro, solteiro receberá liberdade; mas se chegou casado, sua mulher irá com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2081, 'nvi', 'Êxodo', 'ex', 21, 4, 'Se o seu senhor lhe tiver dado uma mulher, e esta lhe tiver dado filhos ou filhas, a mulher e os filhos pertencerão ao senhor; somente o homem sairá livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2082, 'nvi', 'Êxodo', 'ex', 21, 5, '"Se, porém, o escravo declarar: ‘Eu amo o meu senhor, a minha mulher e os meus filhos, e não quero sair livre’,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2083, 'nvi', 'Êxodo', 'ex', 21, 6, 'o seu senhor o levará perante os juízes. Terá que levá-lo à porta ou à lateral da porta e furar a sua orelha. Assim, ele será seu escravo por toda a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2084, 'nvi', 'Êxodo', 'ex', 21, 7, '"Se um homem vender sua filha como escrava, ela não será liberta como os escravos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2085, 'nvi', 'Êxodo', 'ex', 21, 8, 'Se ela não agradar ao seu senhor que a escolheu, ele deverá permitir que ela seja resgatada. Não poderá vendê-la a estrangeiros, pois isso seria deslealdade para com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2086, 'nvi', 'Êxodo', 'ex', 21, 9, 'Se o seu senhor a escolher para seu filho, lhe dará os direitos de uma filha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2087, 'nvi', 'Êxodo', 'ex', 21, 10, 'Se o senhor tomar uma segunda mulher, não poderá privar a primeira de alimento, de roupas e dos direitos conjugais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2088, 'nvi', 'Êxodo', 'ex', 21, 11, 'Se não lhe garantir essas três coisas, ela poderá ir embora sem precisar pagar nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2089, 'nvi', 'Êxodo', 'ex', 21, 12, '"Quem ferir um homem, vindo a matá-lo, terá que ser executado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2090, 'nvi', 'Êxodo', 'ex', 21, 13, 'Todavia, se não o fez intencionalmente, mas Deus o permitiu, designei um lugar para onde poderá fugir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2091, 'nvi', 'Êxodo', 'ex', 21, 14, 'Mas se alguém tiver planejado matar outro deliberadamente, tire-o até mesmo do meu altar e mate-o.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2092, 'nvi', 'Êxodo', 'ex', 21, 15, '"Quem agredir o próprio pai ou a própria mãe terá que ser executado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2093, 'nvi', 'Êxodo', 'ex', 21, 16, '"Aquele que seqüestrar alguém e vendê-lo ou for apanhado com ele em seu poder, terá que ser executado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2094, 'nvi', 'Êxodo', 'ex', 21, 17, '"Quem amaldiçoar seu pai ou sua mãe terá que ser executado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2095, 'nvi', 'Êxodo', 'ex', 21, 18, '"Se dois homens brigarem e um deles ferir o outro com uma pedra ou com o punho e o outro não morrer, mas cair de cama,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2096, 'nvi', 'Êxodo', 'ex', 21, 19, 'aquele que o feriu será absolvido, se o outro se levantar e caminhar com o auxílio de uma bengala; todavia ele terá que indenizar o homem ferido pelo tempo que este perdeu e responsabilizar-se por sua completa recuperação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2097, 'nvi', 'Êxodo', 'ex', 21, 20, '"Se alguém ferir seu escravo ou escrava com um pedaço de pau, e como resultado o escravo morrer, será punido;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2098, 'nvi', 'Êxodo', 'ex', 21, 21, 'mas se o escravo sobreviver um ou dois dias, não será punido, visto que é sua propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2099, 'nvi', 'Êxodo', 'ex', 21, 22, '"Se homens brigarem e ferirem uma mulher grávida, e ela der à luz prematuramente, não havendo, porém, nenhum dano sério, o ofensor pagará a indenização que o marido daquela mulher exigir, conforme a determinação dos juízes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2100, 'nvi', 'Êxodo', 'ex', 21, 23, 'Mas, se houver danos graves, a pena será vida por vida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2101, 'nvi', 'Êxodo', 'ex', 21, 24, 'olho por olho, dente por dente, mão por mão, pé por pé,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2102, 'nvi', 'Êxodo', 'ex', 21, 25, 'queimadura por queimadura, ferida por ferida, contusão por contusão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2103, 'nvi', 'Êxodo', 'ex', 21, 26, '"Se alguém ferir o seu escravo ou sua escrava no olho e o cegar, terá que libertar o escravo como compensação pelo olho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2104, 'nvi', 'Êxodo', 'ex', 21, 27, 'Se quebrar um dente de um escravo ou de uma escrava, terá que libertar o escravo como compensação pelo dente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2105, 'nvi', 'Êxodo', 'ex', 21, 28, '"Se um boi chifrar um homem ou uma mulher, causando-lhe a morte, o boi terá que ser apedrejado até a morte, e a sua carne não poderá ser comida. Mas o dono do boi será absolvido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2106, 'nvi', 'Êxodo', 'ex', 21, 29, 'Se, todavia, o boi costumava chifrar e o dono, ainda que alertado, não o manteve preso, e o boi matar um homem ou uma mulher, o boi será apedrejado e o dono também terá que ser morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2107, 'nvi', 'Êxodo', 'ex', 21, 30, 'Caso, porém, lhe pedirem um pagamento, poderá resgatar a sua vida pagando o que for exigido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2108, 'nvi', 'Êxodo', 'ex', 21, 31, 'Esta sentença também se aplica no caso de um boi chifrar um menino ou uma menina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2109, 'nvi', 'Êxodo', 'ex', 21, 32, 'Se o boi chifrar um escravo ou escrava, o dono do animal terá que pagar trezentos e sessenta gramas de prata ao dono do escravo, e o boi será apedrejado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2110, 'nvi', 'Êxodo', 'ex', 21, 33, '"Se alguém abrir ou deixar aberta uma cisterna, não tendo o cuidado de tampá-la, e um jumento ou um boi nela cair,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2111, 'nvi', 'Êxodo', 'ex', 21, 34, 'o dono da cisterna terá que pagar o prejuízo indenizando o dono do animal, e ficará com o animal morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2112, 'nvi', 'Êxodo', 'ex', 21, 35, '"Se o boi de alguém ferir o boi de outro e o matar, venderão o boi vivo e o dividirão em partes iguais, tanto o valor como o animal morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2113, 'nvi', 'Êxodo', 'ex', 21, 36, 'Contudo, se o boi costumava chifrar, e o dono não o manteve preso, este terá que pagar boi por boi, e ficará com o que morreu. "');
