INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2145, 'nvi', 'Êxodo', 'ex', 23, 1, '"Não faça declarações falsas e não seja cúmplice do ímpio, sendo-lhe testemunha mal-intencionada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2146, 'nvi', 'Êxodo', 'ex', 23, 2, '"Não acompanhe a maioria para fazer o mal. Ao testemunhar num processo, não perverta a justiça para apoiar a maioria,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2147, 'nvi', 'Êxodo', 'ex', 23, 3, 'nem para favorecer o pobre num processo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2148, 'nvi', 'Êxodo', 'ex', 23, 4, '"Se você encontrar perdido o boi ou o jumento que pertence ao seu inimigo, leve-o de volta a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2149, 'nvi', 'Êxodo', 'ex', 23, 5, 'Se você vir o jumento de alguém que o odeia caído sob o peso de sua carga, não o abandone, procure ajudá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2150, 'nvi', 'Êxodo', 'ex', 23, 6, '"Não perverta o direito dos pobres em seus processos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2151, 'nvi', 'Êxodo', 'ex', 23, 7, 'Não se envolva em falsas acusações nem condene à morte o inocente e o justo, porque não absolverei o culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2152, 'nvi', 'Êxodo', 'ex', 23, 8, '"Não aceite suborno, pois o suborno cega até os que têm discernimento e prejudica a causa do justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2153, 'nvi', 'Êxodo', 'ex', 23, 9, '"Não oprima o estrangeiro. Vocês sabem o que é ser estrangeiro, pois foram estrangeiros no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2154, 'nvi', 'Êxodo', 'ex', 23, 10, '"Plantem e colham em sua terra durante seis anos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2155, 'nvi', 'Êxodo', 'ex', 23, 11, 'mas no sétimo deixem-na descansar sem cultivá-la. Assim os pobres do povo poderão comer o que crescer por si, e o que restar ficará para os animais do campo. Façam o mesmo com as suas vinhas e com os seus olivais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2156, 'nvi', 'Êxodo', 'ex', 23, 12, '"Em seis dias façam os seus trabalhos, mas no sétimo não trabalhem, para que o seu boi e o seu jumento possam descansar, e o seu escravo e o estrangeiro renovem as forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2157, 'nvi', 'Êxodo', 'ex', 23, 13, '"Tenham o cuidado de fazer tudo o que lhes ordenei. Não invoquem o nome de outros deuses; não se ouçam tais nomes dos seus lábios. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2158, 'nvi', 'Êxodo', 'ex', 23, 14, '"Três vezes por ano vocês me celebrarão festa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2159, 'nvi', 'Êxodo', 'ex', 23, 15, '"Celebrem a festa dos pães sem fermento; durante sete dias comam pão sem fermento, como eu lhes ordenei. Façam isto na época determinada do mês de abibe, pois nesse mês vocês saíram do Egito. "Ninguém se apresentará a mim de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2160, 'nvi', 'Êxodo', 'ex', 23, 16, '"Celebrem a festa da colheita dos primeiros frutos do seu trabalho de semeadura. "Celebrem a festa do encerramento da colheita, quando, no final do ano, vocês armazenarem as colheitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2161, 'nvi', 'Êxodo', 'ex', 23, 17, '"Três vezes por ano todos os homens devem comparecer diante do Senhor soberano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2162, 'nvi', 'Êxodo', 'ex', 23, 18, '"Não ofereçam o sangue de um sacrifício feito em minha honra com pão fermentado. "A gordura das ofertas de minhas festas não deverá ser guardada até a manhã seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2163, 'nvi', 'Êxodo', 'ex', 23, 19, '"Tragam ao santuário do Senhor seu Deus o melhor dos primeiros frutos das suas colheitas. "Não cozinhem o cabrito no leite da própria mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2164, 'nvi', 'Êxodo', 'ex', 23, 20, '"Eis que envio um anjo à frente de vocês para protegê-los por todo o caminho e fazê-los chegar ao lugar que preparei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2165, 'nvi', 'Êxodo', 'ex', 23, 21, 'Prestem atenção e ouçam o que ele diz. Não se rebelem contra ele, pois não perdoará as suas transgressões, pois nele está o meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2166, 'nvi', 'Êxodo', 'ex', 23, 22, 'Se vocês ouvirem atentamente o que ele disser e fizerem tudo o que lhes ordeno, serei inimigo dos seus inimigos, e adversário dos seus adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2167, 'nvi', 'Êxodo', 'ex', 23, 23, 'O meu anjo irá à frente de vocês e os fará chegar à terra dos amorreus, dos hititas, dos ferezeus, dos cananeus, dos heveus e dos jebuseus, e eu os exterminarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2168, 'nvi', 'Êxodo', 'ex', 23, 24, 'Não se curvem diante dos deuses deles, nem lhes prestem culto, nem sigam as suas práticas. Destruam-nos totalmente e quebrem as suas colunas sagradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2169, 'nvi', 'Êxodo', 'ex', 23, 25, 'Prestem culto ao Senhor, o Deus de vocês, e ele os abençoará, dando-lhes alimento e água. Tirarei a doença do meio de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2170, 'nvi', 'Êxodo', 'ex', 23, 26, 'Em sua terra nenhuma grávida perderá o filho, nem haverá mulher estéril. Farei completar-se o tempo de duração da vida de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2171, 'nvi', 'Êxodo', 'ex', 23, 27, '"Mandarei adiante de vocês o meu terror, que porá em confusão todas as nações que vocês encontrarem. Farei que todos os seus inimigos virem as costas e fujam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2172, 'nvi', 'Êxodo', 'ex', 23, 28, 'Causarei pânico entre os heveus, os cananeus e os hititas para expulsá-los de diante de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2173, 'nvi', 'Êxodo', 'ex', 23, 29, 'Não os expulsarei num só ano, pois a terra se tornaria desolada e os animais selvagens se multiplicariam, ameaçando vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2174, 'nvi', 'Êxodo', 'ex', 23, 30, 'Eu os expulsarei aos poucos, até que vocês sejam numerosos o suficiente para tomar posse da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2175, 'nvi', 'Êxodo', 'ex', 23, 31, '"Estabelecerei as suas fronteiras desde o mar Vermelho até o mar dos filisteus, e desde o deserto até o Rio. Entregarei em suas mãos os povos que vivem na terra, aos quais expulsarão de diante de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2176, 'nvi', 'Êxodo', 'ex', 23, 32, 'Não façam aliança com eles nem com os seus deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2177, 'nvi', 'Êxodo', 'ex', 23, 33, 'Não deixem que esses povos morem na terra de vocês, senão eles os levarão a pecar contra mim, porque prestar culto aos deuses deles será uma armadilha para vocês. "');
