INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2196, 'nvi', 'Êxodo', 'ex', 25, 1, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2197, 'nvi', 'Êxodo', 'ex', 25, 2, '"Diga aos israelitas que me tragam uma oferta. Receba-a de todo aquele cujo coração o compelir a dar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2198, 'nvi', 'Êxodo', 'ex', 25, 3, 'Estas são as ofertas que deverá receber deles: ouro, prata e bronze,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2199, 'nvi', 'Êxodo', 'ex', 25, 4, 'fios de tecido azul, roxo e vermelho, linho fino, pêlos de cabra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2200, 'nvi', 'Êxodo', 'ex', 25, 5, 'peles de carneiro tingidas de vermelho, couro, madeira de acácia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2201, 'nvi', 'Êxodo', 'ex', 25, 6, 'azeite para iluminação; especiarias para o óleo da unção e para o incenso aromático;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2202, 'nvi', 'Êxodo', 'ex', 25, 7, 'pedras de ônix e outras pedras preciosas para serem encravadas no colete sacerdotal e no peitoral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2203, 'nvi', 'Êxodo', 'ex', 25, 8, '"E farão um santuário para mim, e eu habitarei no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2204, 'nvi', 'Êxodo', 'ex', 25, 9, 'Façam tudo como eu lhe mostrar, conforme o modelo do tabernáculo e de cada utensílio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2205, 'nvi', 'Êxodo', 'ex', 25, 10, '"Faça uma arca de madeira de acácia com um metro e dez centímetros de comprimento, setenta centímetros de largura e setenta centímetros de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2206, 'nvi', 'Êxodo', 'ex', 25, 11, 'Revista-a de ouro puro, por dentro e por fora, e faça uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2207, 'nvi', 'Êxodo', 'ex', 25, 12, 'Mande fundir quatro argolas de ouro para ela e prenda-as em seus quatro pés, com duas argolas de um lado e duas do outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2208, 'nvi', 'Êxodo', 'ex', 25, 13, 'Depois faça varas de madeira de acácia, revista-as de ouro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2209, 'nvi', 'Êxodo', 'ex', 25, 14, 'e coloque-as nas argolas laterais da arca, para que possa ser carregada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2210, 'nvi', 'Êxodo', 'ex', 25, 15, 'As varas permanecerão nas argolas da arca; não devem ser retiradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2211, 'nvi', 'Êxodo', 'ex', 25, 16, 'Então coloque dentro da arca as tábuas da aliança que lhe darei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2212, 'nvi', 'Êxodo', 'ex', 25, 17, '"Faça uma tampa de ouro puro com um metro e dez centímetros de comprimento por setenta centímetros de largura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2213, 'nvi', 'Êxodo', 'ex', 25, 18, 'com dois querubins de ouro batido nas extremidades da tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2214, 'nvi', 'Êxodo', 'ex', 25, 19, 'Faça um querubim numa extremidade e o segundo na outra, formando uma só peça com a tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2215, 'nvi', 'Êxodo', 'ex', 25, 20, 'Os querubins devem ter suas asas estendidas para cima, cobrindo com elas a tampa. Ficarão de frente um para o outro, com o rosto voltado para a tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2216, 'nvi', 'Êxodo', 'ex', 25, 21, 'Coloque a tampa sobre a arca, e dentro dela as tábuas da aliança que darei a você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2217, 'nvi', 'Êxodo', 'ex', 25, 22, 'Ali, sobre a tampa, no meio dos dois querubins que se encontram sobre a arca da aliança, eu me encontrarei com você e lhe darei todos os meus mandamentos destinados aos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2218, 'nvi', 'Êxodo', 'ex', 25, 23, '"Faça uma mesa de madeira de acácia com noventa centímetros de comprimento, quarenta e cinco centímetros de largura e setenta centímetros de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2219, 'nvi', 'Êxodo', 'ex', 25, 24, 'Revista-a de ouro puro e faça uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2220, 'nvi', 'Êxodo', 'ex', 25, 25, 'Faça também ao seu redor uma borda com a largura de quatro dedos e uma moldura de ouro para essa borda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2221, 'nvi', 'Êxodo', 'ex', 25, 26, 'Faça quatro argolas de ouro para a mesa e prenda-as nos quatro cantos dela, onde estão os seus quatro pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2222, 'nvi', 'Êxodo', 'ex', 25, 27, 'As argolas devem ser presas próximas da borda para que sustentem as varas usadas para carregar a mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2223, 'nvi', 'Êxodo', 'ex', 25, 28, 'Faça as varas de madeira de acácia, revestindo-as de ouro; com elas se carregará a mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2224, 'nvi', 'Êxodo', 'ex', 25, 29, 'Faça de ouro puro os seus pratos e o recipiente para incenso, as suas tigelas e as bacias nas quais se derramam as ofertas de bebidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2225, 'nvi', 'Êxodo', 'ex', 25, 30, 'Coloque sobre a mesa os pães da Presença, para que estejam sempre diante de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2226, 'nvi', 'Êxodo', 'ex', 25, 31, '"Faça um candelabro de ouro puro e batido. O pedestal, a haste, as taças, as flores e os botões do candelabro formarão com ele uma só peça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2227, 'nvi', 'Êxodo', 'ex', 25, 32, 'Seis braços sairão do candelabro: três de um lado e três do outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2228, 'nvi', 'Êxodo', 'ex', 25, 33, 'Haverá três taças com formato de flor de amêndoa num dos braços, cada uma com botão e flor, e três taças com formato de flor de amêndoa no braço seguinte, cada uma com botão e flor. Assim será com os seis braços que saem do candelabro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2229, 'nvi', 'Êxodo', 'ex', 25, 34, 'Na haste do candelabro haverá quatro taças com formato de flor de amêndoa, cada uma com botão e flor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2230, 'nvi', 'Êxodo', 'ex', 25, 35, 'Haverá um botão debaixo de cada par dos seis braços que saem do candelabro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2231, 'nvi', 'Êxodo', 'ex', 25, 36, 'Os braços com seus botões formarão uma só peça com o candelabro, tudo feito de ouro puro e batido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2232, 'nvi', 'Êxodo', 'ex', 25, 37, '"Faça-lhe também sete lâmpadas e coloque-as nele para que iluminem a frente dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2233, 'nvi', 'Êxodo', 'ex', 25, 38, 'Seus cortadores de pavio e seus apagadores serão de ouro puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2234, 'nvi', 'Êxodo', 'ex', 25, 39, 'Com trinta e cinco quilos de ouro puro faça o candelabro e todos esses utensílios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2235, 'nvi', 'Êxodo', 'ex', 25, 40, 'Tenha o cuidado de fazê-lo segundo o modelo que lhe foi mostrado no monte".');
