INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2273, 'nvi', 'Êxodo', 'ex', 27, 1, '"Faça um altar de madeira de acácia. Será quadrado, com dois metros e vinte e cinco centímetros de largura e um metro e trinta e cinco centímetros de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2274, 'nvi', 'Êxodo', 'ex', 27, 2, 'Faça uma ponta em forma de chifre em cada um dos quatro cantos, formando uma só peça com o altar, que será revestido de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2275, 'nvi', 'Êxodo', 'ex', 27, 3, 'Faça de bronze todos os seus utensílios: os recipientes para recolher cinzas, as pás, as bacias de aspersão, os garfos para carne e os braseiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2276, 'nvi', 'Êxodo', 'ex', 27, 4, 'Faça também para ele uma grelha de bronze em forma de rede e uma argola de bronze em cada um dos quatro cantos da grelha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2277, 'nvi', 'Êxodo', 'ex', 27, 5, 'Coloque-a abaixo da beirada do altar, de maneira que fique a meia altura do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2278, 'nvi', 'Êxodo', 'ex', 27, 6, 'Faça varas de madeira de acácia para o altar e revista-as de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2279, 'nvi', 'Êxodo', 'ex', 27, 7, 'Estas varas serão colocadas nas argolas, dos dois lados do altar, quando este for carregado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2280, 'nvi', 'Êxodo', 'ex', 27, 8, 'Faça o altar oco e de tábuas, conforme lhe foi mostrado no monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2281, 'nvi', 'Êxodo', 'ex', 27, 9, '"Faça um pátio para o tabernáculo. O lado sul terá quarenta e cinco metros de comprimento, e cortinas externas de linho fino trançado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2282, 'nvi', 'Êxodo', 'ex', 27, 10, 'com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2283, 'nvi', 'Êxodo', 'ex', 27, 11, 'O lado norte também terá quarenta e cinco metros de comprimento e cortinas externas, com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2284, 'nvi', 'Êxodo', 'ex', 27, 12, '"O lado ocidental, com as suas cortinas externas, terá vinte e dois metros e meio de largura, com dez colunas e dez bases.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2285, 'nvi', 'Êxodo', 'ex', 27, 13, 'O lado oriental, que dá para o nascente, também terá vinte e dois metros e meio de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2286, 'nvi', 'Êxodo', 'ex', 27, 14, 'Haverá cortinas de seis metros e setenta e cinco centímetros de comprimento num dos lados da entrada, com três colunas e três bases,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2287, 'nvi', 'Êxodo', 'ex', 27, 15, 'e cortinas externas de seis metros e setenta e cinco centímetros de comprimento no outro lado, também com três colunas e três bases.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2288, 'nvi', 'Êxodo', 'ex', 27, 16, '"Na entrada do pátio, haverá uma cortina de nove metros de comprimento, de linho fino trançado e de fios de tecido azul, roxo e vermelho, obra de bordador, com quatro colunas e quatro bases.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2289, 'nvi', 'Êxodo', 'ex', 27, 17, 'Todas as colunas ao redor do pátio terão ligaduras e ganchos de prata e bases de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2290, 'nvi', 'Êxodo', 'ex', 27, 18, 'O pátio terá quarenta e cinco metros de comprimento e vinte e dois metros e meio de largura, com cortinas de linho fino trançado de dois metros e vinte e cinco centímetros de altura e bases de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2291, 'nvi', 'Êxodo', 'ex', 27, 19, 'Todos os utensílios para o serviço do tabernáculo, inclusive todas as estacas da tenda e as do pátio, serão feitos de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2292, 'nvi', 'Êxodo', 'ex', 27, 20, '"Ordene aos israelitas que lhe tragam azeite puro de oliva batida para a iluminação, para que as lâmpadas fiquem sempre acesas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2293, 'nvi', 'Êxodo', 'ex', 27, 21, 'Na Tenda do Encontro, do lado de fora do véu que se encontra diante das tábuas da aliança, Arão e seus filhos manterão acesas as lâmpadas diante do Senhor, do entardecer até de manhã. Este será um decreto perpétuo entre os israelitas, geração após geração. "');
