INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2294, 'nvi', 'Êxodo', 'ex', 28, 1, '"Chame seu irmão Arão e separe-o dentre os israelitas, e também os seus filhos Nadabe e Abiú, Eleazar e Itamar, para que me sirvam como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2295, 'nvi', 'Êxodo', 'ex', 28, 2, 'Para o seu irmão Arão, faça vestes sagradas que lhe confiram dignidade e honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2296, 'nvi', 'Êxodo', 'ex', 28, 3, 'Diga a todos os homens capazes, aos quais dei habilidade, que façam vestes para a consagração de Arão, para que me sirva como sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2297, 'nvi', 'Êxodo', 'ex', 28, 4, 'São estas as vestes que farão: um peitoral, um colete sacerdotal, um manto, uma túnica bordada, um turbante e um cinturão. Para que o sacerdote Arão e seus filhos me sirvam como sacerdotes, eles farão essas vestes sagradas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2298, 'nvi', 'Êxodo', 'ex', 28, 5, 'e usarão linho fino, fios de ouro e fios de tecido azul, roxo e vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2299, 'nvi', 'Êxodo', 'ex', 28, 6, '"Faça o colete sacerdotal de linho fino trançado e de fios de ouro e de fios de tecido azul, roxo e vermelho, trabalho artesanal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2300, 'nvi', 'Êxodo', 'ex', 28, 7, 'Terá duas ombreiras atadas às suas duas extremidades para uni-lo bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2301, 'nvi', 'Êxodo', 'ex', 28, 8, 'O cinturão e o colete que por ele é preso serão feitos da mesma peça. O cinturão também será de linho fino trançado, de fios de ouro e de fios de tecido azul, roxo e vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2302, 'nvi', 'Êxodo', 'ex', 28, 9, '"Grave em duas pedras de ônix os nomes dos filhos de Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2303, 'nvi', 'Êxodo', 'ex', 28, 10, 'por ordem de nascimento: seis nomes numa pedra e seis na outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2304, 'nvi', 'Êxodo', 'ex', 28, 11, 'Grave os nomes dos filhos de Israel nas duas pedras como o lapidador grava um selo. Em seguida prenda-as com filigranas de ouro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2305, 'nvi', 'Êxodo', 'ex', 28, 12, 'costurando-as nas ombreiras do colete sacerdotal, como pedras memoriais para os filhos de Israel. Assim Arão levará os nomes em seus ombros como memorial diante do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2306, 'nvi', 'Êxodo', 'ex', 28, 13, 'Faça filigranas de ouro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2307, 'nvi', 'Êxodo', 'ex', 28, 14, 'e duas correntes de ouro puro, entrelaçadas como uma corda; e prenda as correntes às filigranas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2308, 'nvi', 'Êxodo', 'ex', 28, 15, '"Faça um peitoral de decisões, trabalho artesanal. Faça-o como o colete sacerdotal: de linho fino trançado e de fios de ouro e de fios de tecido azul, roxo e vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2309, 'nvi', 'Êxodo', 'ex', 28, 16, 'Será quadrado, com um palmo de comprimento e um palmo de largura, e dobrado em dois.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2310, 'nvi', 'Êxodo', 'ex', 28, 17, 'Em seguida, fixe nele quatro fileiras de pedras preciosas. Na primeira fileira haverá um rubi, um topázio e um berilo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2311, 'nvi', 'Êxodo', 'ex', 28, 18, 'na segunda, uma turquesa, uma safira e um diamante;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2312, 'nvi', 'Êxodo', 'ex', 28, 19, 'na terceira, um jacinto, uma ágata e uma ametista;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2313, 'nvi', 'Êxodo', 'ex', 28, 20, 'na quarta, um crisólito, um ônix e um jaspe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2314, 'nvi', 'Êxodo', 'ex', 28, 21, 'Serão doze pedras, uma para cada um dos nomes dos filhos de Israel, cada uma gravada como um selo, com o nome de uma das doze tribos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2315, 'nvi', 'Êxodo', 'ex', 28, 22, '"Faça para o peitoral correntes de ouro puro trançadas como cordas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2316, 'nvi', 'Êxodo', 'ex', 28, 23, 'Faça também duas argolas de ouro e prenda-as às duas extremidades do peitoral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2317, 'nvi', 'Êxodo', 'ex', 28, 24, 'Prenda as duas correntes de ouro às argolas nas extremidades do peitoral,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2318, 'nvi', 'Êxodo', 'ex', 28, 25, 'e as outras extremidades das correntes, às duas filigranas, unindo-as às peças das ombreiras do colete sacerdotal, na parte da frente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2319, 'nvi', 'Êxodo', 'ex', 28, 26, 'Faça outras duas argolas de ouro e prenda-as às outras duas extremidades do peitoral, na borda interna, próxima ao colete sacerdotal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2320, 'nvi', 'Êxodo', 'ex', 28, 27, 'Faça mais duas argolas de ouro e prenda-as na parte inferior das ombreiras, na frente do colete sacerdotal, próximas da costura, logo acima do cinturão do colete sacerdotal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2321, 'nvi', 'Êxodo', 'ex', 28, 28, 'As argolas do peitoral serão amarradas às argolas do colete com um cordão azul, ligando-o ao cinturão, para que o peitoral não se separe do colete sacerdotal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2322, 'nvi', 'Êxodo', 'ex', 28, 29, '"Toda vez que Arão entrar no Lugar Santo, levará os nomes dos filhos de Israel sobre o seu coração no peitoral de decisões, como memorial permanente perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2323, 'nvi', 'Êxodo', 'ex', 28, 30, 'Ponha também o Urim e o Tumim no peitoral das decisões, para que estejam sobre o coração de Arão sempre que ele entrar na presença do Senhor. Assim, Arão levará sempre sobre o coração, na presença do Senhor, os meios para tomar decisões em Israel. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2324, 'nvi', 'Êxodo', 'ex', 28, 31, '"Faça o manto do colete sacerdotal inteiramente de fios de tecido azul,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2325, 'nvi', 'Êxodo', 'ex', 28, 32, 'com uma abertura para a cabeça no centro. Ao redor dessa abertura haverá uma dobra tecida, como uma gola, para que não se rasgue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2326, 'nvi', 'Êxodo', 'ex', 28, 33, 'Faça romãs de fios de tecido azul, roxo e vermelho em volta da borda do manto, intercaladas com pequenos sinos de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2327, 'nvi', 'Êxodo', 'ex', 28, 34, 'Os sinos de ouro e as romãs se alternarão por toda a volta da borda do manto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2328, 'nvi', 'Êxodo', 'ex', 28, 35, 'Arão o vestirá quando ministrar. O som dos sinos será ouvido quando ele entrar no Lugar Santo diante do Senhor e quando sair, para que não morra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2329, 'nvi', 'Êxodo', 'ex', 28, 36, '"Faça um diadema de ouro puro e grave nele como se grava um selo: Consagrado ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2330, 'nvi', 'Êxodo', 'ex', 28, 37, 'Prenda-o na parte da frente do turbante com uma fita azul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2331, 'nvi', 'Êxodo', 'ex', 28, 38, 'Estará sobre a testa de Arão; assim ele levará a culpa de qualquer pecado que os israelitas cometerem em relação às coisas sagradas, ao fazerem todas as suas ofertas. Estará sempre sobre a testa de Arão, para que as ofertas sejam aceitas pelo Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2332, 'nvi', 'Êxodo', 'ex', 28, 39, '"Teça a túnica e o turbante com linho fino. O cinturão será feito por um bordador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2333, 'nvi', 'Êxodo', 'ex', 28, 40, 'Faça também túnicas, cinturões e gorros para os filhos de Arão, para conferir-lhes honra e dignidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2334, 'nvi', 'Êxodo', 'ex', 28, 41, 'Depois de vestir seu irmão Arão e os filhos dele, unja-os e consagre-os, para que me sirvam como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2335, 'nvi', 'Êxodo', 'ex', 28, 42, '"Faça-lhes calções de linho que vão da cintura até a coxa, para cobrirem a sua nudez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2336, 'nvi', 'Êxodo', 'ex', 28, 43, 'Arão e seus filhos terão que vesti-los sempre que entrarem na Tenda do Encontro ou quando se aproximarem do altar para ministrar no Lugar Santo, para que não incorram em culpa e morram. "Este é um decreto perpétuo para Arão e para os seus descendentes. "');
