INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2337, 'nvi', 'Êxodo', 'ex', 29, 1, '"Assim você os consagrará, para que me sirvam como sacerdotes: separe um novilho e dois cordeiros sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2338, 'nvi', 'Êxodo', 'ex', 29, 2, 'Com a melhor farinha de trigo, sem fermento, faça pães e bolos amassados com azeite, e pães finos, untados com azeite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2339, 'nvi', 'Êxodo', 'ex', 29, 3, 'Coloque-os numa cesta e ofereça-os dentro dela; também ofereça o novilho e os dois cordeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2340, 'nvi', 'Êxodo', 'ex', 29, 4, 'Depois traga Arão e seus filhos à entrada da Tenda do Encontro e mande-os se lavar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2341, 'nvi', 'Êxodo', 'ex', 29, 5, 'Pegue as vestes e vista Arão com a túnica e o peitoral. Prenda o colete sacerdotal sobre ele com o cinturão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2342, 'nvi', 'Êxodo', 'ex', 29, 6, 'Ponha-lhe o turbante na cabeça e prenda a coroa sagrada ao turbante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2343, 'nvi', 'Êxodo', 'ex', 29, 7, 'Unja-o com o óleo da unção, derramando-o sobre a cabeça de Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2344, 'nvi', 'Êxodo', 'ex', 29, 8, 'Traga os filhos dele, vista a cada um com uma túnica');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2345, 'nvi', 'Êxodo', 'ex', 29, 9, 'e um gorro na cabeça. Ponha também os cinturões em Arão e em seus filhos. O sacerdócio lhes pertence como ordenança perpétua. Assim você dedicará Arão e seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2346, 'nvi', 'Êxodo', 'ex', 29, 10, '"Traga o novilho para a frente da Tenda do Encontro. Arão e seus filhos colocarão as mãos sobre a cabeça do novilho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2347, 'nvi', 'Êxodo', 'ex', 29, 11, 'e você o sacrificará na presença do Senhor, defronte da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2348, 'nvi', 'Êxodo', 'ex', 29, 12, 'Com o dedo, coloque um pouco do sangue do novilho nas pontas do altar e derrame o resto do sangue na base do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2349, 'nvi', 'Êxodo', 'ex', 29, 13, 'Depois tire toda a gordura que cobre as vísceras, o lóbulo do fígado, e os dois rins com a gordura que os envolve, e queime-os no altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2350, 'nvi', 'Êxodo', 'ex', 29, 14, 'Mas queime a carne, o couro e o excremento do novilho fora do acampamento; é oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2351, 'nvi', 'Êxodo', 'ex', 29, 15, '"Separe um dos cordeiros sobre cuja cabeça Arão e seus filhos terão que colocar as mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2352, 'nvi', 'Êxodo', 'ex', 29, 16, 'Sacrifique-o, pegue o sangue e jogue-o nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2353, 'nvi', 'Êxodo', 'ex', 29, 17, 'Corte o cordeiro em pedaços, lave as vísceras e as pernas e coloque-as ao lado da cabeça e das outras partes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2354, 'nvi', 'Êxodo', 'ex', 29, 18, 'Depois queime o cordeiro inteiro sobre o altar; é holocausto dedicado ao Senhor; é oferta de aroma agradável dedicada ao Senhor, preparada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2355, 'nvi', 'Êxodo', 'ex', 29, 19, '"Pegue depois o outro cordeiro. Arão e seus filhos colocarão as mãos sobre a cabeça do animal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2356, 'nvi', 'Êxodo', 'ex', 29, 20, 'e você o sacrificará. Pegue do sangue e coloque-o na ponta da orelha direita de Arão e dos seus filhos, no polegar da mão direita e do pé direito de cada um deles. Depois derrame o resto do sangue nos lados do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2357, 'nvi', 'Êxodo', 'ex', 29, 21, 'Pegue, então, um pouco do sangue do altar e um pouco do óleo da unção, e faça aspersão com eles sobre Arão e suas vestes, sobre seus filhos e as vestes deles. Assim serão consagrados, ele e suas vestes, seus filhos e as vestes deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2358, 'nvi', 'Êxodo', 'ex', 29, 22, '"Tire desse cordeiro a gordura, a parte gorda da cauda, a gordura que cobre as vísceras, o lóbulo do fígado, os dois rins e a gordura que os envolve, e a coxa direita. Este é o cordeiro da oferta de ordenação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2359, 'nvi', 'Êxodo', 'ex', 29, 23, 'Tire da cesta de pães sem fermento um pão, um bolo assado com azeite e um pão fino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2360, 'nvi', 'Êxodo', 'ex', 29, 24, 'Coloque tudo nas mãos de Arão e de seus filhos, e apresente-os como oferta ritualmente movida perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2361, 'nvi', 'Êxodo', 'ex', 29, 25, 'Em seguida retome-o das mãos deles e queime os pães no altar com o holocausto de aroma agradável ao Senhor; é oferta dedicada ao Senhor, preparada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2362, 'nvi', 'Êxodo', 'ex', 29, 26, 'Tire o peito do cordeiro para a ordenação de Arão e mova-o perante o Senhor, como gesto ritual de apresentação; essa parte pertencerá a você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2363, 'nvi', 'Êxodo', 'ex', 29, 27, '"Consagre aquelas partes do cordeiro da ordenação que pertencem a Arão e a seus filhos: o peito e a coxa movidos como oferta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2364, 'nvi', 'Êxodo', 'ex', 29, 28, 'Essas partes sempre serão dadas pelos israelitas a Arão e a seus filhos. É a contribuição obrigatória que lhes farão, das suas ofertas de comunhão ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2365, 'nvi', 'Êxodo', 'ex', 29, 29, '"As vestes sagradas de Arão passarão aos seus descendentes, que, com elas serão ungidos e consagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2366, 'nvi', 'Êxodo', 'ex', 29, 30, 'O filho que o suceder como sacerdote e vier à Tenda do Encontro para ministrar no Lugar Santo terá que usá-las durante sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2367, 'nvi', 'Êxodo', 'ex', 29, 31, '"Pegue o cordeiro da ordenação e cozinhe a sua carne num lugar sagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2368, 'nvi', 'Êxodo', 'ex', 29, 32, 'À entrada da Tenda do Encontro, Arão e seus filhos deverão comer a carne do cordeiro e o pão que está na cesta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2369, 'nvi', 'Êxodo', 'ex', 29, 33, 'Eles comerão dessas ofertas com as quais se fez propiciação para sua ordenação e consagração; somente os sacerdotes poderão comê-las, pois são sagradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2370, 'nvi', 'Êxodo', 'ex', 29, 34, 'Se sobrar da carne do cordeiro da ordenação ou do pão até a manhã seguinte, queime a sobra. Não se deve comê-la, visto que é sagrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2371, 'nvi', 'Êxodo', 'ex', 29, 35, '"Para a ordenação de Arão e seus filhos, faça durante sete dias tudo o que lhe mandei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2372, 'nvi', 'Êxodo', 'ex', 29, 36, 'Sacrifique um novilho por dia como oferta pelo pecado para fazer propiciação. Purifique o altar, fazendo propiciação por ele, e unja-o para consagrá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2373, 'nvi', 'Êxodo', 'ex', 29, 37, 'Durante sete dias faça propiciação pelo altar, consagrando-o. Então o altar será santíssimo, e tudo o que nele tocar será santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2374, 'nvi', 'Êxodo', 'ex', 29, 38, '"Eis o que você terá que sacrificar regularmente sobre o altar: a cada dia dois cordeiros de um ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2375, 'nvi', 'Êxodo', 'ex', 29, 39, 'Ofereça um de manhã e o outro ao entardecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2376, 'nvi', 'Êxodo', 'ex', 29, 40, 'Com o primeiro cordeiro ofereça um jarro da melhor farinha misturada com um litro de azeite de olivas moídas, e um litro de vinho como oferta derramada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2377, 'nvi', 'Êxodo', 'ex', 29, 41, 'Ofereça o outro cordeiro ao entardecer com uma oferta de cereal e uma oferta derramada, como de manhã. É oferta de aroma agradável ao Senhor, preparada no fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2378, 'nvi', 'Êxodo', 'ex', 29, 42, '"De geração em geração esse holocausto deverá ser feito regularmente à entrada da Tenda do Encontro, diante do Senhor. Nesse local eu os encontrarei e falarei com você;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2379, 'nvi', 'Êxodo', 'ex', 29, 43, 'ali me encontrarei com os israelitas, e o lugar será consagrado pela minha glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2380, 'nvi', 'Êxodo', 'ex', 29, 44, '"Assim consagrarei a Tenda do Encontro e o altar, e consagrarei também Arão e seus filhos para me servirem como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2381, 'nvi', 'Êxodo', 'ex', 29, 45, 'E habitarei no meio dos israelitas e lhes serei o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2382, 'nvi', 'Êxodo', 'ex', 29, 46, 'Saberão que eu sou o Senhor, o seu Deus, que os tirou do Egito para habitar no meio deles. Eu sou o Senhor, o seu Deus. "');
