INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1580, 'nvi', 'Êxodo', 'ex', 3, 1, 'Moisés pastoreava o rebanho de seu sogro Jetro, que era sacerdote de Midiã. Um dia levou o rebanho para o outro lado do deserto e chegou a Horebe, o monte de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1581, 'nvi', 'Êxodo', 'ex', 3, 2, 'Ali o Anjo do Senhor lhe apareceu numa chama de fogo que saía do meio de uma sarça. Moisés viu que, embora a sarça estivesse em chamas, esta não era consumida pelo fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1582, 'nvi', 'Êxodo', 'ex', 3, 3, '"Que impressionante! ", pensou. "Por que a sarça não se queima? Vou ver isso de perto. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1583, 'nvi', 'Êxodo', 'ex', 3, 4, 'O Senhor viu que ele se aproximava para observar. E então, do meio da sarça Deus o chamou: "Moisés, Moisés! " "Eis-me aqui", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1584, 'nvi', 'Êxodo', 'ex', 3, 5, 'Então disse Deus: "Não se aproxime. Tire as sandálias dos pés, pois o lugar em que você está é terra santa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1585, 'nvi', 'Êxodo', 'ex', 3, 6, 'Disse ainda: "Eu sou o Deus de seu pai, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó". Então Moisés cobriu o rosto, pois teve medo de olhar para Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1586, 'nvi', 'Êxodo', 'ex', 3, 7, 'Disse o Senhor: "De fato tenho visto a opressão sobre o meu povo no Egito, e também tenho escutado o seu clamor, por causa dos seus feitores, e sei quanto eles estão sofrendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1587, 'nvi', 'Êxodo', 'ex', 3, 8, 'Por isso desci para livrá-lo das mãos dos egípcios e tirá-los daqui para uma terra boa e vasta, onde manam leite e mel: a terra dos cananeus, dos hititas, dos amorreus, dos ferezeus, dos heveus e dos jebuseus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1588, 'nvi', 'Êxodo', 'ex', 3, 9, 'Pois agora o clamor dos israelitas chegou a mim, e tenho visto como os egípcios os oprimem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1589, 'nvi', 'Êxodo', 'ex', 3, 10, 'Vá, pois, agora; eu o envio ao faraó para tirar do Egito o meu povo, os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1590, 'nvi', 'Êxodo', 'ex', 3, 11, 'Moisés, porém, respondeu a Deus: "Quem sou eu para apresentar-me ao faraó e tirar os israelitas do Egito? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1591, 'nvi', 'Êxodo', 'ex', 3, 12, 'Deus afirmou: "Eu estarei com você. Esta é a prova de que sou eu quem o envia: quando você tirar o povo do Egito, vocês prestarão culto a Deus neste monte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1592, 'nvi', 'Êxodo', 'ex', 3, 13, 'Moisés perguntou: "Quando eu chegar diante dos israelitas e lhes disser: O Deus dos seus antepassados me enviou a vocês, e eles me perguntarem: ‘Qual é o nome dele? ’ Que lhes direi? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1593, 'nvi', 'Êxodo', 'ex', 3, 14, 'Disse Deus a Moisés: "Eu Sou o que Sou. É isto que você dirá aos israelitas: Eu Sou me enviou a vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1594, 'nvi', 'Êxodo', 'ex', 3, 15, 'Disse também Deus a Moisés: "Diga aos israelitas: O Senhor, o Deus dos seus antepassados, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó, enviou-me a vocês. Esse é o meu nome para sempre, nome pelo qual serei lembrado de geração em geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1595, 'nvi', 'Êxodo', 'ex', 3, 16, '"Vá, reúna as autoridades de Israel e diga-lhes: O Senhor, o Deus dos seus antepassados, o Deus de Abraão, de Isaque e de Jacó, apareceu a mim e disse: Eu virei em auxílio de vocês; pois vi o que lhes tem sido feito no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1596, 'nvi', 'Êxodo', 'ex', 3, 17, 'Prometi tirá-los da opressão do Egito para a terra dos cananeus, dos hititas, dos amorreus, dos ferezeus, dos heveus e dos jebuseus, terra onde manam leite e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1597, 'nvi', 'Êxodo', 'ex', 3, 18, '"As autoridades de Israel o atenderão. Depois você irá com elas ao rei do Egito e lhe dirá: O Senhor, o Deus dos hebreus, veio ao nosso encontro. Agora, deixe-nos fazer uma caminhada de três dias, adentrando o deserto, para oferecermos sacrifícios ao Senhor nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1598, 'nvi', 'Êxodo', 'ex', 3, 19, 'Eu sei que o rei do Egito não os deixará sair, a não ser que uma poderosa mão o force.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1599, 'nvi', 'Êxodo', 'ex', 3, 20, 'Por isso estenderei a minha mão e ferirei os egípcios com todas as maravilhas que realizarei no meio deles. Depois disso ele os deixará sair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1600, 'nvi', 'Êxodo', 'ex', 3, 21, '"E farei que os egípcios tenham boa-vontade para com o povo, de modo que, quando vocês saírem, não sairão de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1601, 'nvi', 'Êxodo', 'ex', 3, 22, 'Todas as israelitas pedirão às suas vizinhas, e às mulheres que estiverem hospedando em casa, objetos de prata e de ouro, e roupas, que vocês porão em seus filhos e em suas filhas. Assim vocês despojarão os egípcios".');
