INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2383, 'nvi', 'Êxodo', 'ex', 30, 1, '"Faça um altar de madeira de acácia para queimar incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2384, 'nvi', 'Êxodo', 'ex', 30, 2, 'Será quadrado, com quarenta e cinco centímetros de cada lado e noventa centímetros de altura; suas pontas formarão com ele uma só peça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2385, 'nvi', 'Êxodo', 'ex', 30, 3, 'Revista de ouro puro a parte superior, todos os lados e as pontas, e faça uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2386, 'nvi', 'Êxodo', 'ex', 30, 4, 'Faça duas argolas de ouro de cada lado do altar, abaixo da moldura, que sustentem as varas utilizadas para carregá-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2387, 'nvi', 'Êxodo', 'ex', 30, 5, 'e use madeira de acácia para fazer as varas e revista-as de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2388, 'nvi', 'Êxodo', 'ex', 30, 6, 'Coloque o altar em frente do véu que se encontra diante da arca da aliança, diante da tampa que está sobre ele, onde me encontrarei com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2389, 'nvi', 'Êxodo', 'ex', 30, 7, '"Arão queimará incenso aromático sobre o altar todas as manhãs, quando vier cuidar das lâmpadas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2390, 'nvi', 'Êxodo', 'ex', 30, 8, 'e também quando acendê-las ao entardecer. Será queimado incenso continuamente perante o Senhor, pelas suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2391, 'nvi', 'Êxodo', 'ex', 30, 9, 'Não ofereçam nesse altar nenhum outro tipo de incenso nem holocausto nem oferta de cereal nem derramem sobre ele ofertas de bebidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2392, 'nvi', 'Êxodo', 'ex', 30, 10, 'Uma vez por ano, Arão fará propiciação sobre as pontas do altar. Essa propiciação anual será realizada com o sangue da oferta para propiciação pelo pecado, geração após geração. Esse altar é santíssimo ao Senhor. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2393, 'nvi', 'Êxodo', 'ex', 30, 11, 'Disse então o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2394, 'nvi', 'Êxodo', 'ex', 30, 12, '"Quando você fizer o recenseamento dos israelitas, cada um deles terá que pagar ao Senhor um preço pelo resgate por sua vida quando for contado. Dessa forma nenhuma praga virá sobre eles quando você os contar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2395, 'nvi', 'Êxodo', 'ex', 30, 13, 'Cada recenseado contribuirá com seis gramas, com base no peso padrão do santuário, que tem doze gramas. As seis gramas são uma oferta ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2396, 'nvi', 'Êxodo', 'ex', 30, 14, 'Todos os alistados, da idade de vinte anos para cima, darão ao Senhor essa oferta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2397, 'nvi', 'Êxodo', 'ex', 30, 15, 'Os ricos não contribuirão com mais, nem os pobres darão menos que seis gramas, quando apresentarem a oferta ao Senhor como propiciação por suas vidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2398, 'nvi', 'Êxodo', 'ex', 30, 16, 'Receba dos israelitas o preço da propiciação e use-o para o serviço da Tenda do Encontro. Será um memorial perante o Senhor em favor dos israelitas, para fazerem propiciação por suas vidas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2399, 'nvi', 'Êxodo', 'ex', 30, 17, 'Disse então o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2400, 'nvi', 'Êxodo', 'ex', 30, 18, '"Faça uma bacia de bronze com uma base de bronze, para se lavarem. Coloque-a entre a Tenda do Encontro e o altar, e mande enchê-la de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2401, 'nvi', 'Êxodo', 'ex', 30, 19, 'Arão e seus filhos lavarão as mãos e os pés com a água da bacia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2402, 'nvi', 'Êxodo', 'ex', 30, 20, 'Toda vez que entrarem na Tenda do Encontro, terão que lavar-se com água, para que não morram. Quando também se aproximarem do altar para ministrar ao Senhor, apresentando uma oferta preparada no fogo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2403, 'nvi', 'Êxodo', 'ex', 30, 21, 'lavarão as mãos e os pés para que não morram. Esse é um decreto perpétuo, para Arão e os seus descendentes, geração após geração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2404, 'nvi', 'Êxodo', 'ex', 30, 22, 'Em seguida o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2405, 'nvi', 'Êxodo', 'ex', 30, 23, '"Junte as seguintes especiarias: seis quilos de mirra líquida, a metade disso, ou seja, três quilos de canela, três quilos de cana aromática,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2406, 'nvi', 'Êxodo', 'ex', 30, 24, 'seis quilos de cássia, com base no peso padrão do santuário, e um galão de azeite de oliva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2407, 'nvi', 'Êxodo', 'ex', 30, 25, 'Faça com eles o óleo sagrado para as unções, uma mistura de aromas, obra de perfumista. Este será o óleo sagrado para as unções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2408, 'nvi', 'Êxodo', 'ex', 30, 26, 'Use-o para ungir a Tenda do Encontro, a arca da aliança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2409, 'nvi', 'Êxodo', 'ex', 30, 27, 'a mesa e todos os seus utensílios, o candelabro e os seus utensílios, o altar do incenso,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2410, 'nvi', 'Êxodo', 'ex', 30, 28, 'o altar do holocausto e todos os seus utensílios, e a bacia com a sua base.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2411, 'nvi', 'Êxodo', 'ex', 30, 29, 'Você os consagrará e serão santíssimos, e tudo o que neles tocar se tornará santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2412, 'nvi', 'Êxodo', 'ex', 30, 30, '"Unja Arão e seus filhos e consagre-os para que me sirvam como sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2413, 'nvi', 'Êxodo', 'ex', 30, 31, 'Diga aos israelitas: Este será o meu óleo sagrado para as unções, geração após geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2414, 'nvi', 'Êxodo', 'ex', 30, 32, 'Não o derramem sobre nenhum outro homem, e não façam nenhum outro óleo com a mesma composição. É óleo sagrado, e assim vocês devem considerá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2415, 'nvi', 'Êxodo', 'ex', 30, 33, 'Quem fizer óleo como esse ou usá-lo em alguém que não seja sacerdote, será eliminado do meio do seu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2416, 'nvi', 'Êxodo', 'ex', 30, 34, 'Disse ainda o Senhor a Moisés: "Junte as seguintes essências: bálsamo, ônica, gálbano e incenso puro, todos em quantidades iguais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2417, 'nvi', 'Êxodo', 'ex', 30, 35, 'e faça um incenso de mistura aromática, obra de perfumista. Levará sal, será puro e santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2418, 'nvi', 'Êxodo', 'ex', 30, 36, 'Moa parte dele, até virar pó, e coloque-o diante das tábuas da aliança, na Tenda do Encontro, onde me encontrarei com você. O incenso lhes será santíssimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2419, 'nvi', 'Êxodo', 'ex', 30, 37, 'Não façam nenhum outro incenso com a mesma composição para uso pessoal; considerem-no sagrado, reservado para o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2420, 'nvi', 'Êxodo', 'ex', 30, 38, 'Quem fizer um incenso semelhante, para usufruir sua fragrância, será eliminado do seu povo".');
