INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2439, 'nvi', 'Êxodo', 'ex', 32, 1, 'O povo, ao ver que Moisés demorava a descer do monte, juntou-se ao redor de Arão e lhe disse: "Venha, faça para nós deuses que nos conduzam, pois a esse Moisés, o homem que nos tirou do Egito, não sabemos o que lhe aconteceu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2440, 'nvi', 'Êxodo', 'ex', 32, 2, 'Respondeu-lhes Arão: "Tirem os brincos de ouro de suas mulheres, de seus filhos e de suas filhas e tragam-nos a mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2441, 'nvi', 'Êxodo', 'ex', 32, 3, 'Todos tiraram os seus brincos de ouro e os levaram a Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2442, 'nvi', 'Êxodo', 'ex', 32, 4, 'Ele os recebeu e os fundiu, transformando tudo num ídolo, que modelou com uma ferramenta própria, dando-lhe a forma de um bezerro. Então disseram: "Eis aí os seus deuses, ó Israel, que tiraram vocês do Egito! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2443, 'nvi', 'Êxodo', 'ex', 32, 5, 'Vendo isso, Arão edificou um altar diante do bezerro e anunciou: "Amanhã haverá uma festa dedicada ao Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2444, 'nvi', 'Êxodo', 'ex', 32, 6, 'Na manhã seguinte, ofereceram holocaustos e sacrifícios de comunhão. O povo se assentou para comer e beber, e levantou-se para se entregar à farra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2445, 'nvi', 'Êxodo', 'ex', 32, 7, 'Então o Senhor disse a Moisés: "Desça, porque o seu povo, que você tirou do Egito, corrompeu-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2446, 'nvi', 'Êxodo', 'ex', 32, 8, 'Muito depressa se desviaram daquilo que lhes ordenei e fizeram um ídolo em forma de bezerro, curvaram-se diante dele, ofereceram-lhe sacrifícios, e disseram: ‘Eis aí, ó Israel, os seus deuses que tiraram vocês do Egito’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2447, 'nvi', 'Êxodo', 'ex', 32, 9, 'Disse o Senhor a Moisés: "Tenho visto que este povo é um povo obstinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2448, 'nvi', 'Êxodo', 'ex', 32, 10, 'Deixe-me agora, para que a minha ira se acenda contra eles, e eu os destrua. Depois farei de você uma grande nação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2449, 'nvi', 'Êxodo', 'ex', 32, 11, 'Moisés, porém, suplicou ao Senhor, o seu Deus, clamando: "Ó Senhor, por que se acenderia a tua ira contra o teu povo, que tiraste do Egito com grande poder e forte mão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2450, 'nvi', 'Êxodo', 'ex', 32, 12, 'Por que diriam os egípcios: ‘Foi com intenção maligna que ele os libertou, para matá-los nos montes e bani-los da face da terra’? Arrepende-te do fogo da tua ira! Tem piedade, e não tragas este mal sobre o teu povo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2451, 'nvi', 'Êxodo', 'ex', 32, 13, 'Lembra-te dos teus servos Abraão, Isaque e Israel, aos quais juraste por ti mesmo: ‘Farei que os seus descendentes sejam numerosos como as estrelas do céu e lhes darei toda esta terra que lhes prometi, que será a sua herança para sempre’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2452, 'nvi', 'Êxodo', 'ex', 32, 14, 'E sucedeu que o Senhor arrependeu-se do mal que ameaçara trazer sobre o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2453, 'nvi', 'Êxodo', 'ex', 32, 15, 'Então Moisés desceu do monte, levando nas mãos as duas tábuas da aliança; estavam escritas em ambos os lados, frente e verso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2454, 'nvi', 'Êxodo', 'ex', 32, 16, 'As tábuas tinham sido feitas por Deus; o que nelas estava gravado fora escrito por Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2455, 'nvi', 'Êxodo', 'ex', 32, 17, 'Quando Josué ouviu o barulho do povo gritando, disse a Moisés: "Há barulho de guerra no acampamento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2456, 'nvi', 'Êxodo', 'ex', 32, 18, 'Respondeu Moisés: "Não é canto de vitória, nem canto de derrota; mas ouço o som de canções! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2457, 'nvi', 'Êxodo', 'ex', 32, 19, 'Quando Moisés aproximou-se do acampamento e viu o bezerro e as danças, irou-se e jogou as tábuas no chão, ao pé do monte, quebrando-as.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2458, 'nvi', 'Êxodo', 'ex', 32, 20, 'Pegou o bezerro que eles tinham feito e o destruiu no fogo; depois de moê-lo até virar pó, espalhou-o na água e fez com que os israelitas a bebessem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2459, 'nvi', 'Êxodo', 'ex', 32, 21, 'E perguntou a Arão: "Que lhe fez esse povo para que você o levasse a tão grande pecado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2460, 'nvi', 'Êxodo', 'ex', 32, 22, 'Respondeu Arão: "Não te enfureças, meu senhor; tu bem sabes como esse povo é propenso para o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2461, 'nvi', 'Êxodo', 'ex', 32, 23, 'Eles me disseram: ‘Faça para nós deuses que nos conduzam, pois esse Moisés, o homem que nos tirou do Egito, não sabemos o que lhe aconteceu’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2462, 'nvi', 'Êxodo', 'ex', 32, 24, 'Então eu lhes disse: Quem tiver enfeites de ouro, traga-os para mim. O povo trouxe-me o ouro, eu o joguei no fogo e surgiu esse bezerro! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2463, 'nvi', 'Êxodo', 'ex', 32, 25, 'Moisés viu que o povo estava desenfreado e que Arão o tinha deixado fora de controle, tendo se tornado motivo de riso para os seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2464, 'nvi', 'Êxodo', 'ex', 32, 26, 'Então ficou em pé, à entrada do acampamento, e disse: "Quem é pelo Senhor, junte-se a mim". Todos os levitas se juntaram a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2465, 'nvi', 'Êxodo', 'ex', 32, 27, 'Declarou-lhes também: "Assim diz o Senhor, o Deus de Israel: ‘Pegue cada um sua espada, percorra o acampamento, de tenda em tenda, e mate o seu irmão, o seu amigo e o seu vizinho’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2466, 'nvi', 'Êxodo', 'ex', 32, 28, 'Fizeram os levitas conforme Moisés ordenou, e naquele dia morreram cerca de três mil dentre o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2467, 'nvi', 'Êxodo', 'ex', 32, 29, 'Disse então Moisés: "Hoje vocês se consagraram ao Senhor, pois nenhum de vocês poupou o seu filho e o seu irmão, de modo que o Senhor os abençoou neste dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2468, 'nvi', 'Êxodo', 'ex', 32, 30, 'No dia seguinte Moisés disse ao povo: "Vocês cometeram um grande pecado. Mas agora subirei ao Senhor, e talvez possa oferecer propiciação pelo pecado de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2469, 'nvi', 'Êxodo', 'ex', 32, 31, 'Assim, Moisés voltou ao Senhor e disse: "Ah, que grande pecado cometeu este povo! Fizeram para si um deus de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2470, 'nvi', 'Êxodo', 'ex', 32, 32, 'Mas agora, eu te rogo, perdoa-lhes o pecado; se não, risca-me do teu livro que escreveste".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2471, 'nvi', 'Êxodo', 'ex', 32, 33, 'Respondeu o Senhor a Moisés: "Riscarei do meu livro todo aquele que pecar contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2472, 'nvi', 'Êxodo', 'ex', 32, 34, 'Agora vá, guie o povo ao lugar de que lhe falei, e meu anjo irá à sua frente. Todavia, quando chegar a hora de puni-los, eu os punirei pelos pecados deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2473, 'nvi', 'Êxodo', 'ex', 32, 35, 'E o Senhor feriu o povo com uma praga porque quiseram que Arão fizesse o bezerro.');
