INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2497, 'nvi', 'Êxodo', 'ex', 34, 1, 'Disse o Senhor a Moisés: "Talhe duas tábuas de pedra semelhantes às primeiras, e nelas escreverei as palavras que estavam nas primeiras tábuas que você quebrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2498, 'nvi', 'Êxodo', 'ex', 34, 2, 'Esteja pronto pela manhã para subir ao monte Sinai. E lá mesmo, no alto do monte, apresente-se a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2499, 'nvi', 'Êxodo', 'ex', 34, 3, 'Ninguém poderá ir com você nem ficar em lugar algum do monte; nem mesmo ovelhas e bois deverão pastar diante do monte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2500, 'nvi', 'Êxodo', 'ex', 34, 4, 'Assim Moisés lavrou duas tábuas de pedra semelhantes às primeiras e subiu ao monte Sinai, logo de manhã, como o Senhor lhe havia ordenado, levando nas mãos as duas tábuas de pedra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2501, 'nvi', 'Êxodo', 'ex', 34, 5, 'Então o Senhor desceu na nuvem, permaneceu ali com ele e proclamou o seu nome: o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2502, 'nvi', 'Êxodo', 'ex', 34, 6, 'E passou diante de Moisés, proclamando: "Senhor, Senhor, Deus compassivo e misericordioso, paciente, cheio de amor e de fidelidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2503, 'nvi', 'Êxodo', 'ex', 34, 7, 'que mantém o seu amor a milhares e perdoa a maldade, a rebelião e o pecado. Contudo, não deixa de punir o culpado; castiga os filhos e os netos pelo pecado de seus pais, até a terceira e a quarta gerações".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2504, 'nvi', 'Êxodo', 'ex', 34, 8, 'Imediatamente Moisés prostrou-se, rosto em terra, e o adorou, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2505, 'nvi', 'Êxodo', 'ex', 34, 9, '"Senhor, se de fato me aceitas com agrado, acompanha-nos o Senhor. Mesmo sendo esse um povo obstinado, perdoa a nossa maldade e o nosso pecado e faze de nós a tua herança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2506, 'nvi', 'Êxodo', 'ex', 34, 10, '"Faço com vocês uma aliança", disse o Senhor. "Diante de todo o seu povo farei maravilhas jamais realizadas na presença de nenhum outro povo do mundo. O povo no meio do qual você habita verá a obra maravilhosa que eu, o Senhor, farei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2507, 'nvi', 'Êxodo', 'ex', 34, 11, 'Obedeça às ordens que hoje lhe dou. Expulsarei de diante de você os amorreus, os cananeus, os hititas, os ferezeus, os heveus e os jebuseus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2508, 'nvi', 'Êxodo', 'ex', 34, 12, 'Acautele-se para não fazer acordo com aqueles que vivem na terra para a qual você está indo, pois eles se tornariam uma armadilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2509, 'nvi', 'Êxodo', 'ex', 34, 13, 'Pelo contrário, derrube os altares deles, quebre as suas colunas sagradas e corte os seus postes sagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2510, 'nvi', 'Êxodo', 'ex', 34, 14, 'Nunca adore nenhum outro deus, porque o Senhor, cujo nome é Zeloso, é de fato Deus zeloso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2511, 'nvi', 'Êxodo', 'ex', 34, 15, '"Acautele-se para não fazer acordo com aqueles que já vivem na terra; pois quando eles se prostituírem, seguindo aos seus deuses e lhes oferecerem sacrifícios, convidarão você e poderão levá-lo a comer dos seus sacrifícios');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2512, 'nvi', 'Êxodo', 'ex', 34, 16, 'e escolher para os seus filhos mulheres dentre as filhas deles. Quando elas se prostituírem, seguindo aos seus deuses, poderão levar os seus filhos a se prostituírem também.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2513, 'nvi', 'Êxodo', 'ex', 34, 17, '"Não faça ídolos de metal para você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2514, 'nvi', 'Êxodo', 'ex', 34, 18, '"Celebre a festa dos pães sem fermento. Durante sete dias coma pão sem fermento, como lhe ordenei. Faça isso no tempo certo, no mês de abibe, porquanto naquele mês você saiu do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2515, 'nvi', 'Êxodo', 'ex', 34, 19, '"O primeiro a nascer de cada ventre me pertence, todos os machos dentre as primeiras crias dos seus rebanhos: bezerros, cordeiros e cabritos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2516, 'nvi', 'Êxodo', 'ex', 34, 20, 'Resgate com um cordeiro cada primeiro jumentinho que nascer; mas se não o resgatar, quebre-lhe o pescoço. Resgate todos os seus primogênitos. "Ninguém compareça perante mim de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2517, 'nvi', 'Êxodo', 'ex', 34, 21, '"Trabalhe seis dias, mas descanse no sétimo; tanto na época de arar como na da colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2518, 'nvi', 'Êxodo', 'ex', 34, 22, '"Celebre a festa das semanas, na ocasião dos primeiros frutos da colheita do trigo, e a festa do encerramento da colheita, no fim do ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2519, 'nvi', 'Êxodo', 'ex', 34, 23, 'Três vezes por ano todos os homens do seu povo comparecerão diante do Soberano Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2520, 'nvi', 'Êxodo', 'ex', 34, 24, 'Expulsarei nações de diante de você e ampliarei o seu território. Quando você subir três vezes por ano para apresentar-se ao Senhor, o seu Deus, ninguém cobiçará a sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2521, 'nvi', 'Êxodo', 'ex', 34, 25, '"Não me ofereça o sangue de nenhum sacrifício misturado com algo fermentado, e não deixe sobra alguma do sacrifício da festa da Páscoa até a manhã seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2522, 'nvi', 'Êxodo', 'ex', 34, 26, '"Traga o melhor dos primeiros frutos da terra ao santuário do Senhor, o seu Deus. "Não cozinhe o cabrito no leite da própria mãe. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2523, 'nvi', 'Êxodo', 'ex', 34, 27, 'Disse o Senhor a Moisés: "Escreva essas palavras; porque é de acordo com elas que faço aliança com você e com Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2524, 'nvi', 'Êxodo', 'ex', 34, 28, 'Moisés ficou ali com o Senhor quarenta dias e quarenta noites, sem comer pão e sem beber água. E escreveu nas tábuas as palavras da aliança: os Dez Mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2525, 'nvi', 'Êxodo', 'ex', 34, 29, 'Ao descer do monte Sinai com as duas tábuas da aliança nas mãos, Moisés não sabia que o seu rosto resplandecia por ter conversado com o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2526, 'nvi', 'Êxodo', 'ex', 34, 30, 'Quando Arão e todos os israelitas viram Moisés, com o rosto resplandecente, tiveram medo de aproximar-se dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2527, 'nvi', 'Êxodo', 'ex', 34, 31, 'Ele, porém, os chamou; Arão e os líderes da comunidade atenderam, e Moisés falou com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2528, 'nvi', 'Êxodo', 'ex', 34, 32, 'Depois, todos os israelitas se aproximaram, e ele lhes transmitiu todos os mandamentos que o Senhor lhe tinha dado no monte Sinai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2529, 'nvi', 'Êxodo', 'ex', 34, 33, 'Quando acabou de falar com eles, cobriu o rosto com um véu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2530, 'nvi', 'Êxodo', 'ex', 34, 34, 'Mas toda vez que entrava para estar na presença do Senhor e falar com ele, tirava o véu até sair. Sempre que saía e contava aos israelitas tudo o que lhe havia sido ordenado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2531, 'nvi', 'Êxodo', 'ex', 34, 35, 'eles viam que o seu rosto resplandecia. Então, de novo Moisés cobria o rosto com o véu até entrar de novo para falar com o Senhor.');
