INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2532, 'nvi', 'Êxodo', 'ex', 35, 1, 'Moisés reuniu toda a comunidade de Israel e lhes disse: "Estas são as coisas que o Senhor os mandou fazer:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2533, 'nvi', 'Êxodo', 'ex', 35, 2, 'Em seis dias qualquer trabalho poderá ser feito, mas o sétimo dia lhes será santo, um sábado de descanso consagrado ao Senhor. Todo aquele que trabalhar nesse dia terá que ser morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2534, 'nvi', 'Êxodo', 'ex', 35, 3, 'Nem sequer acendam fogo em nenhuma de suas casas no dia de sábado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2535, 'nvi', 'Êxodo', 'ex', 35, 4, 'Disse Moisés a toda a comunidade de Israel: "Foi isto que o Senhor ordenou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2536, 'nvi', 'Êxodo', 'ex', 35, 5, 'Separem dentre os seus bens uma oferta para o Senhor. Todo aquele que, de coração, estiver disposto, trará como oferta ao Senhor ouro, prata e bronze;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2537, 'nvi', 'Êxodo', 'ex', 35, 6, 'fios de tecido azul, roxo e vermelho; linho fino e pêlos de cabra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2538, 'nvi', 'Êxodo', 'ex', 35, 7, 'peles de carneiro tingidas de vermelho e couro; madeira de acácia;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2539, 'nvi', 'Êxodo', 'ex', 35, 8, 'óleo para a iluminação; especiarias para o óleo da unção e para o incenso aromático;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2540, 'nvi', 'Êxodo', 'ex', 35, 9, 'pedras de ônix e outras pedras preciosas para serem encravadas no colete sacerdotal e no peitoral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2541, 'nvi', 'Êxodo', 'ex', 35, 10, '"Todos os que dentre vocês forem capazes virão fazer tudo quanto o Senhor ordenou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2542, 'nvi', 'Êxodo', 'ex', 35, 11, 'o tabernáculo com sua tenda e sua cobertura, os ganchos, as armações, os travessões, as colunas e as bases;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2543, 'nvi', 'Êxodo', 'ex', 35, 12, 'a arca com suas varas, a tampa e o véu que a protege;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2544, 'nvi', 'Êxodo', 'ex', 35, 13, 'a mesa com suas varas e todos os seus utensílios, e os pães da Presença;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2545, 'nvi', 'Êxodo', 'ex', 35, 14, 'o candelabro com seus utensílios, as lâmpadas e o óleo para iluminação;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2546, 'nvi', 'Êxodo', 'ex', 35, 15, 'o altar do incenso com suas varas, o óleo da unção e o incenso aromático; a cortina divisória à entrada do tabernáculo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2547, 'nvi', 'Êxodo', 'ex', 35, 16, 'o altar de holocaustos com sua grelha de bronze, suas varas e todos os seus utensílios; a bacia de bronze e sua base;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2548, 'nvi', 'Êxodo', 'ex', 35, 17, 'as cortinas externas do pátio com suas colunas e bases, e a cortina da entrada para o pátio;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2549, 'nvi', 'Êxodo', 'ex', 35, 18, 'as estacas do tabernáculo e do pátio e suas cordas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2550, 'nvi', 'Êxodo', 'ex', 35, 19, 'as vestes litúrgicas para ministrar no Lugar Santo, tanto as vestes sagradas de Arão, o sacerdote, como as vestes de seus filhos, para quando servirem como sacerdotes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2551, 'nvi', 'Êxodo', 'ex', 35, 20, 'Então toda a comunidade de Israel saiu da presença de Moisés,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2552, 'nvi', 'Êxodo', 'ex', 35, 21, 'e todos os que estavam dispostos, cujo coração os impeliu a isso, trouxeram uma oferta ao Senhor para a obra na Tenda do Encontro, para todos os seus serviços e para as vestes sagradas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2553, 'nvi', 'Êxodo', 'ex', 35, 22, 'Todos os que se dispuseram, tanto homens como mulheres, trouxeram jóias de ouro de todos os tipos: broches, brincos, anéis e ornamentos; e apresentaram seus objetos de ouro como oferta ritualmente movida perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2554, 'nvi', 'Êxodo', 'ex', 35, 23, 'Todos os que possuíam fios de tecido azul, roxo e vermelho, ou linho fino, ou pêlos de cabra, peles de carneiro tingidas de vermelho ou couro, trouxeram-nos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2555, 'nvi', 'Êxodo', 'ex', 35, 24, 'Aqueles que apresentaram oferta de prata ou de bronze trouxeram-na como oferta ao Senhor, e todo aquele que possuía madeira de acácia para qualquer das partes da obra, também a trouxe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2556, 'nvi', 'Êxodo', 'ex', 35, 25, 'Todas as mulheres capazes teceram com suas mãos e trouxeram o que haviam feito: tecidos azul, roxo e vermelho e linho fino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2557, 'nvi', 'Êxodo', 'ex', 35, 26, 'Todas as mulheres que se dispuseram e que tinham habilidade teceram os pêlos de cabra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2558, 'nvi', 'Êxodo', 'ex', 35, 27, 'Os líderes trouxeram pedras de ônix e outras pedras preciosas para serem encravadas no colete sacerdotal e no peitoral.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2559, 'nvi', 'Êxodo', 'ex', 35, 28, 'Trouxeram também especiarias e azeite de oliva para iluminação, para o óleo da unção e para o incenso aromático.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2560, 'nvi', 'Êxodo', 'ex', 35, 29, 'Todos os israelitas que se dispuseram, tanto homens como mulheres, trouxeram ao Senhor ofertas voluntárias para toda a obra que o Senhor, por meio de Moisés, ordenou-lhes que fizessem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2561, 'nvi', 'Êxodo', 'ex', 35, 30, 'Disse então Moisés aos israelitas: "O Senhor escolheu Bezalel, filho de Uri, neto de Hur, da tribo de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2562, 'nvi', 'Êxodo', 'ex', 35, 31, 'e o encheu do Espírito de Deus, dando-lhe destreza, habilidade e plena capacidade artística,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2563, 'nvi', 'Êxodo', 'ex', 35, 32, 'para desenhar e executar trabalhos em ouro, prata e bronze,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2564, 'nvi', 'Êxodo', 'ex', 35, 33, 'para talhar e lapidar pedras, entalhar madeira para todo tipo de obra artesanal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2565, 'nvi', 'Êxodo', 'ex', 35, 34, 'E concedeu tanto a ele como a Aoliabe, filho de Aisamaque, da tribo de Dã, a habilidade de ensinar os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2566, 'nvi', 'Êxodo', 'ex', 35, 35, 'A todos esses deu capacidade para realizar todo tipo de obra como artesãos, projetistas, bordadores de linho fino e de fios de tecido azul, roxo e vermelho, e como tecelões. Eram capazes para projetar e executar qualquer trabalho artesanal".');
