INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2567, 'nvi', 'Êxodo', 'ex', 36, 1, '"Assim farão Bezalel, Aoliabe e todos os homens capazes, a quem o Senhor concedeu destreza e habilidade para fazerem toda a obra de construção do santuário, realizarão a obra como o Senhor ordenou. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2568, 'nvi', 'Êxodo', 'ex', 36, 2, 'Então Moisés chamou Bezalel e Aoliabe e todos os homens capazes a quem o Senhor dera habilidade e que estavam dispostos a vir realizar a obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2569, 'nvi', 'Êxodo', 'ex', 36, 3, 'Receberam de Moisés todas as ofertas que os israelitas tinham trazido para a obra de construção do santuário. E o povo continuava a trazer voluntariamente ofertas, manhã após manhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2570, 'nvi', 'Êxodo', 'ex', 36, 4, 'Por isso, todos os artesãos habilidosos que trabalhavam no santuário interromperam o trabalho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2571, 'nvi', 'Êxodo', 'ex', 36, 5, 'e disseram a Moisés: "O povo está trazendo mais do que o suficiente para realizar a obra que o Senhor ordenou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2572, 'nvi', 'Êxodo', 'ex', 36, 6, 'Então Moisés ordenou que fosse feita esta proclamação em todo o acampamento: "Nenhum homem ou mulher deverá fazer mais nada para ser oferecido ao santuário". Assim, o povo foi impedido de trazer mais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2573, 'nvi', 'Êxodo', 'ex', 36, 7, 'pois o que já haviam recebido era mais que suficiente para realizar toda a obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2574, 'nvi', 'Êxodo', 'ex', 36, 8, 'Todos os homens capazes dentre os trabalhadores fizeram o tabernáculo com dez cortinas internas de linho fino trançado e de fios de tecido azul, roxo e vermelho, com os querubins bordados sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2575, 'nvi', 'Êxodo', 'ex', 36, 9, 'Todas as cortinas internas tinham o mesmo tamanho: doze metros e sessenta centímetros de comprimento por um metro e oitenta centímetros de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2576, 'nvi', 'Êxodo', 'ex', 36, 10, 'Prendeu cinco cortinas internas, e fez o mesmo com as outras cinco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2577, 'nvi', 'Êxodo', 'ex', 36, 11, 'Em seguida fez laçadas de tecido azul ao longo da borda da última cortina interna do primeiro conjunto de cortinas internas, fazendo o mesmo com o segundo conjunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2578, 'nvi', 'Êxodo', 'ex', 36, 12, 'Fez também cinqüenta laçadas na primeira cortina interna e cinqüenta laçadas na última cortina interna do segundo conjunto; as laçadas estavam opostas umas às outras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2579, 'nvi', 'Êxodo', 'ex', 36, 13, 'Depois fez cinqüenta ganchos de ouro e com eles prendeu um conjunto de cortinas internas ao outro, para que o tabernáculo formasse um todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2580, 'nvi', 'Êxodo', 'ex', 36, 14, 'Com o total de onze cortinas internas de pêlos de cabra fez uma tenda para cobrir o tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2581, 'nvi', 'Êxodo', 'ex', 36, 15, 'As onze cortinas internas tinham a mesma medida: treze metros e meio de comprimento por um metro e oitenta centímetros de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2582, 'nvi', 'Êxodo', 'ex', 36, 16, 'Prendeu cinco cortinas internas num conjunto e as outras seis noutro conjunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2583, 'nvi', 'Êxodo', 'ex', 36, 17, 'Depois fez cinqüenta laçadas em volta da borda da última cortina interna de um dos conjuntos e também na borda da última cortina interna do outro conjunto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2584, 'nvi', 'Êxodo', 'ex', 36, 18, 'Fez também cinqüenta ganchos de bronze para unir a tenda, formando um todo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2585, 'nvi', 'Êxodo', 'ex', 36, 19, 'Em seguida fez para a tenda uma cobertura de pele de carneiro tingida de vermelho, e por cima desta uma cobertura de couro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2586, 'nvi', 'Êxodo', 'ex', 36, 20, 'Fez ainda armações verticais de madeira de acácia para o tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2587, 'nvi', 'Êxodo', 'ex', 36, 21, 'Cada armação tinha quatro metros e meio de comprimento por setenta centímetros de largura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2588, 'nvi', 'Êxodo', 'ex', 36, 22, 'com dois encaixes paralelos um ao outro. E fez todas as armações do tabernáculo dessa madeira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2589, 'nvi', 'Êxodo', 'ex', 36, 23, 'Fez também vinte armações para o lado sul do tabernáculo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2590, 'nvi', 'Êxodo', 'ex', 36, 24, 'e quarenta bases de prata para serem colocadas debaixo delas; duas bases para cada armação, uma debaixo de cada encaixe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2591, 'nvi', 'Êxodo', 'ex', 36, 25, 'Para o outro lado, o lado norte do tabernáculo, fez vinte armações');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2592, 'nvi', 'Êxodo', 'ex', 36, 26, 'e quarenta bases de prata, duas debaixo de cada armação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2593, 'nvi', 'Êxodo', 'ex', 36, 27, 'Fez ainda seis armações na parte de trás do tabernáculo, isto é, para o lado ocidental,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2594, 'nvi', 'Êxodo', 'ex', 36, 28, 'e duas armações foram montadas nos cantos, na parte de trás do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2595, 'nvi', 'Êxodo', 'ex', 36, 29, 'Nesses dois cantos as armações eram duplas, desde a parte inferior até a mais alta, colocadas numa só argola, ambas feitas do mesmo modo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2596, 'nvi', 'Êxodo', 'ex', 36, 30, 'Havia, pois, oito armações e dezesseis bases de prata, duas debaixo de cada armação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2597, 'nvi', 'Êxodo', 'ex', 36, 31, 'Também fez travessões de madeira de acácia: cinco para as armações de um lado do tabernáculo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2598, 'nvi', 'Êxodo', 'ex', 36, 32, 'cinco para as do outro lado e cinco para as do lado ocidental, na parte de trás do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2599, 'nvi', 'Êxodo', 'ex', 36, 33, 'Fez o travessão central de uma extremidade à outra, passando pelo meio das armações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2600, 'nvi', 'Êxodo', 'ex', 36, 34, 'Revestiu de ouro as armações e fez argolas de ouro para sustentar os travessões, os quais também revestiu de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2601, 'nvi', 'Êxodo', 'ex', 36, 35, 'Fez o véu de linho fino trançado e de fios de tecido azul, roxo e vermelho, e mandou bordar nele querubins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2602, 'nvi', 'Êxodo', 'ex', 36, 36, 'Fez-lhe quatro colunas de madeira de acácia e as revestiu de ouro. Fez-lhe ainda ganchos de ouro e fundiu as suas bases de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2603, 'nvi', 'Êxodo', 'ex', 36, 37, 'Para a entrada da tenda fez uma cortina de linho fino trançado e de fios de tecido azul, roxo e vermelho, obra de bordador;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2604, 'nvi', 'Êxodo', 'ex', 36, 38, 'e fez-lhe cinco colunas com ganchos. Revestiu de ouro as partes superior e lateral das colunas e fez de bronze as suas cinco bases.');
