INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2605, 'nvi', 'Êxodo', 'ex', 37, 1, 'Bezalel fez a arca com madeira de acácia, com um metro e dez centímetros de comprimento, setenta centímetros de largura e setenta centímetros de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2606, 'nvi', 'Êxodo', 'ex', 37, 2, 'Revestiu-a de ouro puro, por dentro e por fora, e fez uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2607, 'nvi', 'Êxodo', 'ex', 37, 3, 'Fundiu quatro argolas de ouro para ela, prendendo-as a seus quatro pés, com duas argolas de um lado e duas do outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2608, 'nvi', 'Êxodo', 'ex', 37, 4, 'Depois fez varas de madeira de acácia e revestiu-as de ouro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2609, 'nvi', 'Êxodo', 'ex', 37, 5, 'e colocou-as nas argolas laterais da arca para que pudesse ser carregada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2610, 'nvi', 'Êxodo', 'ex', 37, 6, 'Fez a tampa de ouro puro com um metro e dez centímetros de comprimento por setenta centímetros de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2611, 'nvi', 'Êxodo', 'ex', 37, 7, 'Fez também dois querubins de ouro batido nas extremidades da tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2612, 'nvi', 'Êxodo', 'ex', 37, 8, 'Fez ainda um querubim numa extremidade e o segundo na outra, formando uma só peça com a tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2613, 'nvi', 'Êxodo', 'ex', 37, 9, 'Os querubins tinham as asas estendidas para cima, cobrindo com elas a tampa. Estavam de frente um para o outro, com o rosto voltado para a tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2614, 'nvi', 'Êxodo', 'ex', 37, 10, 'Fez a mesa com madeira de acácia com noventa centímetros de comprimento, quarenta e cinco centímetros de largura e setenta centímetros de altura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2615, 'nvi', 'Êxodo', 'ex', 37, 11, 'Revestiu-a de ouro puro e fez uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2616, 'nvi', 'Êxodo', 'ex', 37, 12, 'Fez também ao seu redor uma borda com a largura de quatro dedos e uma moldura de ouro para essa borda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2617, 'nvi', 'Êxodo', 'ex', 37, 13, 'Fundiu quatro argolas de ouro para a mesa e prendeu-as nos quatro cantos, onde estavam os seus quatro pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2618, 'nvi', 'Êxodo', 'ex', 37, 14, 'As argolas foram presas próximas da borda, para que sustentassem as varas usadas para carregar a mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2619, 'nvi', 'Êxodo', 'ex', 37, 15, 'Fez as varas para carregar a mesa de madeira de acácia, revestidas de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2620, 'nvi', 'Êxodo', 'ex', 37, 16, 'E de ouro puro fez os utensílios para a mesa: seus pratos e recipientes para incenso, tigelas e as bacias nas quais se derramam as ofertas de bebidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2621, 'nvi', 'Êxodo', 'ex', 37, 17, 'Fez o candelabro de ouro puro e batido. O pedestal, a haste, as taças, as flores e os botões formavam com ele uma só peça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2622, 'nvi', 'Êxodo', 'ex', 37, 18, 'Seis braços saíam do candelabro: três de um lado e três do outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2623, 'nvi', 'Êxodo', 'ex', 37, 19, 'Havia três taças com formato de flor de amêndoa, num dos braços, cada uma com botão e flor, e três taças com formato de flor de amêndoa no braço seguinte, cada uma com botão e flor. Assim será com os seis braços que saem do candelabro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2624, 'nvi', 'Êxodo', 'ex', 37, 20, 'Na haste do candelabro havia quatro taças com formato de flor de amêndoa, cada uma com flor e botão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2625, 'nvi', 'Êxodo', 'ex', 37, 21, 'Havia um botão debaixo de cada par dos seis braços que saíam do candelabro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2626, 'nvi', 'Êxodo', 'ex', 37, 22, 'Os braços com seus botões formavam uma só peça com o candelabro, tudo feito de ouro puro e batido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2627, 'nvi', 'Êxodo', 'ex', 37, 23, 'Fez de ouro puro suas sete lâmpadas, seus cortadores de pavio e seus apagadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2628, 'nvi', 'Êxodo', 'ex', 37, 24, 'Com trinta e cinco quilos de ouro puro fez o candelabro com seus botões e todos esses utensílios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2629, 'nvi', 'Êxodo', 'ex', 37, 25, 'Fez ainda o altar de incenso de madeira de acácia. Era quadrado, com quarenta e cinco centímetros de cada lado e noventa centímetros de altura; suas pontas formavam com ele uma só peça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2630, 'nvi', 'Êxodo', 'ex', 37, 26, 'Revestiu de ouro puro a parte superior, todos os lados e as pontas, e fez uma moldura de ouro ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2631, 'nvi', 'Êxodo', 'ex', 37, 27, 'Fez também duas argolas de ouro de cada lado do altar, abaixo da moldura, para sustentar as varas utilizadas para carregá-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2632, 'nvi', 'Êxodo', 'ex', 37, 28, 'e usou madeira de acácia para fazer as varas e revestiu-as de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2633, 'nvi', 'Êxodo', 'ex', 37, 29, 'Fez ainda o óleo sagrado para as unções e o incenso puro e aromático, obra de perfumista.');
