INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2634, 'nvi', 'Êxodo', 'ex', 38, 1, 'Fez um altar de madeira de acácia para os holocaustos, com um metro e trinta e cinco centímetros de altura; era quadrado, com dois metros e vinte e cinco centímetros de cada lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2635, 'nvi', 'Êxodo', 'ex', 38, 2, 'E fez uma ponta em forma de chifre em cada um dos quatro cantos, formando uma só peça com o altar, o qual revestiu de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2636, 'nvi', 'Êxodo', 'ex', 38, 3, 'De bronze fez todos os seus utensílios: os recipientes para recolher cinzas, as pás, as bacias de aspersão, os garfos para carne e os braseiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2637, 'nvi', 'Êxodo', 'ex', 38, 4, 'Fez uma grelha de bronze para o altar em forma de rede, abaixo da sua beirada, a meia altura do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2638, 'nvi', 'Êxodo', 'ex', 38, 5, 'Fundiu quatro argolas de bronze para sustentar as varas nos quatro cantos da grelha de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2639, 'nvi', 'Êxodo', 'ex', 38, 6, 'Fez as varas de madeira de acácia, revestiu-as de bronze');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2640, 'nvi', 'Êxodo', 'ex', 38, 7, 'e colocou-as nas argolas, nos dois lados do altar, para que o pudessem carregar. O altar era oco, feito de tábuas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2641, 'nvi', 'Êxodo', 'ex', 38, 8, 'Fez a bacia de bronze e a sua base com os espelhos das mulheres que serviam à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2642, 'nvi', 'Êxodo', 'ex', 38, 9, 'Fez também o pátio. O lado sul tinha quarenta e cinco metros de comprimento e cortinas externas de linho fino trançado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2643, 'nvi', 'Êxodo', 'ex', 38, 10, 'com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2644, 'nvi', 'Êxodo', 'ex', 38, 11, 'O lado norte também tinha quarenta e cinco metros de comprimento, com vinte colunas e vinte bases de bronze. Os ganchos e as ligaduras das colunas eram de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2645, 'nvi', 'Êxodo', 'ex', 38, 12, 'O lado ocidental, com suas cortinas externas, tinha vinte e dois metros e meio de largura, com dez colunas e dez bases, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2646, 'nvi', 'Êxodo', 'ex', 38, 13, 'O lado oriental, que dá para o nascente, também tinha vinte e dois metros e meio de largura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2647, 'nvi', 'Êxodo', 'ex', 38, 14, 'Havia cortinas de seis metros e setenta e cinco centímetros de comprimento num dos lados da entrada, com três colunas e três bases,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2648, 'nvi', 'Êxodo', 'ex', 38, 15, 'e cortinas de seis metros e setenta e cinco centímetros de comprimento no outro lado da entrada do pátio, também com três colunas e três bases.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2649, 'nvi', 'Êxodo', 'ex', 38, 16, 'Todas as cortinas ao redor do pátio eram feitas de linho fino trançado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2650, 'nvi', 'Êxodo', 'ex', 38, 17, 'As bases das colunas eram de bronze. Os ganchos e as ligaduras das colunas eram de prata, e o topo das colunas também eram revestidos de prata; de modo que todas as colunas do pátio tinham ligaduras de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2651, 'nvi', 'Êxodo', 'ex', 38, 18, 'Na entrada do pátio havia uma cortina de linho fino trançado e de fios de tecidos azul, roxo e vermelho, obra de bordador. Tinha nove metros de comprimento e, à semelhança das cortinas do pátio, tinha dois metros e vinte e cinco centímetros de altura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2652, 'nvi', 'Êxodo', 'ex', 38, 19, 'com quatro colunas e quatro bases de bronze. Seus ganchos e ligaduras eram de prata, e o topo das colunas também era revestido de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2653, 'nvi', 'Êxodo', 'ex', 38, 20, 'Todas as estacas da tenda do tabernáculo e do pátio que o rodeava eram de bronze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2654, 'nvi', 'Êxodo', 'ex', 38, 21, 'Esta é a relação do material usado para o tabernáculo, o tabernáculo da aliança, registrada por ordem de Moisés pelos levitas, sob a direção de Itamar, filho de Arão, o sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2655, 'nvi', 'Êxodo', 'ex', 38, 22, 'Bezalel, filho de Uri, neto de Hur, da tribo de Judá, fez tudo o que o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2656, 'nvi', 'Êxodo', 'ex', 38, 23, 'Com ele estava Aoliabe, filho de Aisamaque, da tribo de Dã, artesão e projetista, e também bordador em linho fino e de fios de tecido azul, roxo e vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2657, 'nvi', 'Êxodo', 'ex', 38, 24, 'O peso total do ouro recebido na oferta movida e utilizado para a obra do santuário foi de uma tonelada, com base no peso padrão do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2658, 'nvi', 'Êxodo', 'ex', 38, 25, 'O peso da prata recebida dos que foram contados no recenseamento da comunidade foi superior a três toneladas e meia, com base no peso padrão do santuário:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2659, 'nvi', 'Êxodo', 'ex', 38, 26, 'seis gramas para cada um dos recenseados, seiscentos e três mil, quinhentos e cinqüenta homens de vinte anos de idade para cima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2660, 'nvi', 'Êxodo', 'ex', 38, 27, 'As três toneladas e meia de prata foram usadas para fundir as bases do santuário e do véu: cem bases feitas das três toneladas e meia, trinta e cinco quilos para cada base.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2661, 'nvi', 'Êxodo', 'ex', 38, 28, 'Vinte quilos e trezentos gramas foram usados para fazer os ganchos para as colunas, para revestir a parte superior das colunas e para fazer as suas ligaduras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2662, 'nvi', 'Êxodo', 'ex', 38, 29, 'O peso do bronze da oferta movida foi de duas toneladas e meia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2663, 'nvi', 'Êxodo', 'ex', 38, 30, 'Ele o utilizou para fazer as bases da entrada da Tenda do Encontro, o altar de bronze, a sua grelha e todos os seus utensílios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2664, 'nvi', 'Êxodo', 'ex', 38, 31, 'as bases do pátio ao redor e da sua entrada, e todas as estacas do tabernáculo e do pátio em derredor.');
