INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1602, 'nvi', 'Êxodo', 'ex', 4, 1, 'Moisés respondeu: "E se eles não acreditarem em mim nem quiserem me ouvir e disserem: ‘O Senhor não lhe apareceu’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1603, 'nvi', 'Êxodo', 'ex', 4, 2, 'Então o Senhor lhe perguntou: "Que é isso em sua mão? " "Uma vara", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1604, 'nvi', 'Êxodo', 'ex', 4, 3, 'Disse o Senhor: "Jogue-a ao chão". Moisés jogou-a, e ela se transformou numa serpente. Moisés fugiu dela,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1605, 'nvi', 'Êxodo', 'ex', 4, 4, 'mas o Senhor lhe disse: "Estenda a mão e pegue-a pela cauda". Moisés estendeu a mão, pegou a serpente e esta se transformou numa vara em sua mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1606, 'nvi', 'Êxodo', 'ex', 4, 5, 'E disse o Senhor: "Isso é para que eles acreditem que o Deus dos seus antepassados, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó, apareceu a você".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1607, 'nvi', 'Êxodo', 'ex', 4, 6, 'Disse-lhe mais o Senhor: "Coloque a mão no peito". Moisés obedeceu e, quando a retirou, ela estava leprosa; parecia neve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1608, 'nvi', 'Êxodo', 'ex', 4, 7, 'Ordenou-lhe depois: "Agora, coloque de novo a mão no peito". Moisés tornou a pôr a mão no peito e, quando a tirou, ela estava novamente como o restante da sua pele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1609, 'nvi', 'Êxodo', 'ex', 4, 8, 'Prosseguiu o Senhor: "Se eles não acreditarem em você nem derem atenção ao primeiro sinal miraculoso, acreditarão no segundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1610, 'nvi', 'Êxodo', 'ex', 4, 9, 'E se ainda assim não acreditarem nestes dois sinais nem lhe derem ouvidos, tire um pouco de água do Nilo e derrame-a em terra seca. Quando você derramar essa água em terra seca ela se transformará em sangue".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1611, 'nvi', 'Êxodo', 'ex', 4, 10, 'Disse, porém, Moisés ao Senhor: "Ó Senhor! Nunca tive facilidade para falar, nem no passado nem agora que falaste a teu servo. Não consigo falar bem! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1612, 'nvi', 'Êxodo', 'ex', 4, 11, 'Disse-lhe o Senhor: "Quem deu boca ao homem? Quem o fez surdo ou mudo? Quem lhe concede vista ou o torna cego? Não sou eu, o Senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1613, 'nvi', 'Êxodo', 'ex', 4, 12, 'Agora, pois, vá; eu estarei com você, ensinando-lhe o que dizer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1614, 'nvi', 'Êxodo', 'ex', 4, 13, 'Respondeu-lhe, porém, Moisés: "Ah Senhor! Peço-te que envies outra pessoa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1615, 'nvi', 'Êxodo', 'ex', 4, 14, 'Então o Senhor se irou com Moisés e lhe disse: "Você não tem o seu irmão Arão, o levita? Eu sei que ele fala bem. Ele já está vindo ao seu encontro e se alegrará ao vê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1616, 'nvi', 'Êxodo', 'ex', 4, 15, 'Você falará com ele e lhe dirá o que ele deve dizer; eu estarei com vocês quando falarem, e lhes direi o que fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1617, 'nvi', 'Êxodo', 'ex', 4, 16, 'Assim como Deus fala ao profeta, você falará a seu irmão, e ele será o seu porta-voz diante do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1618, 'nvi', 'Êxodo', 'ex', 4, 17, 'E leve na mão esta vara; com ela você fará os sinais miraculosos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1619, 'nvi', 'Êxodo', 'ex', 4, 18, 'Depois Moisés voltou a Jetro, seu sogro, e lhe disse: "Preciso voltar ao Egito para ver se meus parentes ainda vivem". Jetro lhe respondeu: "Vá em paz! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1620, 'nvi', 'Êxodo', 'ex', 4, 19, 'Ora, o Senhor tinha dito a Moisés, em Midiã: "Volte ao Egito, pois já morreram todos os que procuravam matá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1621, 'nvi', 'Êxodo', 'ex', 4, 20, 'Então Moisés levou sua mulher e seus filhos montados num jumento e partiu de volta ao Egito. Levava na mão a vara de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1622, 'nvi', 'Êxodo', 'ex', 4, 21, 'Disse mais o Senhor a Moisés: "Quando você voltar ao Egito, tenha o cuidado de fazer diante do faraó todas as maravilhas que concedi a você o poder de realizar. Mas eu vou endurecer o coração dele, para não deixar o povo ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1623, 'nvi', 'Êxodo', 'ex', 4, 22, 'Depois diga ao faraó que assim diz o Senhor: Israel é o meu primeiro filho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1624, 'nvi', 'Êxodo', 'ex', 4, 23, 'e eu já lhe disse que deixe o meu filho ir para prestar-me culto. Mas você não quis deixá-lo ir; por isso matarei o seu primeiro filho! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1625, 'nvi', 'Êxodo', 'ex', 4, 24, 'Numa hospedaria ao longo do caminho, o Senhor foi ao encontro de Moisés e procurou matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1626, 'nvi', 'Êxodo', 'ex', 4, 25, 'Mas Zípora pegou uma pedra afiada, cortou o prepúcio de seu filho e tocou os pés de Moisés. E disse: "Você é para mim um marido de sangue! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1627, 'nvi', 'Êxodo', 'ex', 4, 26, 'Ela disse "marido de sangue", referindo-se à circuncisão. Nessa ocasião o Senhor o deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1628, 'nvi', 'Êxodo', 'ex', 4, 27, 'Então o Senhor disse a Arão: "Vá ao deserto encontrar-se com Moisés". Ele foi, encontrou-se com Moisés no monte de Deus, e o saudou com um beijo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1629, 'nvi', 'Êxodo', 'ex', 4, 28, 'Moisés contou a Arão tudo o que o Senhor lhe tinha mandado dizer, e também falou-lhe de todos os sinais miraculosos que lhe havia ordenado realizar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1630, 'nvi', 'Êxodo', 'ex', 4, 29, 'Assim Moisés e Arão foram e reuniram todas as autoridades dos israelitas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1631, 'nvi', 'Êxodo', 'ex', 4, 30, 'e Arão lhes contou tudo o que o Senhor dissera a Moisés. Em seguida Moisés também realizou os sinais diante do povo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1632, 'nvi', 'Êxodo', 'ex', 4, 31, 'e eles creram. Quando o povo soube que o Senhor decidira vir em auxílio deles e tinha visto a sua opressão, curvou-se em adoração.');
