INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2708, 'nvi', 'Êxodo', 'ex', 40, 1, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2709, 'nvi', 'Êxodo', 'ex', 40, 2, '"Arme o tabernáculo, a Tenda do Encontro, no primeiro dia do primeiro mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2710, 'nvi', 'Êxodo', 'ex', 40, 3, 'Coloque nele a arca da aliança e proteja-a com o véu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2711, 'nvi', 'Êxodo', 'ex', 40, 4, 'Traga a mesa e arrume sobre ela tudo o que lhe pertence. Depois traga o candelabro e coloque as suas lâmpadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2712, 'nvi', 'Êxodo', 'ex', 40, 5, 'Ponha o altar de ouro para o incenso diante da arca da aliança e pendure a cortina na entrada do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2713, 'nvi', 'Êxodo', 'ex', 40, 6, '"Coloque o altar dos holocaustos em frente da entrada do tabernáculo, da Tenda do Encontro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2714, 'nvi', 'Êxodo', 'ex', 40, 7, 'ponha a bacia entre a Tenda do Encontro e o altar, e encha-a de água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2715, 'nvi', 'Êxodo', 'ex', 40, 8, 'Arme ao seu redor o pátio e coloque a cortina na entrada do pátio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2716, 'nvi', 'Êxodo', 'ex', 40, 9, '"Unja com o óleo da unção o tabernáculo e tudo o que nele há; consagre-o juntamente com tudo o que lhe pertence, e ele será sagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2717, 'nvi', 'Êxodo', 'ex', 40, 10, 'Depois unja o altar dos holocaustos e todos os seus utensílios; consagre o altar, e ele será santíssimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2718, 'nvi', 'Êxodo', 'ex', 40, 11, 'Unja também a bacia com a sua base e consagre-a.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2719, 'nvi', 'Êxodo', 'ex', 40, 12, '"Traga Arão e seus filhos à entrada da Tenda do Encontro e mande-os se lavar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2720, 'nvi', 'Êxodo', 'ex', 40, 13, 'Vista depois Arão com as vestes sagradas, unja-o e consagre-o para que me sirva como sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2721, 'nvi', 'Êxodo', 'ex', 40, 14, 'Traga os filhos dele e vista-os com túnicas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2722, 'nvi', 'Êxodo', 'ex', 40, 15, 'Unja-os como você ungiu o pai deles, para que me sirvam como sacerdotes. A unção deles será para um sacerdócio perpétuo, geração após geração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2723, 'nvi', 'Êxodo', 'ex', 40, 16, 'Moisés fez tudo conforme o Senhor lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2724, 'nvi', 'Êxodo', 'ex', 40, 17, 'Assim, o tabernáculo foi armado no primeiro dia do primeiro mês do segundo ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2725, 'nvi', 'Êxodo', 'ex', 40, 18, 'Moisés armou o tabernáculo, colocou as bases em seus lugares, armou as molduras, colocou as vigas e levantou as colunas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2726, 'nvi', 'Êxodo', 'ex', 40, 19, 'Depois estendeu a tenda sobre o tabernáculo e colocou a cobertura sobre ela, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2727, 'nvi', 'Êxodo', 'ex', 40, 20, 'Colocou também as tábuas da aliança na arca, fixou nela as varas, e pôs sobre ela a tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2728, 'nvi', 'Êxodo', 'ex', 40, 21, 'Em seguida trouxe a arca para dentro do tabernáculo e pendurou o véu protetor, cobrindo a arca da aliança, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2729, 'nvi', 'Êxodo', 'ex', 40, 22, 'Moisés colocou a mesa na Tenda do Encontro, no lado norte do tabernáculo, do lado de fora do véu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2730, 'nvi', 'Êxodo', 'ex', 40, 23, 'e sobre ela colocou os pães da Presença, diante do Senhor, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2731, 'nvi', 'Êxodo', 'ex', 40, 24, 'Pôs o candelabro na Tenda do Encontro, em frente da mesa, no lado sul do tabernáculo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2732, 'nvi', 'Êxodo', 'ex', 40, 25, 'e colocou as lâmpadas diante do Senhor, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2733, 'nvi', 'Êxodo', 'ex', 40, 26, 'Moisés também pôs o altar de ouro na Tenda do Encontro, diante do véu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2734, 'nvi', 'Êxodo', 'ex', 40, 27, 'e nele queimou incenso aromático, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2735, 'nvi', 'Êxodo', 'ex', 40, 28, 'Pôs também a cortina à entrada do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2736, 'nvi', 'Êxodo', 'ex', 40, 29, 'Montou o altar de holocaustos à entrada do tabernáculo, a Tenda do Encontro, e sobre ele ofereceu holocaustos e ofertas de cereal, como o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2737, 'nvi', 'Êxodo', 'ex', 40, 30, 'Colocou a bacia entre a Tenda do Encontro e o altar, e encheu-a de água;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2738, 'nvi', 'Êxodo', 'ex', 40, 31, 'Moisés, Arão e os filhos deste usavam-na para lavar as mãos e os pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2739, 'nvi', 'Êxodo', 'ex', 40, 32, 'Sempre que entravam na Tenda do Encontro e se aproximavam do altar, eles se lavavam, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2740, 'nvi', 'Êxodo', 'ex', 40, 33, 'Finalmente, Moisés armou o pátio ao redor do tabernáculo e colocou a cortina à entrada do pátio. Assim, Moisés terminou a obra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2741, 'nvi', 'Êxodo', 'ex', 40, 34, 'Então a nuvem cobriu a Tenda do Encontro, e a glória do Senhor encheu o tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2742, 'nvi', 'Êxodo', 'ex', 40, 35, 'Moisés não podia entrar na Tenda do Encontro, porque a nuvem estava sobre ela, e a glória do Senhor enchia o tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2743, 'nvi', 'Êxodo', 'ex', 40, 36, 'Sempre que a nuvem se erguia sobre o tabernáculo os israelitas seguiam viagem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2744, 'nvi', 'Êxodo', 'ex', 40, 37, 'mas se a nuvem não se erguia, eles não prosseguiam; só partiam no dia em que ela se erguesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2745, 'nvi', 'Êxodo', 'ex', 40, 38, 'De dia a nuvem do Senhor ficava sobre o tabernáculo, e de noite havia fogo na nuvem, à vista de toda a nação de Israel, em todas as suas viagens.');
