INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1633, 'nvi', 'Êxodo', 'ex', 5, 1, 'Depois disso Moisés e Arão foram falar com o faraó e disseram: "Assim diz o Senhor, o Deus de Israel: ‘Deixe o meu povo ir para celebrar-me uma festa no deserto’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1634, 'nvi', 'Êxodo', 'ex', 5, 2, 'O faraó respondeu: "Quem é o Senhor, para que eu lhe obedeça e deixe Israel sair? Não conheço o Senhor, e não deixarei Israel sair".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1635, 'nvi', 'Êxodo', 'ex', 5, 3, 'Eles insistiram: "O Deus dos hebreus veio ao nosso encontro. Agora, permite-nos caminhar três dias no deserto, para oferecer sacrifícios ao Senhor, o nosso Deus; caso contrário, ele nos atingirá com pragas ou com a espada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1636, 'nvi', 'Êxodo', 'ex', 5, 4, 'Mas o rei do Egito respondeu: "Moisés e Arão, por que vocês estão fazendo o povo interromper suas tarefas? Voltem ao trabalho! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1637, 'nvi', 'Êxodo', 'ex', 5, 5, 'E acrescentou: "Essa gente já é tão numerosa, e vocês ainda os fazem parar de trabalhar! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1638, 'nvi', 'Êxodo', 'ex', 5, 6, 'No mesmo dia o faraó deu a seguinte ordem aos feitores e capatazes responsáveis pelo povo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1639, 'nvi', 'Êxodo', 'ex', 5, 7, '"Não forneçam mais palha ao povo para fazer tijolos, como faziam antes. Eles que tratem de ajuntar palha!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1640, 'nvi', 'Êxodo', 'ex', 5, 8, 'Mas exijam que continuem a fazer a mesma quantidade de tijolos; não reduzam a cota. São preguiçosos, e por isso estão clamando: ‘Iremos oferecer sacrifícios ao nosso Deus’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1641, 'nvi', 'Êxodo', 'ex', 5, 9, 'Aumentem a carga de trabalho dessa gente para que cumpram suas tarefas e não dêem atenção a mentiras".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1642, 'nvi', 'Êxodo', 'ex', 5, 10, 'Os feitores e os capatazes foram dizer ao povo: "Assim diz o faraó: ‘Já não lhes darei palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1643, 'nvi', 'Êxodo', 'ex', 5, 11, 'Saiam e recolham-na onde puderem achá-la, pois o trabalho de vocês em nada será reduzido’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1644, 'nvi', 'Êxodo', 'ex', 5, 12, 'O povo então espalhou-se por todo o Egito, a fim de ajuntar restolho em lugar da palha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1645, 'nvi', 'Êxodo', 'ex', 5, 13, 'Enquanto isso, os feitores os pressionavam, dizendo: "Completem a mesma tarefa diária que lhes foi exigida quando tinham palha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1646, 'nvi', 'Êxodo', 'ex', 5, 14, 'Os capatazes israelitas indicados pelos feitores do faraó eram espancados e interrogados: "Por que não completaram ontem e hoje a mesma cota de tijolos dos dias anteriores? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1647, 'nvi', 'Êxodo', 'ex', 5, 15, 'Então os capatazes israelitas foram apelar para o faraó: "Por que tratas os teus servos dessa maneira?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1648, 'nvi', 'Êxodo', 'ex', 5, 16, 'Não se fornece a nós, teus servos, a palha, e contudo nos dizem: ‘Façam tijolos! ’ Os teus servos têm sido espancados, mas a culpa é do teu próprio povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1649, 'nvi', 'Êxodo', 'ex', 5, 17, 'Respondeu o faraó: "Preguiçosos, é o que vocês são! Preguiçosos! Por isso andam dizendo: ‘Iremos oferecer sacrifícios ao Senhor’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1650, 'nvi', 'Êxodo', 'ex', 5, 18, 'Agora, voltem ao trabalho. Vocês não receberão palha alguma! Continuem a produzir a cota integral de tijolos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1651, 'nvi', 'Êxodo', 'ex', 5, 19, 'Os capatazes israelitas se viram em dificuldade quando lhes disseram que não poderiam reduzir a quantidade de tijolos exigida a cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1652, 'nvi', 'Êxodo', 'ex', 5, 20, 'Ao saírem da presença do faraó, encontraram-se com Moisés e Arão, que estavam à espera deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1653, 'nvi', 'Êxodo', 'ex', 5, 21, 'e lhes disseram: "O Senhor os examine e os julgue! Vocês atraíram o ódio do faraó e dos seus conselheiros sobre nós, e lhes puseram nas mãos uma espada para que nos matem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1654, 'nvi', 'Êxodo', 'ex', 5, 22, 'Moisés voltou-se para o Senhor e perguntou: "Senhor, por que maltrataste a este povo? Afinal, por que me enviaste?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1655, 'nvi', 'Êxodo', 'ex', 5, 23, 'Desde que me dirigi ao faraó para falar em teu nome, ele tem maltratado a este povo, e tu de modo algum libertaste o teu povo! "');
