INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1656, 'nvi', 'Êxodo', 'ex', 6, 1, 'Então o Senhor disse a Moisés: "Agora você verá o que farei ao faraó: Por minha mão poderosa, ele os deixará ir; por minha mão poderosa, ele os expulsará do seu país".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1657, 'nvi', 'Êxodo', 'ex', 6, 2, 'Disse Deus ainda a Moisés: "Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1658, 'nvi', 'Êxodo', 'ex', 6, 3, 'Apareci a Abraão, a Isaque e a Jacó como o Deus Todo-poderoso, mas pelo meu nome, o Senhor, não me revelei a eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1659, 'nvi', 'Êxodo', 'ex', 6, 4, 'Depois estabeleci com eles a minha aliança para dar-lhes a terra de Canaã, terra onde viveram como estrangeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1660, 'nvi', 'Êxodo', 'ex', 6, 5, 'E agora ouvi o lamento dos israelitas, a quem os egípcios mantêm escravos, e lembrei-me da minha aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1661, 'nvi', 'Êxodo', 'ex', 6, 6, '"Por isso, diga aos israelitas: Eu sou o Senhor. Eu os livrarei do trabalho imposto pelos egípcios. Eu os libertarei da escravidão e os resgatarei com braço forte e com poderosos atos de juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1662, 'nvi', 'Êxodo', 'ex', 6, 7, 'Eu os farei meu povo e serei o Deus de vocês. Então vocês saberão que eu sou o Senhor, o Deus de vocês, que os livra do trabalho imposto pelos egípcios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1663, 'nvi', 'Êxodo', 'ex', 6, 8, 'E os farei entrar na terra que, com mão levantada, jurei que daria a Abraão, a Isaque e a Jacó. Eu a darei a vocês como propriedade. Eu sou o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1664, 'nvi', 'Êxodo', 'ex', 6, 9, 'Moisés declarou isso aos israelitas, mas eles não lhe deram ouvidos, por causa da angústia e da cruel escravidão que sofriam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1665, 'nvi', 'Êxodo', 'ex', 6, 10, 'Então o Senhor ordenou a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1666, 'nvi', 'Êxodo', 'ex', 6, 11, '"Vá dizer ao faraó, rei do Egito, que deixe os israelitas saírem do país".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1667, 'nvi', 'Êxodo', 'ex', 6, 12, 'Moisés, porém, disse na presença do Senhor: "Se os israelitas não me dão ouvidos, como me ouvirá o faraó? Ainda mais que não tenho facilidade para falar! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1668, 'nvi', 'Êxodo', 'ex', 6, 13, 'Mas, o Senhor ordenou a Moisés e a Arão que dissessem aos israelitas e ao faraó, rei do Egito, que tinham ordem para tirar do Egito os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1669, 'nvi', 'Êxodo', 'ex', 6, 14, 'Estes foram os chefes das famílias israelitas: Os filhos de Rúben, filho mais velho de Israel, foram: Enoque, Palu, Hezrom e Carmi. Esses foram os clãs de Rúben.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1670, 'nvi', 'Êxodo', 'ex', 6, 15, 'Os filhos de Simeão foram: Jemuel, Jamim, Oade, Jaquim, Zoar e Saul, filho de uma cananéia. Esses foram os clãs de Simeão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1671, 'nvi', 'Êxodo', 'ex', 6, 16, 'Estes, pois, são os nomes dos filhos de Levi, por ordem de nascimento: Gérson, Coate e Merari. Levi viveu cento e trinta e sete anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1672, 'nvi', 'Êxodo', 'ex', 6, 17, 'Os filhos de Gérson, conforme seus clãs, foram Libni e Simei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1673, 'nvi', 'Êxodo', 'ex', 6, 18, 'Os filhos de Coate foram Anrão, Isar, Hebrom e Uziel. Coate viveu cento e trinta e três anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1674, 'nvi', 'Êxodo', 'ex', 6, 19, 'Os filhos de Merari foram Mali e Musi. Esses foram os clãs de Levi, por ordem de nascimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1675, 'nvi', 'Êxodo', 'ex', 6, 20, 'Anrão tomou por mulher sua tia Joquebede, que lhe deu à luz Arão e Moisés. Anrão viveu cento e trinta e sete anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1676, 'nvi', 'Êxodo', 'ex', 6, 21, 'Os filhos de Isar foram Corá, Nefegue e Zicri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1677, 'nvi', 'Êxodo', 'ex', 6, 22, 'Os filhos de Uziel foram Misael, Elzafã e Sitri.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1678, 'nvi', 'Êxodo', 'ex', 6, 23, 'Arão tomou por mulher a Eliseba, filha de Aminadabe, irmã de Naassom, e ela lhe deu à luz Nadabe, Abiú, Eleazar e Itamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1679, 'nvi', 'Êxodo', 'ex', 6, 24, 'Os filhos de Corá foram Assir, Elcana e Abiasafe. Esses foram os clãs dos coraítas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1680, 'nvi', 'Êxodo', 'ex', 6, 25, 'Eleazar, filho de Arão, tomou por mulher uma das filhas de Futiel, e ela lhe deu à luz Finéias. Esses foram os chefes das famílias dos levitas, conforme seus clãs.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1681, 'nvi', 'Êxodo', 'ex', 6, 26, 'Foi a este Arão e a este Moisés que o Senhor disse: "Tirem os israelitas do Egito, organizados segundo as suas divisões".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1682, 'nvi', 'Êxodo', 'ex', 6, 27, 'Foram eles, Moisés e Arão, que falaram ao faraó, rei do Egito, a fim de tirar os israelitas do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1683, 'nvi', 'Êxodo', 'ex', 6, 28, 'Ora, quando o Senhor falou com Moisés no Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1684, 'nvi', 'Êxodo', 'ex', 6, 29, 'disse-lhe: "Eu sou o Senhor. Diga ao faraó, rei do Egito, tudo o que eu lhe disser".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1685, 'nvi', 'Êxodo', 'ex', 6, 30, 'Moisés, porém, perguntou ao Senhor: "Como o faraó me dará ouvidos, se não tenho facilidade para falar? "');
