INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1686, 'nvi', 'Êxodo', 'ex', 7, 1, 'O Senhor lhe respondeu: "Dou-lhe a minha autoridade perante o faraó, e seu irmão Arão será seu porta-voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1687, 'nvi', 'Êxodo', 'ex', 7, 2, 'Você falará tudo o que eu lhe ordenar, e o seu irmão Arão dirá ao faraó que deixe os israelitas saírem do país.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1688, 'nvi', 'Êxodo', 'ex', 7, 3, 'Eu, porém, farei o coração do faraó resistir; e, embora multiplique meus sinais e maravilhas no Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1689, 'nvi', 'Êxodo', 'ex', 7, 4, 'ele não os ouvirá. Então porei a minha mão sobre o Egito, e com poderosos atos de juízo tirarei do Egito os meus exércitos, o meu povo, os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1690, 'nvi', 'Êxodo', 'ex', 7, 5, 'E os egípcios saberão que eu sou o Senhor, quando eu estender a minha mão contra o Egito e tirar de lá os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1691, 'nvi', 'Êxodo', 'ex', 7, 6, 'Moisés e Arão fizeram como o Senhor lhes havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1692, 'nvi', 'Êxodo', 'ex', 7, 7, 'Moisés tinha oitenta anos de idade e Arão oitenta e três, quando falaram com o faraó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1693, 'nvi', 'Êxodo', 'ex', 7, 8, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1694, 'nvi', 'Êxodo', 'ex', 7, 9, '"Quando o faraó lhes pedir que façam algum milagre, diga a Arão que tome a sua vara e a jogue diante do faraó; e ela se transformará numa serpente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1695, 'nvi', 'Êxodo', 'ex', 7, 10, 'Moisés e Arão dirigiram-se ao faraó e fizeram como o Senhor tinha ordenado. Arão jogou a vara diante do faraó e seus conselheiros, e ela se transformou em serpente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1696, 'nvi', 'Êxodo', 'ex', 7, 11, 'O faraó, porém, mandou chamar os sábios e feiticeiros; e também os magos do Egito fizeram a mesma coisa por meio das suas ciências ocultas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1697, 'nvi', 'Êxodo', 'ex', 7, 12, 'Cada um deles jogou ao chão uma vara, e estas se transformaram em serpentes. Mas a vara de Arão engoliu as varas deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1698, 'nvi', 'Êxodo', 'ex', 7, 13, 'Contudo, o coração do faraó se endureceu e ele não quis dar ouvidos a Moisés e a Arão, como o Senhor tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1699, 'nvi', 'Êxodo', 'ex', 7, 14, 'Disse o Senhor a Moisés: "O coração do faraó está obstinado; ele não quer deixar o povo ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1700, 'nvi', 'Êxodo', 'ex', 7, 15, 'Vá ao faraó de manhã, quando ele estiver indo às águas. Espere-o na margem do rio para encontrá-lo e leve também a vara que se transformou em serpente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1701, 'nvi', 'Êxodo', 'ex', 7, 16, 'Diga-lhe: O Senhor, o Deus dos hebreus, mandou-me dizer-lhe: Deixe ir o meu povo, para prestar-me culto no deserto. Mas até agora você não me atendeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1702, 'nvi', 'Êxodo', 'ex', 7, 17, 'Assim diz o Senhor: Nisto você saberá que eu sou o Senhor: com a vara que trago na mão ferirei as águas do Nilo, e elas se transformarão em sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1703, 'nvi', 'Êxodo', 'ex', 7, 18, 'Os peixes do Nilo morrerão, o rio ficará cheirando mal, e os egípcios não suportarão beber das suas águas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1704, 'nvi', 'Êxodo', 'ex', 7, 19, 'Disse o Senhor a Moisés: "Diga a Arão que tome a sua vara e estenda a mão sobre as águas do Egito, dos rios, dos canais, dos açudes e de todos os reservatórios, e elas se transformarão em sangue. Haverá sangue por toda a terra do Egito, até nas vasilhas de madeira e nas vasilhas de pedra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1705, 'nvi', 'Êxodo', 'ex', 7, 20, 'Moisés e Arão fizeram como o Senhor tinha ordenado. Arão levantou a vara e feriu as águas do Nilo na presença do faraó e dos seus conselheiros; e toda a água do rio transformou-se em sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1706, 'nvi', 'Êxodo', 'ex', 7, 21, 'Os peixes morreram e o rio cheirava tão mal que os egípcios não conseguiam beber das suas águas. Havia sangue por toda a terra do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1707, 'nvi', 'Êxodo', 'ex', 7, 22, 'Mas os magos do Egito fizeram a mesma coisa por meio de suas ciências ocultas. O coração do faraó se endureceu, e ele não deu ouvidos a Moisés e a Arão, como o Senhor tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1708, 'nvi', 'Êxodo', 'ex', 7, 23, 'Pelo contrário, deu-lhes as costas e voltou para o seu palácio. Nem assim o faraó levou isso a sério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1709, 'nvi', 'Êxodo', 'ex', 7, 24, 'Todos os egípcios cavaram buracos às margens do Nilo para encontrar água potável, pois da água do rio não podiam mais beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (1710, 'nvi', 'Êxodo', 'ex', 7, 25, 'Passaram-se sete dias depois que o Senhor feriu o Nilo.');
