INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20466, 'nvi', 'Ezequiel', 'ez', 1, 1, 'Era o quinto dia do quarto mês do trigésimo ano, e eu estava entre os exilados, junto ao rio Quebar. Abriram-se os céus, e eu tive visões de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20467, 'nvi', 'Ezequiel', 'ez', 1, 2, 'Foi no quinto ano do exílio do rei Joaquim, no quinto dia do quarto mês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20468, 'nvi', 'Ezequiel', 'ez', 1, 3, 'A palavra do Senhor veio ao sacerdote Ezequiel, filho de Buzi, junto ao rio Quebar, na terra dos caldeus. Ali a mão do Senhor esteve sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20469, 'nvi', 'Ezequiel', 'ez', 1, 4, 'Olhei e vi uma tempestade que vinha do norte: uma nuvem imensa, com relâmpagos e faíscas, e cercada por uma luz brilhante. O centro do fogo parecia metal reluzente,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20470, 'nvi', 'Ezequiel', 'ez', 1, 5, 'e no meio do fogo havia quatro vultos que pareciam seres viventes. Na aparência tinham forma de homem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20471, 'nvi', 'Ezequiel', 'ez', 1, 6, 'mas cada um deles tinha quatro rostos e quatro asas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20472, 'nvi', 'Ezequiel', 'ez', 1, 7, 'Suas pernas eram retas; seus pés eram como os de um bezerro e reluziam como bronze polido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20473, 'nvi', 'Ezequiel', 'ez', 1, 8, 'Debaixo de suas asas, nos quatro lados, tinham mãos humanas. Os quatro tinham rostos e asas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20474, 'nvi', 'Ezequiel', 'ez', 1, 9, 'e as suas asas encostavam umas nas outras. Quando se moviam andavam para a frente, e não se viravam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20475, 'nvi', 'Ezequiel', 'ez', 1, 10, 'Quanto à aparência dos seus rostos, os quatro tinham rosto de homem, rosto de leão no lado direito, rosto de boi no lado esquerdo, e rosto de águia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20476, 'nvi', 'Ezequiel', 'ez', 1, 11, 'Assim eram os seus rostos. Suas asas estavam estendidas para cima; cada um deles tinha duas asas que se encostavam na de outro ser vivente, de um lado e do outro, e duas asas que cobriam os seus corpos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20477, 'nvi', 'Ezequiel', 'ez', 1, 12, 'Cada um deles ia sempre para a frente. Para onde quer que fosse o Espírito eles iam, e não se viravam quando se moviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20478, 'nvi', 'Ezequiel', 'ez', 1, 13, 'Os seres viventes pareciam carvão aceso; eram como tochas. O fogo ia de um lado a outro entre os seres viventes, e do fogo saíam relâmpagos e faíscas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20479, 'nvi', 'Ezequiel', 'ez', 1, 14, 'Os seres viventes iam e vinham como relâmpagos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20480, 'nvi', 'Ezequiel', 'ez', 1, 15, 'Enquanto eu olhava para eles, vi uma roda ao lado de cada um deles, diante dos seus quatro rostos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20481, 'nvi', 'Ezequiel', 'ez', 1, 16, 'Esta era a aparência das rodas e a sua estrutura: Reluziam como o berilo; e as quatro tinham aparência semelhante. Cada roda parecia estar entrosada na outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20482, 'nvi', 'Ezequiel', 'ez', 1, 17, 'Quando se moviam, seguiam nas quatro direções dos quatro rostos, e não se viravam enquanto iam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20483, 'nvi', 'Ezequiel', 'ez', 1, 18, 'Seus aros eram altos e impressionantes e estavam cheios de olhos ao redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20484, 'nvi', 'Ezequiel', 'ez', 1, 19, 'Quando os seres viventes se moviam, as rodas ao seu lado se moviam; e, quando se elevavam do chão, as rodas também se elevavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20485, 'nvi', 'Ezequiel', 'ez', 1, 20, 'Para onde quer que o Espírito fosse, os seres viventes iam, e as rodas os seguiam, porque o mesmo Espírito estava nelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20486, 'nvi', 'Ezequiel', 'ez', 1, 21, 'Quando os seres viventes se moviam, elas também se moviam; quando eles ficavam imóveis, elas também ficavam; e quando os seres viventes se elevavam do chão, as rodas também se elevavam com eles, porque o mesmo Espírito deles estava nelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20487, 'nvi', 'Ezequiel', 'ez', 1, 22, 'Acima das cabeças dos seres viventes estava o que parecia uma abóboda, reluzente como gelo, e impressionante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20488, 'nvi', 'Ezequiel', 'ez', 1, 23, 'Debaixo dela cada ser vivente estendia duas asas ao que lhe estava mais próximo, e com as outras duas asas cobria o corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20489, 'nvi', 'Ezequiel', 'ez', 1, 24, 'Ouvi o ruído de suas asas quando voavam. Parecia o ruído de muitas águas, parecia a voz do Todo-poderoso. Era um ruído estrondoso, como o de um exército. Quando paravam, fechavam as asas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20490, 'nvi', 'Ezequiel', 'ez', 1, 25, 'Então veio uma voz de cima da abóboda sobre as suas cabeças, enquanto eles ficavam de asas fechadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20491, 'nvi', 'Ezequiel', 'ez', 1, 26, 'Acima da abóboda sobre as suas cabeças havia o que parecia um trono de safira, e, bem no alto, sobre o trono, havia uma figura que parecia um homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20492, 'nvi', 'Ezequiel', 'ez', 1, 27, 'A parte de cima do que parecia ser a cintura dele, vi que parecia metal brilhante, como que cheia de fogo, e que a parte de baixo parecia fogo; e uma luz brilhante o cercava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20493, 'nvi', 'Ezequiel', 'ez', 1, 28, 'Tal como a aparência do arco-íris nas nuvens de um dia chuvoso, assim era o resplendor ao seu redor. Essa era a aparência da figura da glória do Senhor. Quando a vi, prostrei-me com o rosto em terra, e ouvi a voz de alguém falando.');
