INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20635, 'nvi', 'Ezequiel', 'ez', 10, 1, 'Olhei e vi algo semelhante a um trono de safira sobre a abóbada que estava por cima das cabeças dos querubins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20636, 'nvi', 'Ezequiel', 'ez', 10, 2, 'O Senhor disse ao homem vestido de linho: "Vá entre as rodas por baixo dos querubins. Encha as mãos com brasas ardentes de entre os querubins e espalhe-as sobre a cidade". E, enquanto eu observava, ele foi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20637, 'nvi', 'Ezequiel', 'ez', 10, 3, 'Ora, os querubins estavam no lado sul do templo quando o homem entrou, e uma nuvem encheu o pátio interno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20638, 'nvi', 'Ezequiel', 'ez', 10, 4, 'Então a glória do Senhor levantou-se de cima dos querubins e moveu-se para a entrada do templo. A nuvem encheu o templo, e o pátio foi tomado pelo resplendor da glória do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20639, 'nvi', 'Ezequiel', 'ez', 10, 5, 'O som das asas dos querubins podia ser ouvido até no pátio externo, como a voz do Deus Todo-poderoso, quando ele fala.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20640, 'nvi', 'Ezequiel', 'ez', 10, 6, 'Quando o Senhor ordenou ao homem vestido de linho: "Apanhe fogo do meio das rodas, do meio dos querubins", o homem foi e colocou-se ao lado de uma roda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20641, 'nvi', 'Ezequiel', 'ez', 10, 7, 'Então um dos querubins estendeu a mão no fogo que estava no meio deles. Apanhou algumas brasas e as colocou nas mãos do homem vestido de linho, que as recebeu e saiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20642, 'nvi', 'Ezequiel', 'ez', 10, 8, '( Debaixo das asas dos querubins podia-se ver o que se parecia com mãos humanas. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20643, 'nvi', 'Ezequiel', 'ez', 10, 9, 'Olhei e vi ao lado dos querubins quatro rodas, uma ao lado de cada um dos querubins; as rodas reluziam como berilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20644, 'nvi', 'Ezequiel', 'ez', 10, 10, 'Quanto à sua aparência, eram iguais, e cada uma parecia estar entrosada na outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20645, 'nvi', 'Ezequiel', 'ez', 10, 11, 'Enquanto se moviam, elas iam em qualquer uma das quatro direções que tomavam os querubins; as rodas não se viravam enquanto os querubins se moviam. Eles seguiam qualquer direção à sua frente, sem se virar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20646, 'nvi', 'Ezequiel', 'ez', 10, 12, 'Seus corpos, inclusive as costas, as mãos e as asas, estavam completamente cheios de olhos, como as suas quatro rodas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20647, 'nvi', 'Ezequiel', 'ez', 10, 13, 'Ouvi que chamavam às rodas, "rodas rotatórias".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20648, 'nvi', 'Ezequiel', 'ez', 10, 14, 'Cada um dos querubins tinha quatro rostos: Um rosto era o de um querubim, o segundo, de um homem, o terceiro, de um leão, e o quarto, de uma águia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20649, 'nvi', 'Ezequiel', 'ez', 10, 15, 'Então os querubins se elevaram. Eram os mesmos seres viventes que eu tinha visto junto ao rio Quebar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20650, 'nvi', 'Ezequiel', 'ez', 10, 16, 'Quando os querubins se moviam, as rodas ao lado deles se moviam; e, quando os querubins estendiam as asas para erguer-se do chão, as rodas também iam com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20651, 'nvi', 'Ezequiel', 'ez', 10, 17, 'Quando os querubins se mantinham imóveis, elas também ficavam imóveis; e, quando os querubins se levantavam, elas se levantavam com eles, porque o espírito dos seres viventes estava nelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20652, 'nvi', 'Ezequiel', 'ez', 10, 18, 'Então a glória do Senhor afastou-se da entrada do templo e parou sobre os querubins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20653, 'nvi', 'Ezequiel', 'ez', 10, 19, 'Enquanto eu observava, os querubins estenderam as asas e se ergueram do chão, e as rodas foram com eles. Eles pararam à entrada da porta oriental do templo do Senhor, e a glória do Deus de Israel estava sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20654, 'nvi', 'Ezequiel', 'ez', 10, 20, 'Esses seres viventes eram os mesmos que eu vi debaixo do Deus de Israel junto ao rio Quebar, e percebi que eles eram querubins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20655, 'nvi', 'Ezequiel', 'ez', 10, 21, 'Cada um tinha quatro rostos e quatro asas, e debaixo de suas asas havia o que pareciam mãos humanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20656, 'nvi', 'Ezequiel', 'ez', 10, 22, 'Seus rostos tinham a mesma aparência daqueles que eu tinha visto junto ao rio Quebar. Todos iam sempre para a frente.');
