INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20657, 'nvi', 'Ezequiel', 'ez', 11, 1, 'Então o Espírito me ergueu e me levou para a porta do templo do Senhor, que dá para o oriente. Ali, à entrada da porta, havia vinte e cinco homens, e vi entre eles Jazanias, filho de Azur, e Pelatias, filho de Benaia, líderes do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20658, 'nvi', 'Ezequiel', 'ez', 11, 2, 'O Senhor me disse: "Filho do homem, estes são os homens que estão tramando o mal e dando maus conselhos nesta cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20659, 'nvi', 'Ezequiel', 'ez', 11, 3, 'Eles dizem: ‘Não será logo o tempo de construir casas? Esta cidade é uma panela, e nós somos a carne dentro dela’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20660, 'nvi', 'Ezequiel', 'ez', 11, 4, 'Portanto, profetize contra eles; profetize, filho do homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20661, 'nvi', 'Ezequiel', 'ez', 11, 5, 'Então o Espírito do Senhor veio sobre mim, e mandou-me dizer: "Assim diz o Senhor: É isso que vocês estão dizendo, ó nação de Israel, mas eu sei em que vocês estão pensando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20662, 'nvi', 'Ezequiel', 'ez', 11, 6, 'Vocês mataram muita gente nesta cidade e encheram as suas ruas de cadáveres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20663, 'nvi', 'Ezequiel', 'ez', 11, 7, '"Portanto, assim diz o Soberano Senhor: Os corpos que vocês jogaram nas ruas são a carne, e esta cidade é a panela, mas eu os expulsarei dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20664, 'nvi', 'Ezequiel', 'ez', 11, 8, 'Vocês têm medo da espada, e a espada é o que trarei contra vocês, palavra do Soberano Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20665, 'nvi', 'Ezequiel', 'ez', 11, 9, 'Eu os expulsarei da cidade e os entregarei nas mãos de estrangeiros e lhes castigarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20666, 'nvi', 'Ezequiel', 'ez', 11, 10, 'Vocês cairão pela espada, e eu os julgarei nas fronteiras de Israel. Então vocês saberão que eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20667, 'nvi', 'Ezequiel', 'ez', 11, 11, 'Esta cidade não será uma panela para vocês, nem vocês serão carne nela; eu os julgarei nas fronteiras de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20668, 'nvi', 'Ezequiel', 'ez', 11, 12, 'E vocês saberão que eu sou o Senhor, pois vocês não agiram segundo os meus decretos nem obedeceram às minhas leis, mas se conformaram aos padrões das nações ao seu redor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20669, 'nvi', 'Ezequiel', 'ez', 11, 13, 'Ora, enquanto eu estava profetizando, Pelatias, filho de Benaia, morreu. Então prostrei-me, rosto em terra, e clamei em alta voz: "Ah! Soberano Senhor! Destruirás totalmente o remanescente de Israel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20670, 'nvi', 'Ezequiel', 'ez', 11, 14, 'Esta palavra do Senhor veio a mim:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20671, 'nvi', 'Ezequiel', 'ez', 11, 15, '"Filho do homem, seus irmãos, sim, seus irmãos que são seus parentes consangüíneos e toda a nação de Israel, são aqueles de quem o povo de Jerusalém tem dito: ‘Eles estão longe do Senhor. É a nós que esta terra foi dada, para ser nossa propriedade’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20672, 'nvi', 'Ezequiel', 'ez', 11, 16, '"Portanto diga: ‘Assim diz o Soberano Senhor: Embora eu os tenha mandado para terras muito distantes entre os povos e os tenha espalhado entre as nações, por breve período tenho sido um santuário para eles nas terras para onde foram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20673, 'nvi', 'Ezequiel', 'ez', 11, 17, '"Portanto, diga: ‘Assim diz o Soberano Senhor: Eu os ajuntarei dentre as nações e os trarei de volta das terras para onde vocês foram espalhados, e lhes devolverei a terra de Israel’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20674, 'nvi', 'Ezequiel', 'ez', 11, 18, '"Eles voltarão para ela e retirarão todas as suas imagens repugnantes e os seus ídolos detestáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20675, 'nvi', 'Ezequiel', 'ez', 11, 19, 'Darei a eles um coração não dividido e porei um novo espírito dentro deles; retirarei deles o coração de pedra e lhes darei um coração de carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20676, 'nvi', 'Ezequiel', 'ez', 11, 20, 'Então agirão segundo os meus decretos e serão cuidadosos em obedecer às minhas leis. Eles serão o meu povo, e eu serei o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20677, 'nvi', 'Ezequiel', 'ez', 11, 21, 'Mas, quanto àqueles cujos corações estão afeiçoados às suas imagens repugnantes e aos ídolos detestáveis, farei cair sobre suas próprias cabeças aquilo que eles têm feito, palavra do Soberano Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20678, 'nvi', 'Ezequiel', 'ez', 11, 22, 'Então os querubins, com as rodas ao lado, estenderam as asas, e a glória do Deus de Israel estava sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20679, 'nvi', 'Ezequiel', 'ez', 11, 23, 'A glória do Senhor subiu da cidade e parou sobre o monte que fica a leste dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20680, 'nvi', 'Ezequiel', 'ez', 11, 24, 'Então o Espírito ergueu-me e levou-me aos que estavam exilados na Babilônia, na visão dada pelo Espírito de Deus. Findou-se então a visão que eu estava tendo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20681, 'nvi', 'Ezequiel', 'ez', 11, 25, 'e contei aos exilados tudo o que o Senhor tinha me mostrado.');
